// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity 0.8.21;

/// @dev   Modified from the original UniswapV3 library to support v0.8
/// From:  require(absTick <= uint256(MAX_TICK), 'T');
/// To:    require(absTick <= uint256(int(MAX_TICK)), 'T');

/// @title Math library for computing sqrt prices from ticks and vice versa
/// @notice Computes sqrt price for ticks of size 1.0001, i.e. sqrt(1.0001^tick) as fixed point Q64.96 numbers. Supports
/// prices between 2**-128 and 2**128
library TickMath {
    /// @dev The minimum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**-128
    int24 internal constant MIN_TICK = -887272;
    /// @dev The maximum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**128
    int24 internal constant MAX_TICK = -MIN_TICK;

    /// @dev The minimum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MIN_TICK)
    uint160 internal constant MIN_SQRT_RATIO = 4295128739;
    /// @dev The maximum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MAX_TICK)
    uint160 internal constant MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342;

    /// @notice Calculates sqrt(1.0001^tick) * 2^96
    /// @dev Throws if |tick| > max tick
    /// @param tick The input tick for the above formula
    /// @return sqrtPriceX96 A Fixed point Q64.96 number representing the sqrt of the ratio of the two assets (token1/token0)
    /// at the given tick
    function getSqrtRatioAtTick(int24 tick) internal pure returns (uint160 sqrtPriceX96) {
        uint256 absTick = tick < 0 ? uint256(-int256(tick)) : uint256(int256(tick));
        /// @dev This line was modified for v0.8.x
        // require(absTick <= uint256(MAX_TICK), 'T');
        require(absTick <= uint256(int(MAX_TICK)), "T");

        uint256 ratio = absTick & 0x1 != 0 ? 0xfffcb933bd6fad37aa2d162d1a594001 : 0x100000000000000000000000000000000;
        if (absTick & 0x2 != 0) ratio = (ratio * 0xfff97272373d413259a46990580e213a) >> 128;
        if (absTick & 0x4 != 0) ratio = (ratio * 0xfff2e50f5f656932ef12357cf3c7fdcc) >> 128;
        if (absTick & 0x8 != 0) ratio = (ratio * 0xffe5caca7e10e4e61c3624eaa0941cd0) >> 128;
        if (absTick & 0x10 != 0) ratio = (ratio * 0xffcb9843d60f6159c9db58835c926644) >> 128;
        if (absTick & 0x20 != 0) ratio = (ratio * 0xff973b41fa98c081472e6896dfb254c0) >> 128;
        if (absTick & 0x40 != 0) ratio = (ratio * 0xff2ea16466c96a3843ec78b326b52861) >> 128;
        if (absTick & 0x80 != 0) ratio = (ratio * 0xfe5dee046a99a2a811c461f1969c3053) >> 128;
        if (absTick & 0x100 != 0) ratio = (ratio * 0xfcbe86c7900a88aedcffc83b479aa3a4) >> 128;
        if (absTick & 0x200 != 0) ratio = (ratio * 0xf987a7253ac413176f2b074cf7815e54) >> 128;
        if (absTick & 0x400 != 0) ratio = (ratio * 0xf3392b0822b70005940c7a398e4b70f3) >> 128;
        if (absTick & 0x800 != 0) ratio = (ratio * 0xe7159475a2c29b7443b29c7fa6e889d9) >> 128;
        if (absTick & 0x1000 != 0) ratio = (ratio * 0xd097f3bdfd2022b8845ad8f792aa5825) >> 128;
        if (absTick & 0x2000 != 0) ratio = (ratio * 0xa9f746462d870fdf8a65dc1f90e061e5) >> 128;
        if (absTick & 0x4000 != 0) ratio = (ratio * 0x70d869a156d2a1b890bb3df62baf32f7) >> 128;
        if (absTick & 0x8000 != 0) ratio = (ratio * 0x31be135f97d08fd981231505542fcfa6) >> 128;
        if (absTick & 0x10000 != 0) ratio = (ratio * 0x9aa508b5b7a84e1c677de54f3e99bc9) >> 128;
        if (absTick & 0x20000 != 0) ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
        if (absTick & 0x40000 != 0) ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
        if (absTick & 0x80000 != 0) ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

        if (tick > 0) ratio = type(uint256).max / ratio;

        // this divides by 1<<32 rounding up to go from a Q128.128 to a Q128.96.
        // we then downcast because we know the result always fits within 160 bits due to our tick input constraint
        // we round up in the division so getTickAtSqrtRatio of the output price is always consistent
        sqrtPriceX96 = uint160((ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1));
    }

    /// @notice Calculates the greatest tick value such that getRatioAtTick(tick) <= ratio
    /// @dev Throws in case sqrtPriceX96 < MIN_SQRT_RATIO, as MIN_SQRT_RATIO is the lowest value getRatioAtTick may
    /// ever return.
    /// @param sqrtPriceX96 The sqrt ratio for which to compute the tick as a Q64.96
    /// @return tick The greatest tick for which the ratio is less than or equal to the input ratio
    function getTickAtSqrtRatio(uint160 sqrtPriceX96) internal pure returns (int24 tick) {
        // second inequality must be < because the price can never reach the price at the max tick
        require(sqrtPriceX96 >= MIN_SQRT_RATIO && sqrtPriceX96 < MAX_SQRT_RATIO, "R");
        uint256 ratio = uint256(sqrtPriceX96) << 32;

        uint256 r = ratio;
        uint256 msb = 0;

        assembly {
            let f := shl(7, gt(r, 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(6, gt(r, 0xFFFFFFFFFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(5, gt(r, 0xFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(4, gt(r, 0xFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(3, gt(r, 0xFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(2, gt(r, 0xF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(1, gt(r, 0x3))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := gt(r, 0x1)
            msb := or(msb, f)
        }

        if (msb >= 128) r = ratio >> (msb - 127);
        else r = ratio << (127 - msb);

        int256 log_2 = (int256(msb) - 128) << 64;

        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(63, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(62, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(61, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(60, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(59, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(58, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(57, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(56, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(55, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(54, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(53, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(52, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(51, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(50, f))
        }

        int256 log_sqrt10001 = log_2 * 255738958999603826347141; // 128.128 number

        int24 tickLow = int24((log_sqrt10001 - 3402992956809132418596140100660247210) >> 128);
        int24 tickHi = int24((log_sqrt10001 + 291339464771989622907027621153398088495) >> 128);

        tick = tickLow == tickHi ? tickLow : getSqrtRatioAtTick(tickHi) <= sqrtPriceX96 ? tickHi : tickLow;
    }
}

contracts/oracle/oracles/uniV3CheckCLRSO.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracle } from "../fluidOracle.sol";
import { FallbackOracleImpl } from "../implementations/fallbackOracleImpl.sol";
import { UniV3OracleImpl } from "../implementations/uniV3OracleImpl.sol";
import { ErrorTypes } from "../errorTypes.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";

/// @title   UniswapV3 checked against Chainlink / Redstone Oracle. Either one reported as exchange rate.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          the price from a UniV3 pool (compared against 3 TWAPs) and (optionally) comparing it against a Chainlink
///          or Redstone price (one of Chainlink or Redstone being the main source and the other one the fallback source).
///          Alternatively it can also use Chainlink / Redstone as main price and use UniV3 as check price.
/// @dev     The process for getting the aggregate oracle price is:
///           1. Fetch the UniV3 TWAPS, the latest interval is used as the current price
///           2. Verify this price is within an acceptable DELTA from the Uniswap TWAPS e.g.:
///              a. 240 to 60s
///              b. 60 to 15s
///              c. 15 to 1s (last block)
///              d. 1 to 0s (current)
///           3. (unless UniV3 only mode): Verify this price is within an acceptable DELTA from the Chainlink / Redstone Oracle
///           4. If it passes all checks, return the price. Otherwise use fallbacks, usually to Chainlink. In extreme edge-cases revert.
/// @dev     For UniV3 with check mode, if fetching the check price fails, the UniV3 rate is used directly.
contract UniV3CheckCLRSOracle is FluidOracle, UniV3OracleImpl, FallbackOracleImpl {
    /// @dev Rate check oracle delta percent in 1e2 percent. If current uniswap price is out of this delta,
    /// current price fetching reverts.
    uint256 internal immutable _RATE_CHECK_MAX_DELTA_PERCENT;

    /// @dev which oracle to use as final rate source:
    ///      - 1 = UniV3 ONLY (no check),
    ///      - 2 = UniV3 with Chainlink / Redstone check
    ///      - 3 = Chainlink / Redstone with UniV3 used as check.
    uint8 internal immutable _RATE_SOURCE;

    struct UniV3CheckCLRSConstructorParams {
        /// @param uniV3Params                UniV3Oracle constructor params struct.
        UniV3ConstructorParams uniV3Params;
        /// @param chainlinkParams            ChainlinkOracle constructor params struct for UniV3CheckCLRSOracle.
        ChainlinkConstructorParams chainlinkParams;
        /// @param redstoneOracle             Redstone Oracle data for UniV3CheckCLRSOracle. (address can be set to zero address if using Chainlink only)
        RedstoneOracleData redstoneOracle;
        /// @param rateSource                 which oracle to use as final rate source for UniV3CheckCLRSOracle:
        ///                                         - 1 = UniV3 ONLY (no check),
        ///                                         - 2 = UniV3 with Chainlink / Redstone check
        ///                                         - 3 = Chainlink / Redstone with UniV3 used as check.
        uint8 rateSource;
        /// @param fallbackMainSource         which oracle to use as CL/RS main source for UniV3CheckCLRSOracle: see FallbackOracleImpl constructor `mainSource_`
        uint8 fallbackMainSource;
        /// @param rateCheckMaxDeltaPercent   Rate check oracle delta in 1e2 percent for UniV3CheckCLRSOracle
        uint256 rateCheckMaxDeltaPercent;
    }

    constructor(
        string memory infoName_,
        UniV3CheckCLRSConstructorParams memory params_
    )
        UniV3OracleImpl(params_.uniV3Params)
        FallbackOracleImpl(params_.fallbackMainSource, params_.chainlinkParams, params_.redstoneOracle)
        FluidOracle(infoName_)
    {
        if (
            params_.rateSource < 1 ||
            params_.rateSource > 3 ||
            params_.rateCheckMaxDeltaPercent > OracleUtils.HUNDRED_PERCENT_DELTA_SCALER ||
            // Chainlink only Oracle with UniV3 check. Delta would be ignored so revert this type of Oracle setup.
            (params_.fallbackMainSource == 1 && params_.rateSource == 3)
        ) {
            revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidParams);
        }

        _RATE_CHECK_MAX_DELTA_PERCENT = params_.rateCheckMaxDeltaPercent;
        _RATE_SOURCE = params_.rateSource;
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateOperate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateLiquidate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @inheritdoc FluidOracle
    function getExchangeRate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @notice returns all oracle related data as utility for easy off-chain / block explorer use in a single view method
    function uniV3CheckOracleData()
        public
        view
        returns (uint256 rateCheckMaxDelta_, uint256 rateSource_, uint256 fallbackMainSource_)
    {
        return (_RATE_CHECK_MAX_DELTA_PERCENT, _RATE_SOURCE, _FALLBACK_ORACLE_MAIN_SOURCE);
    }

    function _getExchangeRate() internal view returns (uint256 exchangeRate_) {
        if (_RATE_SOURCE == 1) {
            // uniswap is the only main source without check:
            // 1. get uniV3 rate.
            // 2. If that fails (outside delta range) -> revert (no other Oracle configured).
            exchangeRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                // fetching UniV3 failed or invalid delta -> revert
                revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
            }

            return exchangeRate_;
        }

        uint256 checkRate_;
        bool fallback_;
        if (_RATE_SOURCE == 2) {
            // uniswap is main source, with Chainlink / Redstone as check
            // 1. get uniV3 rate

            // case uniV3 rate fails (outside delta range):
            // 2. get Chainlink rate. -> if successful, use Chainlink as result
            // 3. if Chainlink fails too, get Redstone -> if successful, use Redstone as result
            // 4. if Redstone fails too, revert

            // case if uniV3 rate is ok
            // 2. get Chainlink or Redstone rate for check (one is configured as main check source, other one is fallback source)
            //    -> if both fail to fetch, use uniV3 rate directly.
            // 3. check the delta for uniV3 rate against the check soure rate. -> if ok, return uniV3 rate
            // 4. if delta check fails, check delta against the fallback check source. -> if ok, return uniV3 rate
            // 5. if delta check fails for both sources, return Chainlink price

            exchangeRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                // uniV3 failed or invalid delta -> use (Chainlink with Redstone as fallback)
                exchangeRate_ = _getChainlinkOrRedstoneAsFallback();
                if (exchangeRate_ == 0) {
                    // Chainlink / Redstone failed too -> revert
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
                }
                return exchangeRate_;
            }

            (checkRate_, fallback_) = _getRateWithFallback();
            if (checkRate_ == 0) {
                // check price source failed to fetch -> directly use uniV3 TWAP checked price
                // Note uniV3 price fetching was successful, would have been caught otherwise above.
                return exchangeRate_;
            }
        } else {
            // Chainlink / Redstone is main source, with uniV3 as check.
            // 1. get Chainlink / Redstone rate (one is configured as main source, other one is fallback source)

            // case when both Chainlink & Redstone fail:
            // 2. get uniV3 rate. if successful, use uniV3 rate. otherwise, revert (all oracles failed).

            // case when Chainlink / Redstone fetch is successful:
            // 2. get uniV3 rate for check.
            // 3. if uniV3 rate fails to fetch (outside delta), use Chainlink / Redstone directly (skip check).
            // 4. if uniV3 rate is ok, check the delta for Chainlink / Redstone rate against uniV3 rate.
            //    -> if ok, return Chainlink / Redstone (main) rate
            // 5. if delta check fails, check delta against the fallback main source.
            //    -> if ok, return fallback main rate
            // 6. if delta check fails for both sources, return Chainlink price.

            (exchangeRate_, fallback_) = _getRateWithFallback();
            checkRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                if (checkRate_ == 0) {
                    // all oracles failed, revert
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
                }

                // Both Chainlink & Redstone failed -> directly use uniV3 TWAP checked price
                // Note uniV3 price fetching was successful, would have been caught otherwise above.
                return checkRate_;
            }

            if (checkRate_ == 0) {
                // uniV3 failed -> skip check against Uniswap price.

                return exchangeRate_;
            }
        }

        if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
            if (fallback_) {
                // fallback already used, no other rate available to check.

                // if price is chainlink price -> return it.
                if (_FALLBACK_ORACLE_MAIN_SOURCE == 3) {
                    // redstone with Chainlink as fallback
                    return _RATE_SOURCE == 2 ? checkRate_ : exchangeRate_; // if rate source is 2, Chainlink rate is in checkRate_
                }

                // if price is redstone price -> revert
                revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidPrice);
            }

            if (_FALLBACK_ORACLE_MAIN_SOURCE == 1) {
                // 1 = only chainlink and UniV3 is configured and delta check failed. no fallback available.
                if (_RATE_SOURCE == 2) {
                    // case where uniV3 is main source with only Chainlink as check rate Oracle configured.
                    // delta check failed -> return Chainlink price (instead of uniV3 price).
                    return checkRate_;
                }

                // here: if (_FALLBACK_ORACLE_MAIN_SOURCE == 1 && _RATE_SOURCE == 3)
                // rate source is 3: Chainlink as main, uniV3 as delta. delta check failed.
                // this Oracle type would basically be a more expensive Chainlink-only Oracle because the delta check against UniV3 is ignored.
                // this setup is reverted in constructor, but in any case returning Chainlink price here even though this code should never be reached.
                return exchangeRate_; // exchangeRate_ here is chainlink price
            }

            // fallback not done yet -> check against fallback price.
            // So if originally Chainlink was fetched and delta failed, check against Redstone.
            // if originally Redstone was fetched and delta failed, check against Chainlink.
            if (_FALLBACK_ORACLE_MAIN_SOURCE == 2) {
                // 2 = Chainlink with Redstone Fallback. delta check against Chainlink failed. try against Redstone.
                uint256 redstoneRate_ = _getRedstoneExchangeRate();
                uint256 chainlinkRate_;
                if (_RATE_SOURCE == 2) {
                    // uniV3 main source. -> update checkRate_ with Redstone price
                    chainlinkRate_ = checkRate_;
                    checkRate_ = redstoneRate_;
                } else {
                    // uniV3 is check source. -> update exchangeRate_ with Redstone price
                    chainlinkRate_ = exchangeRate_;
                    exchangeRate_ = redstoneRate_;
                }

                if (redstoneRate_ == 0) {
                    // fetching Redstone failed. So delta UniV3 <> Chainlink failed, fetching Redstone as backup failed.
                    // -> return chainlink price (for both cases when Chainlink is main and when UniV3 is the main source).
                    return chainlinkRate_;
                }

                if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
                    // delta check against Redstone failed too. return Chainlink price
                    return chainlinkRate_;
                }

                // delta check against Redstone passed. if uniV3 main source -> return uniV3, else return Redstone.
                // exchangeRate_ is already set correctly for this.
            } else {
                // 3 = Redstone with Chainlink Fallback. delta check against Redstone failed. try against Chainlink.
                uint256 chainlinkRate_ = _getChainlinkExchangeRate();
                if (chainlinkRate_ == 0) {
                    // fetching Chainlink failed. So delta UniV3 <> Redstone failed, fetching Chainlink as backup check failed.
                    // -> revert.
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidPrice);
                }

                if (_RATE_SOURCE == 3) {
                    // uniV3 is check source. -> update exchangeRate_ with Chainlink price.
                    // Optimization: in this case we can directly return chainlink price, because if delta check between
                    // Chainlink (new main source) and uniV3 (check source) fails, we anyway return Chainlink price still.
                    return chainlinkRate_;
                }

                // uniV3 main source. -> update checkRate_ with Chainlink price and compare delta again
                checkRate_ = chainlinkRate_;

                if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
                    // delta check against Chainlink failed too. case here can only be where uniV3 would have been
                    // main source and Chainlink check source. -> return Chainlink as price instead of uniV3
                    return checkRate_;
                }

                // delta check against Chainlink passed. if uniV3 main source -> return uniV3, else return Chainlink.
                // exchangeRate_ is already set correctly for this.
            }
        }
    }
}

contracts/oracle/oraclesL2/cLRS2UniV3Che.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracleL2 } from "../fluidOracleL2.sol";
import { UniV3CheckCLRSOracleL2 } from "./uniV3CheckCLRSOracleL2.sol";
import { WstETHOracleImpl } from "../implementations/wstETHOracleImpl.sol";
import { FallbackOracleImpl2 } from "../implementations/fallbackOracleImpl2.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";
import { ErrorTypes } from "../errorTypes.sol";

// @dev uses FallbackOracleImpl2 to avoid conflicts with already used ChainlinkOracleImpl, RedstoneOracleImpl and
// FallbackOracleImpl in UniV3CheckCLRSOracle.

/// @title   CLRSOracle combined with a uniV3CheckCLRSOracle.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          1. rate from Chainlink price feeds (or Redstone as fallback).
///          2. result from 1. combined with a uniV3CheckCLRSOracle to get from someToken <> someToken2
///          e.g. when going from wstETH to USDC:
///          1. wstETH -> stETH -> ETH Chainlink feed.
///          2. ETH -> USDC via UniV3 ETH <> USDC pool checked against ETH -> USDC Chainlink feed.
contract CLRS2UniV3CheckCLRSOracleL2 is FluidOracleL2, FallbackOracleImpl2, UniV3CheckCLRSOracleL2 {
    struct CLRS2ConstructorParams {
        /// @param fallbackMainSource             which oracle to use as main source for wstETH <> CLRS: 1 = Chainlink, 2 = Redstone (other one is fallback).
        uint8 fallbackMainSource;
        /// @param chainlinkParams                chainlink Oracle constructor params struct for wstETH <> CLRS.
        ChainlinkConstructorParams chainlinkParams;
        /// @param redstoneOracle                 Redstone Oracle data for wstETH <> CLRS. (address can be set to zero address if using Chainlink only)
        RedstoneOracleData redstoneOracle;
    }

    /// @notice                      constructs a CLRS2UniV3CheckCLRSOracleL2 with all inherited contracts
    /// @param infoName_             Oracle identify helper name.
    /// @param cLRS2Params_          CLRS2ConstructorParams for wstETH <> CLRS Token2 conversion
    /// @param uniV3CheckCLRSParams_ UniV3CheckCLRSOracle constructor params
    /// @param sequencerUptimeFeed_  L2 sequencer uptime Chainlink feed
    constructor(
        string memory infoName_,
        CLRS2ConstructorParams memory cLRS2Params_,
        UniV3CheckCLRSConstructorParams memory uniV3CheckCLRSParams_,
        address sequencerUptimeFeed_
    )
        FallbackOracleImpl2(cLRS2Params_.fallbackMainSource, cLRS2Params_.chainlinkParams, cLRS2Params_.redstoneOracle)
        UniV3CheckCLRSOracleL2(infoName_, uniV3CheckCLRSParams_, sequencerUptimeFeed_)
    {}

    /// @inheritdoc FluidOracleL2
    function getExchangeRateOperate()
        public
        view
        override(FluidOracleL2, UniV3CheckCLRSOracleL2)
        returns (uint256 exchangeRate_)
    {
        // 1. get CLRS Oracle rate for wstETH <> CLRS feed. uses FallbackOracleImpl2
        (exchangeRate_, ) = _getRateWithFallback2();
        if (exchangeRate_ == 0) {
            // revert if fetched exchange rate is 0
            revert FluidOracleError(ErrorTypes.CLRS2UniV3CheckCLRSOracleL2__ExchangeRateZero);
        }

        // 2. get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0. Combine this rate with existing.
        //    (super.getExchangeRate returns UniV3CheckCLRSOracleL2 rate, no other inherited contract has this.)
        //    includes _ensureSequencerUpAndValid();
        exchangeRate_ = (super.getExchangeRateOperate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracleL2
    function getExchangeRateLiquidate()
        public
        view
        override(FluidOracleL2, UniV3CheckCLRSOracleL2)
        returns (uint256 exchangeRate_)
    {
        // 1. get CLRS Oracle rate for wstETH <> CLRS feed. uses FallbackOracleImpl2
        (exchangeRate_, ) = _getRateWithFallback2();
        if (exchangeRate_ == 0) {
            // revert if fetched exchange rate is 0
            revert FluidOracleError(ErrorTypes.CLRS2UniV3CheckCLRSOracleL2__ExchangeRateZero);
        }

        // 2. get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0. Combine this rate with existing.
        //    (super.getExchangeRate returns UniV3CheckCLRSOracleL2 rate, no other inherited contract has this.)
        //    includes _ensureSequencerUpAndValid();
        exchangeRate_ = (super.getExchangeRateLiquidate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracleL2
    function getExchangeRate()
        public
        view
        override(FluidOracleL2, UniV3CheckCLRSOracleL2)
        returns (uint256 exchangeRate_)
    {
        return getExchangeRateOperate();
    }

    /// @notice which oracle to use as main source:
    ///          - 1 = Chainlink ONLY (no fallback)
    ///          - 2 = Chainlink with Redstone Fallback
    ///          - 3 = Redstone with Chainlink Fallback
    function FALLBACK_ORACLE2_MAIN_SOURCE() public view returns (uint8) {
        return _FALLBACK_ORACLE2_MAIN_SOURCE;
    }
}

contracts/oracle/oraclesL2/uniV3CheckCLR.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracleL2 } from "../fluidOracleL2.sol";
import { UniV3CheckCLRSOracle } from "../oracles/uniV3CheckCLRSOracle.sol";

/// @title   UniswapV3 checked against Chainlink / Redstone Oracle for Layer 2 (with sequencer outage detection). Either one reported as exchange rate.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          the price from a UniV3 pool (compared against 3 TWAPs) and (optionally) comparing it against a Chainlink
///          or Redstone price (one of Chainlink or Redstone being the main source and the other one the fallback source).
///          Alternatively it can also use Chainlink / Redstone as main price and use UniV3 as check price.
/// @dev     The process for getting the aggregate oracle price is:
///           1. Fetch the UniV3 TWAPS, the latest interval is used as the current price
///           2. Verify this price is within an acceptable DELTA from the Uniswap TWAPS e.g.:
///              a. 240 to 60s
///              b. 60 to 15s
///              c. 15 to 1s (last block)
///              d. 1 to 0s (current)
///           3. (unless UniV3 only mode): Verify this price is within an acceptable DELTA from the Chainlink / Redstone Oracle
///           4. If it passes all checks, return the price. Otherwise use fallbacks, usually to Chainlink. In extreme edge-cases revert.
/// @dev     For UniV3 with check mode, if fetching the check price fails, the UniV3 rate is used directly.
contract UniV3CheckCLRSOracleL2 is FluidOracleL2, UniV3CheckCLRSOracle {
    constructor(
        string memory infoName_,
        UniV3CheckCLRSConstructorParams memory params_,
        address sequencerUptimeFeed_
    ) UniV3CheckCLRSOracle(infoName_, params_) FluidOracleL2(sequencerUptimeFeed_) {}

    /// @inheritdoc FluidOracleL2
    function getExchangeRateOperate()
        public
        view
        virtual
        override(UniV3CheckCLRSOracle, FluidOracleL2)
        returns (uint256 exchangeRate_)
    {
        _ensureSequencerUpAndValid();
        return super.getExchangeRateOperate();
    }

    /// @inheritdoc FluidOracleL2
    function getExchangeRateLiquidate()
        public
        view
        virtual
        override(UniV3CheckCLRSOracle, FluidOracleL2)
        returns (uint256 exchangeRate_)
    {
        _ensureSequencerUpAndValid();
        return super.getExchangeRateLiquidate();
    }

    /// @inheritdoc FluidOracleL2
    function getExchangeRate()
        public
        view
        virtual
        override(UniV3CheckCLRSOracle, FluidOracleL2)
        returns (uint256 exchangeRate_)
    {
        return getExchangeRateOperate();
    }
}

contracts/protocols/lending/stakingRewar.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import { IERC20Permit } from "@openzeppelin/contracts/token/ERC20/extensions/draft-IERC20Permit.sol";
import { Owned } from "solmate/src/auth/Owned.sol";

// Adapted from https://github.com/Uniswap/liquidity-staker/blob/master/contracts/StakingRewards.sol
contract FluidLendingStakingRewards is Owned, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public immutable rewardsToken; // should be INST or any ERC20
    IERC20 public immutable stakingToken; // should be fToken

    /* ========== STATE VARIABLES ========== */

    // Owned and ReentranyGuard storage variables before

    uint40 internal _periodFinish;
    uint40 public lastUpdateTime;
    uint40 internal _rewardsDuration; // e.g. 60 days
    uint136 internal _rewardRate;

    // ------------------------------ next slot

    uint128 public rewardPerTokenStored;
    uint128 internal _totalSupply;

    // ------------------------------ next slot

    uint40 public nextRewardsDuration; // e.g. 60 days
    uint216 public nextRewards;

    // ------------------------------ next slot

    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;

    mapping(address => uint256) internal _balances;

    /* ========== CONSTRUCTOR ========== */

    constructor(address owner_, IERC20 rewardsToken_, IERC20 stakingToken_, uint40 rewardsDuration_) Owned(owner_) {
        require(address(rewardsToken_) != address(0), "Invalid params");
        require(address(stakingToken_) != address(0), "Invalid params");
        require(owner_ != address(0), "Invalid params"); // Owned does not have a zero check for owner_
        require(rewardsDuration_ > 0, "Invalid params");

        rewardsToken = rewardsToken_;
        stakingToken = stakingToken_;
        _rewardsDuration = rewardsDuration_;
    }

    /* ========== VIEWS ========== */

    function nextPeriodFinish() public view returns (uint256) {
        if (nextRewardsDuration == 0) {
            return 0;
        }
        return _periodFinish + nextRewardsDuration;
    }

    function nextRewardRate() public view returns (uint256) {
        if (nextRewards == 0) {
            return 0;
        }
        return nextRewards / nextRewardsDuration;
    }

    function periodFinish() public view returns (uint256) {
        if (block.timestamp <= _periodFinish || nextRewardsDuration == 0) {
            return _periodFinish;
        }
        return nextPeriodFinish();
    }

    function rewardRate() public view returns (uint256) {
        if (block.timestamp <= _periodFinish || nextRewardsDuration == 0) {
            return _rewardRate;
        }
        return nextRewardRate();
    }

    function rewardsDuration() public view returns (uint256) {
        if (block.timestamp <= _periodFinish || nextRewardsDuration == 0) {
            return _rewardsDuration;
        }
        return nextRewardsDuration;
    }

    function totalSupply() public view returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    /// @notice gets last time where rewards accrue, also considering already queued next rewards
    function lastTimeRewardApplicable() public view returns (uint256) {
        if (block.timestamp <= _periodFinish) {
            return block.timestamp;
        }

        if (nextRewardsDuration == 0) {
            return _periodFinish;
        }

        // check if block.timestamp is within next rewards duration
        uint256 nextPeriodFinish_ = nextPeriodFinish();
        if (block.timestamp <= nextPeriodFinish_) {
            return block.timestamp;
        }
        return nextPeriodFinish_;
    }

    /// @notice gets reward amount per token, also considering automatic transition to queued next rewards
    function rewardPerToken() public view returns (uint256 rewardPerToken_) {
        if (_totalSupply == 0) {
            return rewardPerTokenStored;
        }

        // reward per token for current rewards
        // get lastTimeRewardApplicable for storage vars (without next queued rewards as returned by view methods)
        uint256 lastTimeRewardApplicable_ = block.timestamp < _periodFinish ? block.timestamp : _periodFinish;

        rewardPerToken_ = (((lastTimeRewardApplicable_ - lastUpdateTime) * _rewardRate * 1e18) / _totalSupply);

        if (block.timestamp > _periodFinish && nextRewardsDuration > 0) {
            // previous rewards ended and next rewards queued, take into account accrued rewards:

            // check if block.timestamp is within next rewards duration
            rewardPerToken_ += (((lastTimeRewardApplicable() - _periodFinish) * nextRewardRate() * 1e18) /
                _totalSupply);
        }

        rewardPerToken_ += rewardPerTokenStored;
    }

    /// @notice gets earned reward amount for an `account`, also considering automatic transition to queued next rewards
    function earned(address account) public view returns (uint256) {
        return (_balances[account] * (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18 + rewards[account];
    }

    /// @notice gets reward amount for current duration, also considering automatic transition to queued next rewards
    function getRewardForDuration() public view returns (uint256) {
        return rewardRate() * rewardsDuration();
    }

    /* ========== MUTATIVE FUNCTIONS ========== */

    function stakeWithPermit(
        uint256 amount,
        uint deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot stake 0");

        // permit
        IERC20Permit(address(stakingToken)).permit(msg.sender, address(this), amount, deadline, v, r, s);

        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        _totalSupply = _totalSupply + uint128(amount);
        _balances[msg.sender] = _balances[msg.sender] + amount;

        emit Staked(msg.sender, amount);
    }

    function stake(uint256 amount) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot stake 0");

        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        _totalSupply = _totalSupply + uint128(amount);
        _balances[msg.sender] = _balances[msg.sender] + amount;

        emit Staked(msg.sender, amount);
    }

    function withdraw(uint256 amount) public nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot withdraw 0");

        _totalSupply = _totalSupply - uint128(amount);
        _balances[msg.sender] = _balances[msg.sender] - amount;

        stakingToken.safeTransfer(msg.sender, amount);

        emit Withdrawn(msg.sender, amount);
    }

    function getReward() public nonReentrant updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            rewardsToken.safeTransfer(msg.sender, reward);
            emit RewardPaid(msg.sender, reward);
        }
    }

    function exit() external {
        withdraw(_balances[msg.sender]);
        getReward();
    }

    /// @notice updates rewards until current block.timestamp or `periodFinish`. Transitions to next rewards
    /// if previous rewards ended and next ones were queued.
    function updateRewards() public nonReentrant updateReward(address(0)) {}

    /* ========== RESTRICTED FUNCTIONS ========== */

    /// @notice queues next rewards that will be transitioned to automatically after current rewards reach `periodFinish`.
    function queueNextRewardAmount(uint216 nextReward_, uint40 nextDuration_) external onlyOwner {
        require(block.timestamp < _periodFinish, "Previous duration already ended");
        require(nextDuration_ > 0 && nextReward_ > 0, "Invalid params");
        // must not already be queued
        require(nextRewardsDuration == 0, "Already queued next rewards");

        // Ensure the provided reward amount is not more than the balance in the contract.
        // This keeps the reward rate in the right range, preventing overflows due to
        // very high values of rewardRate in the earned and rewardsPerToken functions;
        // Reward + nextReward must be less than 2^256 / 10^18 to avoid overflow.
        uint256 balance_ = rewardsToken.balanceOf(address(this));
        uint256 remainingCurrentReward_ = (_periodFinish - block.timestamp) * _rewardRate;
        uint256 requiredBalance_ = remainingCurrentReward_ + nextReward_;
        require(requiredBalance_ <= balance_, "Provided reward too high");

        nextRewards = nextReward_;
        nextRewardsDuration = nextDuration_;

        emit NextRewardQueued(nextReward_, nextDuration_);
    }

    /// @notice add new rewards and update reward duration AFTER a reward period has ended.
    function notifyRewardAmountWithDuration(
        uint256 reward_,
        uint40 newDuration_
    ) external onlyOwner updateReward(address(0)) {
        require(block.timestamp > _periodFinish, "Previous duration not ended");
        // @dev nextRewardsDuration == 0 should not be needed because if there are next rewards queued and block.timestamp > periodFinish,
        // then updateReward(address(0)) would automatically transition to the queued rewards and set nextRewardsDuration == 0.
        // Adding the check here anyway to be safe as gas optimization on an Admin Method is not necessary.
        require(nextRewardsDuration == 0, "Already queued next rewards");
        require(newDuration_ > 0, "Invalid params");

        _rewardsDuration = newDuration_;
        notifyRewardAmount(reward_);
    }

    /// @notice add new rewards or top-up adding to current rewards, adjusting rewardRate going forward for leftover + newReward
    /// until block.timestamp + duration
    function notifyRewardAmount(uint256 reward_) public onlyOwner updateReward(address(0)) {
        require(nextRewardsDuration == 0, "Already queued next rewards");

        if (block.timestamp >= _periodFinish) {
            _rewardRate = uint136(reward_ / _rewardsDuration);
        } else {
            uint256 remaining_ = _periodFinish - block.timestamp;
            uint256 leftover_ = remaining_ * _rewardRate;
            _rewardRate = uint136((reward_ + leftover_) / _rewardsDuration);
        }

        // Ensure the provided reward amount is not more than the balance in the contract.
        // This keeps the reward rate in the right range, preventing overflows due to
        // very high values of rewardRate in the earned and rewardsPerToken functions;
        // Reward + leftover must be less than 2^256 / 10^18 to avoid overflow.
        uint256 balance_ = rewardsToken.balanceOf(address(this));
        require(_rewardRate <= balance_ / _rewardsDuration, "Provided reward too high");

        lastUpdateTime = uint40(block.timestamp);
        _periodFinish = uint40(block.timestamp + _rewardsDuration);
        emit RewardAdded(reward_);
    }

    /// @notice         Spell allows owner aka governance to do any arbitrary call
    /// @param target_  Address to which the call needs to be delegated
    /// @param data_    Data to execute at the delegated address
    function spell(address target_, bytes memory data_) external onlyOwner returns (bytes memory response_) {
        assembly {
            let succeeded := delegatecall(gas(), target_, add(data_, 0x20), mload(data_), 0, 0)
            let size := returndatasize()

            response_ := mload(0x40)
            mstore(0x40, add(response_, and(add(add(size, 0x20), 0x1f), not(0x1f))))
            mstore(response_, size)
            returndatacopy(add(response_, 0x20), 0, size)

            switch iszero(succeeded)
            case 1 {
                // throw if delegatecall failed
                returndatacopy(0x00, 0x00, size)
                revert(0x00, size)
            }
        }
    }

    /* ========== MODIFIERS ========== */

    modifier updateReward(address account) {
        // get lastTimeRewardApplicable for storage vars (without next queued rewards as returned by view methods)
        uint256 lastTimeRewardApplicable_ = block.timestamp < _periodFinish ? block.timestamp : _periodFinish;

        // get reward per Token for storage vars (without next queued rewards as returned by view methods)
        uint256 rewardPerToken_;
        if (_totalSupply > 0) {
            rewardPerToken_ =
                rewardPerTokenStored +
                (((lastTimeRewardApplicable_ - lastUpdateTime) * _rewardRate * 1e18) / _totalSupply);
        }

        rewardPerTokenStored = uint128(rewardPerToken_);
        lastUpdateTime = uint40(lastTimeRewardApplicable_);

        if (block.timestamp > _periodFinish && nextRewardsDuration > 0) {
            // previous rewards ended, and new rewards were queued -> start new rewards.

            // previous rewards fully distributed until `periodFinish` by updating `rewardPerTokenStored`
            // according to `rewardRate` that was valid until period finish, with calls above.

            // transition to new rewards
            _rewardRate = uint136(nextRewards / nextRewardsDuration);
            _rewardsDuration = nextRewardsDuration;

            // new rewards started right at periodFinish
            _periodFinish = uint40(_periodFinish + _rewardsDuration);

            emit RewardAdded(nextRewards);

            // reset next rewards storage vars
            nextRewards = 0;
            nextRewardsDuration = 0;

            // update rewardPerTokenStored again to go until current block.timestamp now
            // can use normal view methods here as next rewards storage vars have just been set to 0
            rewardPerTokenStored = uint128(rewardPerToken());
            lastUpdateTime = uint40(lastTimeRewardApplicable());
        }

        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /* ========== EVENTS ========== */

    event RewardAdded(uint256 reward);
    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event NextRewardQueued(uint256 reward, uint256 duration);
}

