contracts/oracle/oracles/genericOracleBa.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { ErrorTypes } from "../errorTypes.sol";
import { Error as OracleError } from "../error.sol";
import { ChainlinkSourceReader } from "../sourceReaders/chainlinkSourceReader.sol";
import { FluidSourceReader } from "../sourceReaders/fluidSourceReader.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";

abstract contract GenericOracleStructs {
    enum SourceType {
        Fluid, // 0, e.g. ContractRate or some other IFluidOracle
        Redstone, // 1
        Chainlink, // 2
        UniV3Checked // 3
    }

    struct OracleHopSource {
        address source;
        bool invertRate;
        uint256 multiplier;
        uint256 divisor;
        SourceType sourceType; // e.g. FLUID, REDSTONE, UNIV3CHECKED, CHAINLINK
    }
}

/// @notice generic configurable Oracle Base
/// combines up to 4 hops from sources such as
///  - an existing IFluidOracle (e.g. ContractRate)
///  - Redstone
///  - Chainlink
abstract contract FluidGenericOracleBase is
    OracleError,
    GenericOracleStructs,
    ChainlinkSourceReader,
    FluidSourceReader
{
    address internal immutable _SOURCE1;
    bool internal immutable _SOURCE1_INVERT;
    uint256 internal immutable _SOURCE1_MULTIPLIER;
    uint256 internal immutable _SOURCE1_DIVISOR;
    SourceType internal immutable _SOURCE1_TYPE;

    address internal immutable _SOURCE2;
    bool internal immutable _SOURCE2_INVERT;
    uint256 internal immutable _SOURCE2_MULTIPLIER;
    uint256 internal immutable _SOURCE2_DIVISOR;
    SourceType internal immutable _SOURCE2_TYPE;

    address internal immutable _SOURCE3;
    bool internal immutable _SOURCE3_INVERT;
    uint256 internal immutable _SOURCE3_MULTIPLIER;
    uint256 internal immutable _SOURCE3_DIVISOR;
    SourceType internal immutable _SOURCE3_TYPE;

    address internal immutable _SOURCE4;
    bool internal immutable _SOURCE4_INVERT;
    uint256 internal immutable _SOURCE4_MULTIPLIER;
    uint256 internal immutable _SOURCE4_DIVISOR;
    SourceType internal immutable _SOURCE4_TYPE;

    address internal immutable _SOURCE5;
    bool internal immutable _SOURCE5_INVERT;
    uint256 internal immutable _SOURCE5_MULTIPLIER;
    uint256 internal immutable _SOURCE5_DIVISOR;
    SourceType internal immutable _SOURCE5_TYPE;

    constructor(OracleHopSource[] memory sources_) {
        if (sources_.length == 0 || sources_.length > 5) {
            revert FluidOracleError(ErrorTypes.GenericOracle__InvalidParams);
        }

        _verifyOracleHopSource(sources_[0]);
        _SOURCE1 = sources_[0].source;
        _SOURCE1_INVERT = sources_[0].invertRate;
        _SOURCE1_MULTIPLIER = sources_[0].multiplier;
        _SOURCE1_DIVISOR = sources_[0].divisor;
        _SOURCE1_TYPE = sources_[0].sourceType;

        if (sources_.length > 1) {
            _verifyOracleHopSource(sources_[1]);
            _SOURCE2 = sources_[1].source;
            _SOURCE2_INVERT = sources_[1].invertRate;
            _SOURCE2_MULTIPLIER = sources_[1].multiplier;
            _SOURCE2_DIVISOR = sources_[1].divisor;
            _SOURCE2_TYPE = sources_[1].sourceType;
        }

        if (sources_.length > 2) {
            _verifyOracleHopSource(sources_[2]);
            _SOURCE3 = sources_[2].source;
            _SOURCE3_INVERT = sources_[2].invertRate;
            _SOURCE3_MULTIPLIER = sources_[2].multiplier;
            _SOURCE3_DIVISOR = sources_[2].divisor;
            _SOURCE3_TYPE = sources_[2].sourceType;
        }

        if (sources_.length > 3) {
            _verifyOracleHopSource(sources_[3]);
            _SOURCE4 = sources_[3].source;
            _SOURCE4_INVERT = sources_[3].invertRate;
            _SOURCE4_MULTIPLIER = sources_[3].multiplier;
            _SOURCE4_DIVISOR = sources_[3].divisor;
            _SOURCE4_TYPE = sources_[3].sourceType;
        }

        if (sources_.length > 4) {
            _verifyOracleHopSource(sources_[4]);
            _SOURCE5 = sources_[4].source;
            _SOURCE5_INVERT = sources_[4].invertRate;
            _SOURCE5_MULTIPLIER = sources_[4].multiplier;
            _SOURCE5_DIVISOR = sources_[4].divisor;
            _SOURCE5_TYPE = sources_[4].sourceType;
        }
    }

    /// @dev verifies a hop source config
    function _verifyOracleHopSource(OracleHopSource memory source_) internal view virtual {
        if (
            address(source_.source) == address(0) ||
            source_.sourceType == SourceType.UniV3Checked ||
            source_.divisor == 0 ||
            source_.multiplier == 0 ||
            source_.divisor > 1e40 ||
            source_.multiplier > 1e40
        ) {
            revert FluidOracleError(ErrorTypes.GenericOracle__InvalidParams);
        }
    }

    /// @dev reads the exchange rate for a hop source
    function _readSource(
        address source_,
        SourceType sourceType_,
        bool isOperate_
    ) internal view virtual returns (uint256 rate_) {
        if (sourceType_ == SourceType.Redstone || sourceType_ == SourceType.Chainlink) {
            rate_ = _readChainlinkSource(source_);
        } else if (sourceType_ == SourceType.Fluid) {
            rate_ = _readFluidSource(source_, isOperate_);
        } else {
            // should never happen because of config checks in constructor
            revert FluidOracleError(ErrorTypes.GenericOracle__UnexpectedConfig);
        }
    }

    /// @dev gets the exchange rate for a single configured hop
    function _getExchangeRateForHop(
        uint256 curHopsRate_,
        bool isOperate_,
        OracleHopSource memory source_
    ) internal view virtual returns (uint256 rate_) {
        rate_ = _readSource(source_.source, source_.sourceType, isOperate_);

        // scale to 1e27
        rate_ = (rate_ * source_.multiplier) / source_.divisor;

        if (source_.invertRate && rate_ > 0) {
            rate_ = (10 ** (OracleUtils.RATE_OUTPUT_DECIMALS * 2)) / uint256(rate_);
        }

        rate_ = (curHopsRate_ * rate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS); // combine with current hops rate
    }

    /// @dev gets the exchange rate combined for all configured hops
    function _getHopsExchangeRate(bool isOperate_) internal view returns (uint256 rate_) {
        rate_ = _getExchangeRateForHop(
            (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
            isOperate_,
            OracleHopSource(_SOURCE1, _SOURCE1_INVERT, _SOURCE1_MULTIPLIER, _SOURCE1_DIVISOR, _SOURCE1_TYPE)
        );
        if (rate_ == 0) {
            revert FluidOracleError(ErrorTypes.GenericOracle__RateZero);
        }
        if (address(_SOURCE2) == address(0) && _SOURCE2_TYPE != SourceType.UniV3Checked) {
            return rate_;
        }

        // 2 hops -> return rate of hop 1 combined with hop 2
        rate_ = _getExchangeRateForHop(
            rate_,
            isOperate_,
            OracleHopSource(_SOURCE2, _SOURCE2_INVERT, _SOURCE2_MULTIPLIER, _SOURCE2_DIVISOR, _SOURCE2_TYPE)
        );
        if (rate_ == 0) {
            revert FluidOracleError(ErrorTypes.GenericOracle__RateZero);
        }
        if (address(_SOURCE3) == address(0) && _SOURCE3_TYPE != SourceType.UniV3Checked) {
            return rate_;
        }

        // 3 hops -> return rate of hop 1 combined with hop 2 & hop 3
        rate_ = _getExchangeRateForHop(
            rate_,
            isOperate_,
            OracleHopSource(_SOURCE3, _SOURCE3_INVERT, _SOURCE3_MULTIPLIER, _SOURCE3_DIVISOR, _SOURCE3_TYPE)
        );
        if (rate_ == 0) {
            revert FluidOracleError(ErrorTypes.GenericOracle__RateZero);
        }
        if (address(_SOURCE4) == address(0) && _SOURCE4_TYPE != SourceType.UniV3Checked) {
            return rate_;
        }

        // 4 hops -> return rate of hop 1 combined with hop 2, hop 3 & hop 4
        rate_ = _getExchangeRateForHop(
            rate_,
            isOperate_,
            OracleHopSource(_SOURCE4, _SOURCE4_INVERT, _SOURCE4_MULTIPLIER, _SOURCE4_DIVISOR, _SOURCE4_TYPE)
        );
        if (rate_ == 0) {
            revert FluidOracleError(ErrorTypes.GenericOracle__RateZero);
        }
        if (address(_SOURCE5) == address(0) && _SOURCE5_TYPE != SourceType.UniV3Checked) {
            return rate_;
        }

        // 5 hops -> return rate of hop 1 combined with hop 2, hop 3, hop 4 & hop 5
        rate_ = _getExchangeRateForHop(
            rate_,
            isOperate_,
            OracleHopSource(_SOURCE5, _SOURCE5_INVERT, _SOURCE5_MULTIPLIER, _SOURCE5_DIVISOR, _SOURCE5_TYPE)
        );
        if (rate_ == 0) {
            revert FluidOracleError(ErrorTypes.GenericOracle__RateZero);
        }
    }

    /// @notice Returns the exchange rate for each hop.
    /// @return rateSource1Operate_ The exchange rate for hop 1 during operate.
    /// @return rateSource1Liquidate_ The exchange rate for hop 1 during liquidate.
    /// @return rateSource2Operate_ The exchange rate for hop 2 during operate.
    /// @return rateSource2Liquidate_ The exchange rate for hop 2 during liquidate.
    /// @return rateSource3Operate_ The exchange rate for hop 3 during operate.
    /// @return rateSource3Liquidate_ The exchange rate for hop 3 during liquidate.
    /// @return rateSource4Operate_ The exchange rate for hop 4 during operate.
    /// @return rateSource4Liquidate_ The exchange rate for hop 4 during liquidate.
    /// @return rateSource5Operate_ The exchange rate for hop 5 during operate.
    /// @return rateSource5Liquidate_ The exchange rate for hop 5 during liquidate.
    function getHopExchangeRates()
        public
        view
        returns (
            uint256 rateSource1Operate_,
            uint256 rateSource1Liquidate_,
            uint256 rateSource2Operate_,
            uint256 rateSource2Liquidate_,
            uint256 rateSource3Operate_,
            uint256 rateSource3Liquidate_,
            uint256 rateSource4Operate_,
            uint256 rateSource4Liquidate_,
            uint256 rateSource5Operate_,
            uint256 rateSource5Liquidate_
        )
    {
        rateSource1Operate_ = _getExchangeRateForHop(
            (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
            true,
            OracleHopSource(_SOURCE1, _SOURCE1_INVERT, _SOURCE1_MULTIPLIER, _SOURCE1_DIVISOR, _SOURCE1_TYPE)
        );
        rateSource1Liquidate_ = _getExchangeRateForHop(
            (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
            false,
            OracleHopSource(_SOURCE1, _SOURCE1_INVERT, _SOURCE1_MULTIPLIER, _SOURCE1_DIVISOR, _SOURCE1_TYPE)
        );

        if (address(_SOURCE2) != address(0) || _SOURCE2_TYPE == SourceType.UniV3Checked) {
            rateSource2Operate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                true,
                OracleHopSource(_SOURCE2, _SOURCE2_INVERT, _SOURCE2_MULTIPLIER, _SOURCE2_DIVISOR, _SOURCE2_TYPE)
            );
            rateSource2Liquidate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                false,
                OracleHopSource(_SOURCE2, _SOURCE2_INVERT, _SOURCE2_MULTIPLIER, _SOURCE2_DIVISOR, _SOURCE2_TYPE)
            );
        }

        if (address(_SOURCE3) != address(0) || _SOURCE3_TYPE == SourceType.UniV3Checked) {
            rateSource3Operate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                true,
                OracleHopSource(_SOURCE3, _SOURCE3_INVERT, _SOURCE3_MULTIPLIER, _SOURCE3_DIVISOR, _SOURCE3_TYPE)
            );
            rateSource3Liquidate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                false,
                OracleHopSource(_SOURCE3, _SOURCE3_INVERT, _SOURCE3_MULTIPLIER, _SOURCE3_DIVISOR, _SOURCE3_TYPE)
            );
        }

        if (address(_SOURCE4) != address(0) || _SOURCE4_TYPE == SourceType.UniV3Checked) {
            rateSource4Operate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                true,
                OracleHopSource(_SOURCE4, _SOURCE4_INVERT, _SOURCE4_MULTIPLIER, _SOURCE4_DIVISOR, _SOURCE4_TYPE)
            );
            rateSource4Liquidate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                false,
                OracleHopSource(_SOURCE4, _SOURCE4_INVERT, _SOURCE4_MULTIPLIER, _SOURCE4_DIVISOR, _SOURCE4_TYPE)
            );
        }

        if (address(_SOURCE5) != address(0) || _SOURCE5_TYPE == SourceType.UniV3Checked) {
            rateSource5Operate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                true,
                OracleHopSource(_SOURCE5, _SOURCE5_INVERT, _SOURCE5_MULTIPLIER, _SOURCE5_DIVISOR, _SOURCE5_TYPE)
            );
            rateSource5Liquidate_ = _getExchangeRateForHop(
                (10 ** OracleUtils.RATE_OUTPUT_DECIMALS),
                false,
                OracleHopSource(_SOURCE5, _SOURCE5_INVERT, _SOURCE5_MULTIPLIER, _SOURCE5_DIVISOR, _SOURCE5_TYPE)
            );
        }
    }

    /// @notice Returns the configured OracleHopSources
    function getOracleHopSources() public view returns (OracleHopSource[] memory sources_) {
        sources_ = new OracleHopSource[](5);
        sources_[0] = OracleHopSource(_SOURCE1, _SOURCE1_INVERT, _SOURCE1_MULTIPLIER, _SOURCE1_DIVISOR, _SOURCE1_TYPE);
        sources_[1] = OracleHopSource(_SOURCE2, _SOURCE2_INVERT, _SOURCE2_MULTIPLIER, _SOURCE2_DIVISOR, _SOURCE2_TYPE);
        sources_[2] = OracleHopSource(_SOURCE3, _SOURCE3_INVERT, _SOURCE3_MULTIPLIER, _SOURCE3_DIVISOR, _SOURCE3_TYPE);
        sources_[3] = OracleHopSource(_SOURCE4, _SOURCE4_INVERT, _SOURCE4_MULTIPLIER, _SOURCE4_DIVISOR, _SOURCE4_TYPE);
        sources_[4] = OracleHopSource(_SOURCE5, _SOURCE5_INVERT, _SOURCE5_MULTIPLIER, _SOURCE5_DIVISOR, _SOURCE5_TYPE);
    }
}

contracts/oracle/oracles/genericUniV3Che.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { ErrorTypes } from "../errorTypes.sol";
import { FluidOracle } from "../fluidOracle.sol";
import { FluidGenericOracleBase } from "./genericOracleBase.sol";
import { UniV3CheckedSourceReader } from "../sourceReaders/uniV3CheckedSourceReader.sol";

/// @notice generic configurable Oracle
/// combines up to 4 hops from sources such as
///  - an existing IFluidOracle (e.g. ContractRate)
///  - Redstone
///  - Chainlink
///  - UniV3 checked against Chainlink
contract FluidGenericUniV3CheckedOracle is FluidGenericOracleBase, UniV3CheckedSourceReader {
    constructor(
        string memory infoName_,
        OracleHopSource[] memory sources_,
        UniV3CheckCLRSConstructorParams memory uniV3Params_
    ) FluidGenericOracleBase(sources_) UniV3CheckedSourceReader(infoName_, uniV3Params_) {
        uint256 uniV3SourcesCount_;
        if (sources_[0].sourceType == SourceType.UniV3Checked) uniV3SourcesCount_++;
        if (sources_.length > 1 && sources_[1].sourceType == SourceType.UniV3Checked) uniV3SourcesCount_++;
        if (sources_.length > 2 && sources_[2].sourceType == SourceType.UniV3Checked) uniV3SourcesCount_++;
        if (sources_.length > 3 && sources_[3].sourceType == SourceType.UniV3Checked) uniV3SourcesCount_++;
        if (sources_.length > 4 && sources_[4].sourceType == SourceType.UniV3Checked) uniV3SourcesCount_++;

        if (uniV3SourcesCount_ != 1) {
            revert FluidOracleError(ErrorTypes.GenericOracle__InvalidParams);
        }
    }

    /// @dev verifies a hop source config
    function _verifyOracleHopSource(OracleHopSource memory source_) internal view virtual override {
        if (
            (source_.sourceType != SourceType.UniV3Checked && address(source_.source) == address(0)) ||
            source_.divisor == 0 ||
            source_.multiplier == 0 ||
            source_.divisor > 1e40 ||
            source_.multiplier > 1e40
        ) {
            revert FluidOracleError(ErrorTypes.GenericOracle__InvalidParams);
        }
    }

    /// @dev reads the exchange rate for a hop source
    function _readSource(
        address source_,
        SourceType sourceType_,
        bool isOperate_
    ) internal view virtual override returns (uint256 rate_) {
        if (sourceType_ == SourceType.Redstone || sourceType_ == SourceType.Chainlink) {
            rate_ = _readChainlinkSource(source_);
        } else if (sourceType_ == SourceType.Fluid) {
            rate_ = _readFluidSource(source_, isOperate_);
        } else if (sourceType_ == SourceType.UniV3Checked) {
            rate_ = _readUniV3CheckedSource(isOperate_);
        } else {
            // should never happen because of config checks in constructor
            revert FluidOracleError(ErrorTypes.GenericOracle__UnexpectedConfig);
        }
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateOperate() public view override returns (uint256 exchangeRate_) {
        exchangeRate_ = _getHopsExchangeRate(true);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateLiquidate() public view override returns (uint256 exchangeRate_) {
        exchangeRate_ = _getHopsExchangeRate(false);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRate() public view override returns (uint256 exchangeRate_) {
        exchangeRate_ = _getHopsExchangeRate(false);
    }
}

contracts/oracle/oracles/uniV3CheckCLRSO.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracle } from "../fluidOracle.sol";
import { FallbackOracleImpl } from "../implementations/fallbackOracleImpl.sol";
import { UniV3OracleImpl } from "../implementations/uniV3OracleImpl.sol";
import { ErrorTypes } from "../errorTypes.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";

/// @title   UniswapV3 checked against Chainlink / Redstone Oracle. Either one reported as exchange rate.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          the price from a UniV3 pool (compared against 3 TWAPs) and (optionally) comparing it against a Chainlink
///          or Redstone price (one of Chainlink or Redstone being the main source and the other one the fallback source).
///          Alternatively it can also use Chainlink / Redstone as main price and use UniV3 as check price.
/// @dev     The process for getting the aggregate oracle price is:
///           1. Fetch the UniV3 TWAPS, the latest interval is used as the current price
///           2. Verify this price is within an acceptable DELTA from the Uniswap TWAPS e.g.:
///              a. 240 to 60s
///              b. 60 to 15s
///              c. 15 to 1s (last block)
///              d. 1 to 0s (current)
///           3. (unless UniV3 only mode): Verify this price is within an acceptable DELTA from the Chainlink / Redstone Oracle
///           4. If it passes all checks, return the price. Otherwise use fallbacks, usually to Chainlink. In extreme edge-cases revert.
/// @dev     For UniV3 with check mode, if fetching the check price fails, the UniV3 rate is used directly.
contract UniV3CheckCLRSOracle is FluidOracle, UniV3OracleImpl, FallbackOracleImpl {
    /// @dev Rate check oracle delta percent in 1e2 percent. If current uniswap price is out of this delta,
    /// current price fetching reverts.
    uint256 internal immutable _RATE_CHECK_MAX_DELTA_PERCENT;

    /// @dev which oracle to use as final rate source:
    ///      - 1 = UniV3 ONLY (no check),
    ///      - 2 = UniV3 with Chainlink / Redstone check
    ///      - 3 = Chainlink / Redstone with UniV3 used as check.
    uint8 internal immutable _RATE_SOURCE;

    struct UniV3CheckCLRSConstructorParams {
        /// @param uniV3Params                UniV3Oracle constructor params struct.
        UniV3ConstructorParams uniV3Params;
        /// @param chainlinkParams            ChainlinkOracle constructor params struct for UniV3CheckCLRSOracle.
        ChainlinkConstructorParams chainlinkParams;
        /// @param redstoneOracle             Redstone Oracle data for UniV3CheckCLRSOracle. (address can be set to zero address if using Chainlink only)
        RedstoneOracleData redstoneOracle;
        /// @param rateSource                 which oracle to use as final rate source for UniV3CheckCLRSOracle:
        ///                                         - 1 = UniV3 ONLY (no check),
        ///                                         - 2 = UniV3 with Chainlink / Redstone check
        ///                                         - 3 = Chainlink / Redstone with UniV3 used as check.
        uint8 rateSource;
        /// @param fallbackMainSource         which oracle to use as CL/RS main source for UniV3CheckCLRSOracle: see FallbackOracleImpl constructor `mainSource_`
        uint8 fallbackMainSource;
        /// @param rateCheckMaxDeltaPercent   Rate check oracle delta in 1e2 percent for UniV3CheckCLRSOracle
        uint256 rateCheckMaxDeltaPercent;
    }

    constructor(
        string memory infoName_,
        UniV3CheckCLRSConstructorParams memory params_
    )
        UniV3OracleImpl(params_.uniV3Params)
        FallbackOracleImpl(params_.fallbackMainSource, params_.chainlinkParams, params_.redstoneOracle)
        FluidOracle(infoName_)
    {
        if (
            params_.rateSource < 1 ||
            params_.rateSource > 3 ||
            params_.rateCheckMaxDeltaPercent > OracleUtils.HUNDRED_PERCENT_DELTA_SCALER ||
            // Chainlink only Oracle with UniV3 check. Delta would be ignored so revert this type of Oracle setup.
            (params_.fallbackMainSource == 1 && params_.rateSource == 3)
        ) {
            revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidParams);
        }

        _RATE_CHECK_MAX_DELTA_PERCENT = params_.rateCheckMaxDeltaPercent;
        _RATE_SOURCE = params_.rateSource;
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateOperate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateLiquidate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @inheritdoc FluidOracle
    function getExchangeRate() public view virtual override returns (uint256 exchangeRate_) {
        return _getExchangeRate();
    }

    /// @notice returns all oracle related data as utility for easy off-chain / block explorer use in a single view method
    function uniV3CheckOracleData()
        public
        view
        returns (uint256 rateCheckMaxDelta_, uint256 rateSource_, uint256 fallbackMainSource_)
    {
        return (_RATE_CHECK_MAX_DELTA_PERCENT, _RATE_SOURCE, _FALLBACK_ORACLE_MAIN_SOURCE);
    }

    function _getExchangeRate() internal view returns (uint256 exchangeRate_) {
        if (_RATE_SOURCE == 1) {
            // uniswap is the only main source without check:
            // 1. get uniV3 rate.
            // 2. If that fails (outside delta range) -> revert (no other Oracle configured).
            exchangeRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                // fetching UniV3 failed or invalid delta -> revert
                revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
            }

            return exchangeRate_;
        }

        uint256 checkRate_;
        bool fallback_;
        if (_RATE_SOURCE == 2) {
            // uniswap is main source, with Chainlink / Redstone as check
            // 1. get uniV3 rate

            // case uniV3 rate fails (outside delta range):
            // 2. get Chainlink rate. -> if successful, use Chainlink as result
            // 3. if Chainlink fails too, get Redstone -> if successful, use Redstone as result
            // 4. if Redstone fails too, revert

            // case if uniV3 rate is ok
            // 2. get Chainlink or Redstone rate for check (one is configured as main check source, other one is fallback source)
            //    -> if both fail to fetch, use uniV3 rate directly.
            // 3. check the delta for uniV3 rate against the check soure rate. -> if ok, return uniV3 rate
            // 4. if delta check fails, check delta against the fallback check source. -> if ok, return uniV3 rate
            // 5. if delta check fails for both sources, return Chainlink price

            exchangeRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                // uniV3 failed or invalid delta -> use (Chainlink with Redstone as fallback)
                exchangeRate_ = _getChainlinkOrRedstoneAsFallback();
                if (exchangeRate_ == 0) {
                    // Chainlink / Redstone failed too -> revert
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
                }
                return exchangeRate_;
            }

            (checkRate_, fallback_) = _getRateWithFallback();
            if (checkRate_ == 0) {
                // check price source failed to fetch -> directly use uniV3 TWAP checked price
                // Note uniV3 price fetching was successful, would have been caught otherwise above.
                return exchangeRate_;
            }
        } else {
            // Chainlink / Redstone is main source, with uniV3 as check.
            // 1. get Chainlink / Redstone rate (one is configured as main source, other one is fallback source)

            // case when both Chainlink & Redstone fail:
            // 2. get uniV3 rate. if successful, use uniV3 rate. otherwise, revert (all oracles failed).

            // case when Chainlink / Redstone fetch is successful:
            // 2. get uniV3 rate for check.
            // 3. if uniV3 rate fails to fetch (outside delta), use Chainlink / Redstone directly (skip check).
            // 4. if uniV3 rate is ok, check the delta for Chainlink / Redstone rate against uniV3 rate.
            //    -> if ok, return Chainlink / Redstone (main) rate
            // 5. if delta check fails, check delta against the fallback main source.
            //    -> if ok, return fallback main rate
            // 6. if delta check fails for both sources, return Chainlink price.

            (exchangeRate_, fallback_) = _getRateWithFallback();
            checkRate_ = _getUniV3ExchangeRate();

            if (exchangeRate_ == 0) {
                if (checkRate_ == 0) {
                    // all oracles failed, revert
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__ExchangeRateZero);
                }

                // Both Chainlink & Redstone failed -> directly use uniV3 TWAP checked price
                // Note uniV3 price fetching was successful, would have been caught otherwise above.
                return checkRate_;
            }

            if (checkRate_ == 0) {
                // uniV3 failed -> skip check against Uniswap price.

                return exchangeRate_;
            }
        }

        if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
            if (fallback_) {
                // fallback already used, no other rate available to check.

                // if price is chainlink price -> return it.
                if (_FALLBACK_ORACLE_MAIN_SOURCE == 3) {
                    // redstone with Chainlink as fallback
                    return _RATE_SOURCE == 2 ? checkRate_ : exchangeRate_; // if rate source is 2, Chainlink rate is in checkRate_
                }

                // if price is redstone price -> revert
                revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidPrice);
            }

            if (_FALLBACK_ORACLE_MAIN_SOURCE == 1) {
                // 1 = only chainlink and UniV3 is configured and delta check failed. no fallback available.
                if (_RATE_SOURCE == 2) {
                    // case where uniV3 is main source with only Chainlink as check rate Oracle configured.
                    // delta check failed -> return Chainlink price (instead of uniV3 price).
                    return checkRate_;
                }

                // here: if (_FALLBACK_ORACLE_MAIN_SOURCE == 1 && _RATE_SOURCE == 3)
                // rate source is 3: Chainlink as main, uniV3 as delta. delta check failed.
                // this Oracle type would basically be a more expensive Chainlink-only Oracle because the delta check against UniV3 is ignored.
                // this setup is reverted in constructor, but in any case returning Chainlink price here even though this code should never be reached.
                return exchangeRate_; // exchangeRate_ here is chainlink price
            }

            // fallback not done yet -> check against fallback price.
            // So if originally Chainlink was fetched and delta failed, check against Redstone.
            // if originally Redstone was fetched and delta failed, check against Chainlink.
            if (_FALLBACK_ORACLE_MAIN_SOURCE == 2) {
                // 2 = Chainlink with Redstone Fallback. delta check against Chainlink failed. try against Redstone.
                uint256 redstoneRate_ = _getRedstoneExchangeRate();
                uint256 chainlinkRate_;
                if (_RATE_SOURCE == 2) {
                    // uniV3 main source. -> update checkRate_ with Redstone price
                    chainlinkRate_ = checkRate_;
                    checkRate_ = redstoneRate_;
                } else {
                    // uniV3 is check source. -> update exchangeRate_ with Redstone price
                    chainlinkRate_ = exchangeRate_;
                    exchangeRate_ = redstoneRate_;
                }

                if (redstoneRate_ == 0) {
                    // fetching Redstone failed. So delta UniV3 <> Chainlink failed, fetching Redstone as backup failed.
                    // -> return chainlink price (for both cases when Chainlink is main and when UniV3 is the main source).
                    return chainlinkRate_;
                }

                if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
                    // delta check against Redstone failed too. return Chainlink price
                    return chainlinkRate_;
                }

                // delta check against Redstone passed. if uniV3 main source -> return uniV3, else return Redstone.
                // exchangeRate_ is already set correctly for this.
            } else {
                // 3 = Redstone with Chainlink Fallback. delta check against Redstone failed. try against Chainlink.
                uint256 chainlinkRate_ = _getChainlinkExchangeRate();
                if (chainlinkRate_ == 0) {
                    // fetching Chainlink failed. So delta UniV3 <> Redstone failed, fetching Chainlink as backup check failed.
                    // -> revert.
                    revert FluidOracleError(ErrorTypes.UniV3CheckCLRSOracle__InvalidPrice);
                }

                if (_RATE_SOURCE == 3) {
                    // uniV3 is check source. -> update exchangeRate_ with Chainlink price.
                    // Optimization: in this case we can directly return chainlink price, because if delta check between
                    // Chainlink (new main source) and uniV3 (check source) fails, we anyway return Chainlink price still.
                    return chainlinkRate_;
                }

                // uniV3 main source. -> update checkRate_ with Chainlink price and compare delta again
                checkRate_ = chainlinkRate_;

                if (OracleUtils.isRateOutsideDelta(exchangeRate_, checkRate_, _RATE_CHECK_MAX_DELTA_PERCENT)) {
                    // delta check against Chainlink failed too. case here can only be where uniV3 would have been
                    // main source and Chainlink check source. -> return Chainlink as price instead of uniV3
                    return checkRate_;
                }

                // delta check against Chainlink passed. if uniV3 main source -> return uniV3, else return Chainlink.
                // exchangeRate_ is already set correctly for this.
            }
        }
    }
}

contracts/oracle/oracles/weETHUniV3Check.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracle } from "../fluidOracle.sol";
import { UniV3CheckCLRSOracle } from "./uniV3CheckCLRSOracle.sol";
import { WeETHOracleImpl } from "../implementations/weETHOracleImpl.sol";
import { IWeETH } from "../interfaces/external/IWeETH.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";

/// @title   weETHOracle combined with a uniV3CheckCLRSOracle.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          1. weETH Oracle price for weETH -> eETH = ETH (pegged)
///          2. result from 1. combined with a uniV3CheckCLRSOracle to get someToken (e.g. ETH) -> someToken2.
///          e.g. when going from weETH to USDC:
///          1. weETH -> eETH = ETH via weETH Oracle
///          2. ETH -> USDC via UniV3 ETH <> USDC pool checked against ETH -> USDC Chainlink feed.
contract WeETHUniV3CheckCLRSOracle is FluidOracle, WeETHOracleImpl, UniV3CheckCLRSOracle {
    /// @notice                       constructs a WeETHUniV3CheckCLRSOracle with all inherited contracts
    /// @param infoName_         Oracle identify helper name.
    /// @param weETH_                address of the weETH contract
    /// @param uniV3CheckCLRSParams_ UniV3CheckCLRSOracle constructor params
    constructor(
        string memory infoName_,
        IWeETH weETH_,
        UniV3CheckCLRSConstructorParams memory uniV3CheckCLRSParams_
    ) WeETHOracleImpl(weETH_) UniV3CheckCLRSOracle(infoName_, uniV3CheckCLRSParams_) {}

    /// @inheritdoc FluidOracle
    function getExchangeRateOperate()
        public
        view
        override(FluidOracle, UniV3CheckCLRSOracle)
        returns (uint256 exchangeRate_)
    {
        //    get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0.
        //    (super.getExchangeRate() returns UniV3CheckCLRSOracle rate, no other inherited contract has this.)
        //    Combine this rate with the weETH -> eETH = ETH rate.
        exchangeRate_ =
            (super.getExchangeRateOperate() * _getWeETHExchangeRate()) /
            (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateLiquidate()
        public
        view
        override(FluidOracle, UniV3CheckCLRSOracle)
        returns (uint256 exchangeRate_)
    {
        //    get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0.
        //    (super.getExchangeRate() returns UniV3CheckCLRSOracle rate, no other inherited contract has this.)
        //    Combine this rate with the weETH -> eETH = ETH rate.
        exchangeRate_ =
            (super.getExchangeRateLiquidate() * _getWeETHExchangeRate()) /
            (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRate() public view override(FluidOracle, UniV3CheckCLRSOracle) returns (uint256 exchangeRate_) {
        return getExchangeRateOperate();
    }
}

contracts/oracle/oracles/wstETHCLRS2UniV.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { FluidOracle } from "../fluidOracle.sol";
import { UniV3CheckCLRSOracle } from "./uniV3CheckCLRSOracle.sol";
import { WstETHOracleImpl } from "../implementations/wstETHOracleImpl.sol";
import { FallbackOracleImpl2 } from "../implementations/fallbackOracleImpl2.sol";
import { IWstETH } from "../interfaces/external/IWstETH.sol";
import { OracleUtils } from "../libraries/oracleUtils.sol";
import { ErrorTypes } from "../errorTypes.sol";

// @dev uses FallbackOracleImpl2 to avoid conflicts with already used ChainlinkOracleImpl, RedstoneOracleImpl and
// FallbackOracleImpl in UniV3CheckCLRSOracle.

/// @title   wstETHCLRSOracle combined with a uniV3CheckCLRSOracle.
/// @notice  Gets the exchange rate between the underlying asset and the peg asset by using:
///          1. wstETH Oracle price in combination with rate from Chainlink price feeds (or Redstone as fallback).
///             combining those two into one rate resulting in wstETH <> someToken
///          2. result from 1. combined with a uniV3CheckCLRSOracle to get from someToken <> someToken2
///          e.g. when going from wstETH to USDC:
///          1. wstETH -> stETH wstETH Oracle, stETH -> ETH Chainlink feed.
///          2. ETH -> USDC via UniV3 ETH <> USDC pool checked against ETH -> USDC Chainlink feed.
contract WstETHCLRS2UniV3CheckCLRSOracle is FluidOracle, WstETHOracleImpl, FallbackOracleImpl2, UniV3CheckCLRSOracle {
    struct WstETHCLRS2ConstructorParams {
        /// @param wstETH                         address of the wstETH contract
        IWstETH wstETH;
        /// @param fallbackMainSource             which oracle to use as main source for wstETH <> CLRS: 1 = Chainlink, 2 = Redstone (other one is fallback).
        uint8 fallbackMainSource;
        /// @param chainlinkParams                chainlink Oracle constructor params struct for wstETH <> CLRS.
        ChainlinkConstructorParams chainlinkParams;
        /// @param redstoneOracle                 Redstone Oracle data for wstETH <> CLRS. (address can be set to zero address if using Chainlink only)
        RedstoneOracleData redstoneOracle;
    }

    /// @notice                       constructs a WstETHCLRS2UniV3CheckCLRSOracle with all inherited contracts
    /// @param infoName_         Oracle identify helper name.
    /// @param wstETHCLRS2Params_    WstETHCLRS2ConstructorParams for wstETH <> CLRS Token2 conversion
    /// @param uniV3CheckCLRSParams_ UniV3CheckCLRSOracle constructor params
    constructor(
        string memory infoName_,
        WstETHCLRS2ConstructorParams memory wstETHCLRS2Params_,
        UniV3CheckCLRSConstructorParams memory uniV3CheckCLRSParams_
    )
        WstETHOracleImpl(wstETHCLRS2Params_.wstETH)
        FallbackOracleImpl2(
            wstETHCLRS2Params_.fallbackMainSource,
            wstETHCLRS2Params_.chainlinkParams,
            wstETHCLRS2Params_.redstoneOracle
        )
        UniV3CheckCLRSOracle(infoName_, uniV3CheckCLRSParams_)
    {}

    /// @inheritdoc FluidOracle
    function getExchangeRateOperate()
        public
        view
        override(FluidOracle, UniV3CheckCLRSOracle)
        returns (uint256 exchangeRate_)
    {
        // 1. get CLRS Oracle rate for stETH <> CLRS feed. uses FallbackOracleImpl2
        (exchangeRate_, ) = _getRateWithFallback2();
        if (exchangeRate_ == 0) {
            // revert if fetched exchange rate is 0
            revert FluidOracleError(ErrorTypes.WstETHCLRS2UniV3CheckCLRSOracle__ExchangeRateZero);
        }

        // 2. combine CLRS feed price with wstETH price to have wstETH <> stETH <> SomeToken fully converted
        exchangeRate_ = (_getWstETHExchangeRate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);

        // 3. get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0. Combine this rate with existing.
        //    (super.getExchangeRate returns UniV3CheckCLRSOracle rate, no other inherited contract has this.)
        exchangeRate_ = (super.getExchangeRateOperate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRateLiquidate()
        public
        view
        override(FluidOracle, UniV3CheckCLRSOracle)
        returns (uint256 exchangeRate_)
    {
        // 1. get CLRS Oracle rate for stETH <> CLRS feed. uses FallbackOracleImpl2
        (exchangeRate_, ) = _getRateWithFallback2();
        if (exchangeRate_ == 0) {
            // revert if fetched exchange rate is 0
            revert FluidOracleError(ErrorTypes.WstETHCLRS2UniV3CheckCLRSOracle__ExchangeRateZero);
        }

        // 2. combine CLRS feed price with wstETH price to have wstETH <> stETH <> SomeToken fully converted
        exchangeRate_ = (_getWstETHExchangeRate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);

        // 3. get rate from UniV3Check Oracle (likely uniV3 / Chainlink checked against for delta). This always returns
        //    a price if some rate is valid, with multiple fallbacks. Can not return 0. Combine this rate with existing.
        //    (super.getExchangeRate returns UniV3CheckCLRSOracle rate, no other inherited contract has this.)
        exchangeRate_ = (super.getExchangeRateLiquidate() * exchangeRate_) / (10 ** OracleUtils.RATE_OUTPUT_DECIMALS);
    }

    /// @inheritdoc FluidOracle
    function getExchangeRate() public view override(FluidOracle, UniV3CheckCLRSOracle) returns (uint256 exchangeRate_) {
        return getExchangeRateOperate();
    }

    /// @notice which oracle to use as main source:
    ///          - 1 = Chainlink ONLY (no fallback)
    ///          - 2 = Chainlink with Redstone Fallback
    ///          - 3 = Redstone with Chainlink Fallback
    function FALLBACK_ORACLE2_MAIN_SOURCE() public view returns (uint8) {
        return _FALLBACK_ORACLE2_MAIN_SOURCE;
    }
}

