// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { LiquiditySlotsLink } from "../../libraries/liquiditySlotsLink.sol";
import { IFluidLiquidity } from "../../liquidity/interfaces/iLiquidity.sol";

import { ErrorTypes } from "./errorTypes.sol";
import { Error } from "./error.sol";
import { Variables } from "./variables.sol";

/// @dev Flashloan protocol helper methods
abstract contract Helpers is Variables, Error {
    constructor(IFluidLiquidity liquidity_) Variables(liquidity_) {}

    /// @notice Reads user borrow data of currency to be lent.
    /// @param token_ The loan currency.
    /// @return user borrow data for `token`.
    function _readUserBorrowData(address token_) internal view returns (uint256) {
        bytes32 slot_ = LiquiditySlotsLink.calculateDoubleMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_USER_BORROW_DOUBLE_MAPPING_SLOT,
            address(this),
            token_
        );
        return LIQUIDITY.readFromStorage(slot_);
    }

    /// @notice Checks if the given address is valid (not equal to address(0)).
    /// @param value_ The address to check..
    function _checkValidAddress(address value_) internal pure {
        if (value_ == address(0)) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__ZeroAddress);
        }
    }

    /// @notice Ensures that the provided address is valid (not equal to address(0)).
    /// @param value_ The address to check.
    modifier validAddress(address value_) {
        _checkValidAddress(value_);
        _;
    }
}

