contracts/protocols/flashloan/main.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";
import { SafeCast } from "@openzeppelin/contracts/utils/math/SafeCast.sol";

import { Helpers } from "./helpers.sol";
import { Structs } from "./structs.sol";
import { ErrorTypes } from "./errorTypes.sol";
import { IFluidFlashLender } from "./interfaces/iFlashLender.sol";
import { IERC3156FlashBorrower } from "./interfaces/IERC3156FlashBorrower.sol";
import { IFlashBorrower } from "./interfaces/iFlashBorrower.sol";

import { IFluidLiquidity } from "../../liquidity/interfaces/iLiquidity.sol";
import { IProtocol } from "../../liquidity/userModule/main.sol";

import { LiquidityCalcs } from "../../libraries/liquidityCalcs.sol";
import { LiquiditySlotsLink } from "../../libraries/liquiditySlotsLink.sol";

/// @notice contract that provides Flashloans via Fluid. ERC-3156 compatible.
/// In addition also implements a `flashLoanMultiple()` method which can be used to flash loan multiple tokens at once.
/// @dev Fee is a constant 0. This contract is not upgradeable.
//
// @dev Fee is not really implemented, just the parts that are needed to ensure compatibility with ERC-3156.
// @dev user borrow amount for this contract at Liquidity will slowly grow but that is not an issue as the amount
// being repaid is still the exact original amount always.
contract FluidFlashLender is Structs, IFluidFlashLender, IProtocol, Helpers {
    using SafeERC20 for IERC20;

    /// @notice emitted whenever funds for a certain `token` are rescued to Liquidity
    event LogRescueFunds(address indexed token);

    /// @notice emitted when a flashloan has been taken and repaid
    event LogFlashLoan(address indexed token, uint256 indexed amount, address indexed receiver);

    constructor(IFluidLiquidity liquidity_) Helpers(liquidity_) {}

    /// @notice The amount of currency available to be lent.
    /// @param token_ The loan currency.
    /// @return The amount of `token` that can be borrowed.
    function maxFlashLoan(address token_) public view returns (uint256) {
        // maximum flashloan is either:
        // a) the borrow limit of Flashloan protocol at Liquidity or
        // b) the total available amount of token at Liquidity (if lower than borrow limit)

        // get current exchange rates
        (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) = LiquidityCalcs.calcExchangePrices(
            LIQUIDITY.readFromStorage(
                LiquiditySlotsLink.calculateMappingStorageSlot(
                    LiquiditySlotsLink.LIQUIDITY_EXCHANGE_PRICES_MAPPING_SLOT,
                    token_
                )
            )
        );

        // a) get the borrow limit
        uint256 userBorrowData_ = _readUserBorrowData(token_);
        uint256 userBorrow_ = (userBorrowData_ >> LiquiditySlotsLink.BITS_USER_BORROW_AMOUNT) & LiquidityCalcs.X64;
        // get borrow limit
        uint256 borrowLimit_ = LiquidityCalcs.calcBorrowLimitBeforeOperate(userBorrowData_, userBorrow_);
        // revert if Flashloan protocol has already borrowed more than 0.0001% of borrow limit for the `token_`. This
        // catches any duplicate token borrows. We can not just check for user borrow == 0 because of rounding.
        if (userBorrow_ > (borrowLimit_ / 100_000)) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__DuplicateBorrow);
        }
        // subtract any already borrowed amount
        borrowLimit_ -= userBorrow_;

        if (userBorrowData_ & 1 == 1) {
            // convert raw borrow limit amount to normal for withInterest mode
            borrowLimit_ = (borrowLimit_ * borrowExchangePrice_) / LiquidityCalcs.EXCHANGE_PRICES_PRECISION;
        }

        // b) get the total available amount at Liquidity (total supply - total borrow)
        uint256 totalAmounts_ = LIQUIDITY.readFromStorage(
            LiquiditySlotsLink.calculateMappingStorageSlot(
                LiquiditySlotsLink.LIQUIDITY_TOTAL_AMOUNTS_MAPPING_SLOT,
                token_
            )
        );
        // totalSupply = supplyRawInterest normalized from raw + supplyInterestFree
        uint256 totalSupply_ = LiquidityCalcs.getTotalSupply(totalAmounts_, supplyExchangePrice_);
        // get total borrow for token interest free + with interest converted from raw
        uint256 totalBorrow_ = LiquidityCalcs.getTotalBorrow(totalAmounts_, borrowExchangePrice_);

        uint256 availableAmount_;
        unchecked {
            // todo: this amount would also have to include max utilization limit.
            // potential other updates might be needed in flashloan protocol before release as Liquidity layer
            // had some changes etc.
            availableAmount_ = totalBorrow_ > totalSupply_ ? 0 : totalSupply_ - totalBorrow_;
        }

        return borrowLimit_ < availableAmount_ ? borrowLimit_ : availableAmount_;
    }

    /// @notice The fee to be charged for a given loan.
    /// @param token_ loan currency.
    /// @param amount_ The amount of tokens lent.
    /// @return The amount of `token` to be charged for the loan, on top of the returned principal.
    function flashFee(address token_, uint256 amount_) external view returns (uint256) {
        if (_readUserBorrowData(token_) == 0) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__TokenNotSupported);
        }
        return _FLASH_FEE;
    }

    /// @notice Initiate a flash loan.
    /// @dev receiver_ must approve `amount_` of `token_` for payback. For native token, it must be send as msg.value.
    /// @param receiver_ The receiver of the tokens in the loan, and the receiver of the callback.
    /// @param token_ The loan currency.
    /// @param amount_ The amount of tokens lent.
    /// @param data_ Arbitrary data structure, intended to contain user-defined parameters.
    function flashLoan(
        IERC3156FlashBorrower receiver_,
        address token_,
        uint256 amount_,
        bytes calldata data_
    ) external validAddress(address(receiver_)) validAddress(token_) returns (bool) {
        if (amount_ > maxFlashLoan(token_)) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__MaxFlashLoanAmountExceeded);
        }

        LIQUIDITY.operate(token_, 0, SafeCast.toInt256(amount_), address(0), address(receiver_), new bytes(0));

        if (
            receiver_.onFlashLoan(msg.sender, token_, amount_, _FLASH_FEE, data_) != _RETURN_VALUE_FOR_FLASHLOAN_SINGLE
        ) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__InvalidReturnValue);
        }

        LIQUIDITY.operate{ value: token_ == _NATIVE_TOKEN_ADDRESS ? amount_ : 0 }(
            token_,
            0,
            -int256(amount_),
            address(0),
            address(0),
            abi.encode(address(receiver_))
        );

        emit LogFlashLoan(token_, amount_, address(receiver_));

        return true;
    }

    /// @notice Initiate a flash loan with multiple tokens.
    /// @dev receiver_ must approve `amount_` of  each token for payback. For native token, it must be send as msg.value.
    /// @param receiver_ The receiver of the tokens in the loan, and the receiver of the callback.
    /// @param tokensWithAmounts_ An array of token addresses and loan amounts.
    /// @param data_ Arbitrary data structure, intended to contain user-defined parameters.
    /// @return 'true' if the flash loans are successful
    function flashLoanMultiple(
        IFlashBorrower receiver_,
        AddressUint256[] calldata tokensWithAmounts_,
        bytes calldata data_
    ) external validAddress(address(receiver_)) returns (bool) {
        uint256 length_ = tokensWithAmounts_.length;

        // execute all borrows at Liquidity
        for (uint256 i; i < length_; ) {
            _checkValidAddress(tokensWithAmounts_[i].addr);
            if (tokensWithAmounts_[i].value > maxFlashLoan(tokensWithAmounts_[i].addr)) {
                revert FluidFlashLenderError(ErrorTypes.FlashLender__MaxFlashLoanAmountExceeded);
            }

            LIQUIDITY.operate(
                tokensWithAmounts_[i].addr,
                0,
                SafeCast.toInt256(tokensWithAmounts_[i].value),
                address(0),
                address(receiver_),
                new bytes(0)
            );

            unchecked {
                i++;
            }
        }

        if (
            //onFlashLoanMultiple or onFlashLoan callback and then we could put tokens with amounts if data callback
            receiver_.onFlashLoanMultiple(msg.sender, tokensWithAmounts_, _FLASH_FEE, data_) !=
            _RETURN_VALUE_FOR_FLASHLOAN_MULTIPLE
        ) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__InvalidReturnValue);
        }

        // execute all paybacks at Liquidity
        for (uint256 i; i < length_; ) {
            {
                address receiverAddress_ = address(receiver_); // required to avoid stack too deep error

                LIQUIDITY.operate{
                    value: tokensWithAmounts_[i].addr == _NATIVE_TOKEN_ADDRESS ? tokensWithAmounts_[i].value : 0
                }(
                    tokensWithAmounts_[i].addr,
                    0,
                    -int256(tokensWithAmounts_[i].value),
                    address(0),
                    address(0),
                    abi.encode(receiverAddress_)
                );

                emit LogFlashLoan(tokensWithAmounts_[i].addr, tokensWithAmounts_[i].value, receiverAddress_);
            }

            unchecked {
                i++;
            }
        }

        return true;
    }

    /// @notice   flashLender liquidity callback
    /// @param    token_ The token being transferred
    /// @param    amount_ The amount being transferred
    function liquidityCallback(address token_, uint256 amount_, bytes calldata data_) external {
        if (msg.sender != address(LIQUIDITY)) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__Unauthorized);
        }

        address receiver_ = abi.decode(data_, (address));
        IERC20(token_).safeTransferFrom(receiver_, address(LIQUIDITY), amount_);
    }

    /// @dev @Fallback function to receive Ether
    receive() external payable {}

    /// @notice sends any potentially stuck funds to Liquidity contract. Only callable by Liquidity auths.
    //
    // @dev this contract never holds any funds:
    // -> borrowed funds are directly sent from Liquidity to the receiver.
    // -> at payback, only approval is given by receiver to this contract but funds are directly transferred from
    // receiver to Liquidity. For native token, msg.value is sent forward to Liquidity operate() call.
    // Thus we can simply create a method to rescue any potentially stuck funds by sending them to Liquidity,
    // where that excess amount is recognized as revenue and could be withdrawn by Governance.
    function rescueFunds(address token_) external {
        // todo make this method protected?
        if (
            // msg.sender must be an auth or Governance at Liquidity
            (LIQUIDITY.readFromStorage(
                LiquiditySlotsLink.calculateMappingStorageSlot(
                    LiquiditySlotsLink.LIQUIDITY_AUTHS_MAPPING_SLOT,
                    msg.sender
                )
            ) &
                1 !=
                1) && address(uint160(LIQUIDITY.readFromStorage(_GOVERNANCE_SLOT))) != msg.sender
        ) {
            revert FluidFlashLenderError(ErrorTypes.FlashLender__Unauthorized);
        }

        if (token_ == _NATIVE_TOKEN_ADDRESS) {
            Address.sendValue(payable(address(LIQUIDITY)), address(this).balance);
        } else {
            IERC20(token_).safeTransfer(address(LIQUIDITY), IERC20(token_).balanceOf(address(this)));
        }

        emit LogRescueFunds(token_);
    }

    /// @notice repays `repayAmount_` for `token_` at Liquidity.
    /// Can be used to balance out any leftover rounding differences at Liquidity if ever needed.
    function repayAtLiquidity(address token_, uint256 repayAmount_) external payable {
        // todo use safeCast here. casting to int256 here otherwise can lead to positive amounts
        // see https://docs.google.com/document/d/15A-fobewpCnS8DVMHlOIBADB4ikvOIKO3H0TTnRoWR0
        // also make this a protected method?
        // -int256(repayAmount_) converts uint256 positive values to its corresponding negative int256:
        // 123 -> -123
        // However, because the negative is produced by making twos complement, it is possible to craft the value with most significant bit flipped to produce positive numbers:
        // 0xffffffffffffffffffffffffffffffffffffffffffffffffffffffffffffff85 ->  123
        // This doesnt lead to issues as passing positive values would lead to revert as the borrower specified as address(0).
        // However, to prevent issues that might arise from further development and implementation changes,
        // we recommend using SafeCast at expense of slightly higher gas costs for this particular call.
        LIQUIDITY.operate{ value: msg.value }(
            token_,
            0,
            -int256(repayAmount_),
            address(0),
            address(0),
            abi.encode(msg.sender)
        );
    }
}

