pragma solidity 0.8.21;

import { SSTORE2 } from "solmate/src/utils/SSTORE2.sol";

import { Error } from "../../error.sol";
import { ErrorTypes } from "../../errorTypes.sol";
import { IFluidDexFactory } from "../../interfaces/iDexFactory.sol";
import { MiniDeployer } from "../deploymentHelpers/miniDeployer.sol";
import { LiquiditySlotsLink } from "../../../../libraries/liquiditySlotsLink.sol";
import { BytesSliceAndConcat } from "../../../../libraries/bytesSliceAndConcat.sol";

import { IFluidDexT1 } from "../../interfaces/iDexT1.sol";
import { FluidDexT1Shift } from "../../poolT1/coreModule/core/shift.sol";
import { FluidDexT1Admin } from "../../poolT1/adminModule/main.sol";

interface IERC20 {
    function decimals() external view returns (uint8);
}

contract FluidDexT1DeploymentLogic is Error {
    address internal constant NATIVE_TOKEN = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    /// @dev SSTORE2 pointer for the PoolT1 creation code. Stored externally to reduce factory bytecode (in 2 parts)
    address internal immutable POOL_T1_CREATIONCODE_ADDRESS_1;
    address internal immutable POOL_T1_CREATIONCODE_ADDRESS_2;

    /// @dev SSTORE2 pointers for the creation code of various operations contracts
    address internal immutable COL_OPERATIONS_CREATIONCODE_ADDRESS;
    address internal immutable DEBT_OPERATIONS_CREATIONCODE_ADDRESS;
    address internal immutable PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_1;
    address internal immutable PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_2;

    /// @notice address of liquidity contract
    address public immutable LIQUIDITY;

    /// @notice address of dexfactory contract
    address public immutable DEX_FACTORY;

    /// @notice address of Admin implementation
    address public immutable ADMIN_IMPLEMENTATION;

    /// @notice address of Shift implementation
    address public immutable SHIFT_IMPLEMENTATION;

    /// @notice address of Deployer Contract
    address public immutable CONTRACT_DEPLOYER;

    /// @notice address of MiniDeployer Contract
    MiniDeployer public immutable MINI_DEPLOYER;

    /// @notice address of this contract
    address public immutable ADDRESS_THIS;

    /// @notice Emitted when a new dexT1 is deployed.
    /// @param dex The address of the newly deployed dex.
    /// @param dexId The id of the newly deployed dex.
    /// @param supplyToken The address of the supply token.
    /// @param borrowToken The address of the borrow token.
    event DexT1Deployed(address indexed dex, uint256 dexId, address indexed supplyToken, address indexed borrowToken);

    /// @dev                            Deploys a contract using the CREATE opcode with the provided bytecode (`bytecode_`).
    ///                                 This is an internal function, meant to be used within the contract to facilitate the deployment of other contracts.
    /// @param bytecode_                The bytecode of the contract to be deployed.
    /// @return address_                Returns the address of the deployed contract.
    function _deploy(bytes memory bytecode_) internal returns (address address_) {
        if (bytecode_.length == 0) {
            revert FluidDexError(ErrorTypes.DexFactory__InvalidOperation);
        }
        /// @solidity memory-safe-assembly
        assembly {
            address_ := create(0, add(bytecode_, 0x20), mload(bytecode_))
        }
        if (address_ == address(0)) {
            revert FluidDexError(ErrorTypes.DexFactory__InvalidOperation);
        }
    }

    constructor(
        address liquidity_,
        address dexFactory_,
        address contractDeployer_,
        address colOperations_,
        address debtOperations_,
        address perfectOperationsAndSwapOut1_,
        address perfectOperationsAndSwapOut2_,
        address mainAddress1_,
        address mainAddress2_
    ) {
        LIQUIDITY = liquidity_;
        DEX_FACTORY = dexFactory_;
        CONTRACT_DEPLOYER = contractDeployer_;

        POOL_T1_CREATIONCODE_ADDRESS_1 = mainAddress1_;
        POOL_T1_CREATIONCODE_ADDRESS_2 = mainAddress2_;

        ADDRESS_THIS = address(this);

        // Deploy mini deployer
        MINI_DEPLOYER = new MiniDeployer(DEX_FACTORY);

        // Deploy admin implementation
        FluidDexT1Admin adminImplementation = new FluidDexT1Admin();
        ADMIN_IMPLEMENTATION = address(adminImplementation);

        // Deploy shift implementation
        FluidDexT1Shift shiftImplementation = new FluidDexT1Shift(CONTRACT_DEPLOYER);
        SHIFT_IMPLEMENTATION = address(shiftImplementation);

        COL_OPERATIONS_CREATIONCODE_ADDRESS = colOperations_;
        DEBT_OPERATIONS_CREATIONCODE_ADDRESS = debtOperations_;
        PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_1 = perfectOperationsAndSwapOut1_;
        PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_2 = perfectOperationsAndSwapOut2_;
    }

    function dexT1(
        address token0_,
        address token1_,
        uint256 oracleMapping_
    ) external returns (bytes memory dexCreationBytecode_) {
        if (address(this) == ADDRESS_THIS) revert FluidDexError(ErrorTypes.DexFactory__OnlyDelegateCallAllowed);

        if (token0_ == token1_) revert FluidDexError(ErrorTypes.DexFactory__SameTokenNotAllowed);
        if (token0_ > token1_) revert FluidDexError(ErrorTypes.DexFactory__TokenConfigNotProper);

        IFluidDexT1.ConstantViews memory constants_;
        constants_.liquidity = LIQUIDITY;
        constants_.factory = address(this);
        constants_.implementations.shift = SHIFT_IMPLEMENTATION;
        constants_.deployerContract = CONTRACT_DEPLOYER;
        constants_.token0 = token0_;
        constants_.token1 = token1_;
        constants_.dexId = IFluidDexFactory(address(this)).totalDexes();
        constants_.oracleMapping = oracleMapping_;

        address dex_ = IFluidDexFactory(address(this)).getDexAddress(constants_.dexId);

        constants_ = _calculateLiquidityDexSlots(constants_, dex_);

        // Deploy perfect operations and oracle implementation
        address perfectOperationsAndOracle_ = MINI_DEPLOYER.deployContract(
            abi.encodePacked(perfectOperationsCreationCode(), abi.encode(constants_))
        );

        // Deploy col operations implementation through mini deployer
        address colOperations_ = MINI_DEPLOYER.deployContract(
            abi.encodePacked(colOperationsCreationCode(), abi.encode(constants_))
        );

        // Deploy debt operations implementation
        address debtOperations_ = MINI_DEPLOYER.deployContract(
            abi.encodePacked(debtOperationsCreationCode(), abi.encode(constants_))
        );

        constants_.implementations.admin = ADMIN_IMPLEMENTATION;
        constants_.implementations.perfectOperationsAndOracle = perfectOperationsAndOracle_;
        constants_.implementations.colOperations = colOperations_;
        constants_.implementations.debtOperations = debtOperations_;

        dexCreationBytecode_ = abi.encodePacked(dexT1CreationBytecode(), abi.encode(constants_));

        emit DexT1Deployed(dex_, constants_.dexId, token0_, token1_);

        return dexCreationBytecode_;
    }

    /// @notice returns the stored DexT1 creation bytecode
    function dexT1CreationBytecode() public view returns (bytes memory) {
        return
            BytesSliceAndConcat.bytesConcat(
                SSTORE2.read(POOL_T1_CREATIONCODE_ADDRESS_1),
                SSTORE2.read(POOL_T1_CREATIONCODE_ADDRESS_2)
            );
    }

    /// @dev Retrieves the creation code for the FluidDexT1OperationsCol contract
    function colOperationsCreationCode() public view returns (bytes memory) {
        return SSTORE2.read(COL_OPERATIONS_CREATIONCODE_ADDRESS);
    }

    /// @dev Retrieves the creation code for the FluidDexT1OperationsDebt contract
    function debtOperationsCreationCode() public view returns (bytes memory) {
        return SSTORE2.read(DEBT_OPERATIONS_CREATIONCODE_ADDRESS);
    }

    /// @dev Retrieves the creation code for the FluidDexT1PerfectOperations contract
    function perfectOperationsCreationCode() public view returns (bytes memory) {
        return
            BytesSliceAndConcat.bytesConcat(
                SSTORE2.read(PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_1),
                SSTORE2.read(PERFECT_OPERATIONS_AND_SWAP_OUT_CREATIONCODE_ADDRESS_2)
            );
    }

    /// @dev                          Calculates the liquidity dex slots for the given supply token, borrow token, and dex (`dex_`).
    /// @param constants_             Constants struct as used in Dex T1
    /// @param dex_                   The address of the dex.
    /// @return liquidityDexSlots_    Returns the calculated liquidity dex slots set in the `IFluidDexT1.ConstantViews` struct.
    function _calculateLiquidityDexSlots(
        IFluidDexT1.ConstantViews memory constants_,
        address dex_
    ) private pure returns (IFluidDexT1.ConstantViews memory) {
        constants_.supplyToken0Slot = LiquiditySlotsLink.calculateDoubleMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_USER_SUPPLY_DOUBLE_MAPPING_SLOT,
            dex_,
            constants_.token0
        );
        constants_.borrowToken0Slot = LiquiditySlotsLink.calculateDoubleMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_USER_BORROW_DOUBLE_MAPPING_SLOT,
            dex_,
            constants_.token0
        );
        constants_.supplyToken1Slot = LiquiditySlotsLink.calculateDoubleMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_USER_SUPPLY_DOUBLE_MAPPING_SLOT,
            dex_,
            constants_.token1
        );
        constants_.borrowToken1Slot = LiquiditySlotsLink.calculateDoubleMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_USER_BORROW_DOUBLE_MAPPING_SLOT,
            dex_,
            constants_.token1
        );
        constants_.exchangePriceToken0Slot = LiquiditySlotsLink.calculateMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_EXCHANGE_PRICES_MAPPING_SLOT,
            constants_.token0
        );
        constants_.exchangePriceToken1Slot = LiquiditySlotsLink.calculateMappingStorageSlot(
            LiquiditySlotsLink.LIQUIDITY_EXCHANGE_PRICES_MAPPING_SLOT,
            constants_.token1
        );

        return constants_;
    }
}
","keccak256":"0x56f8332a78d4be65c1cb0294ea36cc3dea09e904b0e07f415cc87e8cb1b56069","license":"BUSL-1.1"},"contracts/protocols/dex/interfaces/iDexFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

interface IFluidDexFactory {
    /// @notice Global auth is auth for all dexes
    function isGlobalAuth(address auth_) external view returns (bool);

    /// @notice Dex auth is auth for a specific dex
    function isDexAuth(address vault_, address auth_) external view returns (bool);

    /// @notice Total dexes deployed.
    function totalDexes() external view returns (uint256);

    /// @notice Compute dexAddress
    function getDexAddress(uint256 dexId_) external view returns (address);

    /// @notice read uint256 `result_` for a storage `slot_` key
    function readFromStorage(bytes32 slot_) external view returns (uint256 result_);
}
","keccak256":"0xfc81e2f8de72dcf84022b69495996f59fe560cac430767f504de1064c32f550e","license":"MIT"},"contracts/protocols/dex/interfaces/iDexT1.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

interface IFluidDexT1 {
    error FluidDexError(uint256 errorId);

    /// @notice used to simulate swap to find the output amount
    error FluidDexSwapResult(uint256 amountOut);

    error FluidDexPerfectLiquidityOutput(uint256 token0Amt, uint token1Amt);

    error FluidDexSingleTokenOutput(uint256 tokenAmt);

    error FluidDexLiquidityOutput(uint256 shares);

    error FluidDexPricesAndExchangeRates(PricesAndExchangePrice pex_);

    /// @notice returns the dex id
    function DEX_ID() external view returns (uint256);

    /// @notice reads uint256 data `result_` from storage at a bytes32 storage `slot_` key.
    function readFromStorage(bytes32 slot_) external view returns (uint256 result_);

    struct Implementations {
        address shift;
        address admin;
        address colOperations;
        address debtOperations;
        address perfectOperationsAndOracle;
    }

    struct ConstantViews {
        uint256 dexId;
        address liquidity;
        address factory;
        Implementations implementations;
        address deployerContract;
        address token0;
        address token1;
        bytes32 supplyToken0Slot;
        bytes32 borrowToken0Slot;
        bytes32 supplyToken1Slot;
        bytes32 borrowToken1Slot;
        bytes32 exchangePriceToken0Slot;
        bytes32 exchangePriceToken1Slot;
        uint256 oracleMapping;
    }

    struct ConstantViews2 {
        uint token0NumeratorPrecision;
        uint token0DenominatorPrecision;
        uint token1NumeratorPrecision;
        uint token1DenominatorPrecision;
    }

    struct PricesAndExchangePrice {
        uint lastStoredPrice; // last stored price in 1e27 decimals
        uint centerPrice; // last stored price in 1e27 decimals
        uint upperRange; // price at upper range in 1e27 decimals
        uint lowerRange; // price at lower range in 1e27 decimals
        uint geometricMean; // geometric mean of upper range & lower range in 1e27 decimals
        uint supplyToken0ExchangePrice;
        uint borrowToken0ExchangePrice;
        uint supplyToken1ExchangePrice;
        uint borrowToken1ExchangePrice;
    }

    struct CollateralReserves {
        uint token0RealReserves;
        uint token1RealReserves;
        uint token0ImaginaryReserves;
        uint token1ImaginaryReserves;
    }

    struct DebtReserves {
        uint token0Debt;
        uint token1Debt;
        uint token0RealReserves;
        uint token1RealReserves;
        uint token0ImaginaryReserves;
        uint token1ImaginaryReserves;
    }

    function getCollateralReserves(
        uint geometricMean_,
        uint upperRange_,
        uint lowerRange_,
        uint token0SupplyExchangePrice_,
        uint token1SupplyExchangePrice_
    ) external view returns (CollateralReserves memory c_);

    function getDebtReserves(
        uint geometricMean_,
        uint upperRange_,
        uint lowerRange_,
        uint token0BorrowExchangePrice_,
        uint token1BorrowExchangePrice_
    ) external view returns (DebtReserves memory d_);

    // reverts with FluidDexPricesAndExchangeRates(pex_);
    function getPricesAndExchangePrices() external;

    function constantsView() external view returns (ConstantViews memory constantsView_);

    function constantsView2() external view returns (ConstantViews2 memory constantsView2_);

    struct Oracle {
        uint twap1by0; // TWAP price
        uint lowestPrice1by0; // lowest price point
        uint highestPrice1by0; // highest price point
        uint twap0by1; // TWAP price
        uint lowestPrice0by1; // lowest price point
        uint highestPrice0by1; // highest price point
    }

    /// @dev This function allows users to swap a specific amount of input tokens for output tokens
    /// @param swap0to1_ Direction of swap. If true, swaps token0 for token1; if false, swaps token1 for token0
    /// @param amountIn_ The exact amount of input tokens to swap
    /// @param amountOutMin_ The minimum amount of output tokens the user is willing to accept
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with amountOut_
    /// @return amountOut_ The amount of output tokens received from the swap
    function swapIn(
        bool swap0to1_,
        uint256 amountIn_,
        uint256 amountOutMin_,
        address to_
    ) external payable returns (uint256 amountOut_);

    /// @dev Swap tokens with perfect amount out
    /// @param swap0to1_ Direction of swap. If true, swaps token0 for token1; if false, swaps token1 for token0
    /// @param amountOut_ The exact amount of tokens to receive after swap
    /// @param amountInMax_ Maximum amount of tokens to swap in
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with amountIn_
    /// @return amountIn_ The amount of input tokens used for the swap
    function swapOut(
        bool swap0to1_,
        uint256 amountOut_,
        uint256 amountInMax_,
        address to_
    ) external payable returns (uint256 amountIn_);

    /// @dev Deposit tokens in equal proportion to the current pool ratio
    /// @param shares_ The number of shares to mint
    /// @param maxToken0Deposit_ Maximum amount of token0 to deposit
    /// @param maxToken1Deposit_ Maximum amount of token1 to deposit
    /// @param estimate_ If true, function will revert with estimated deposit amounts without executing the deposit
    /// @return token0Amt_ Amount of token0 deposited
    /// @return token1Amt_ Amount of token1 deposited
    function depositPerfect(
        uint shares_,
        uint maxToken0Deposit_,
        uint maxToken1Deposit_,
        bool estimate_
    ) external payable returns (uint token0Amt_, uint token1Amt_);

    /// @dev This function allows users to withdraw a perfect amount of collateral liquidity
    /// @param shares_ The number of shares to withdraw
    /// @param minToken0Withdraw_ The minimum amount of token0 the user is willing to accept
    /// @param minToken1Withdraw_ The minimum amount of token1 the user is willing to accept
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with token0Amt_ & token1Amt_
    /// @return token0Amt_ The amount of token0 withdrawn
    /// @return token1Amt_ The amount of token1 withdrawn
    function withdrawPerfect(
        uint shares_,
        uint minToken0Withdraw_,
        uint minToken1Withdraw_,
        address to_
    ) external returns (uint token0Amt_, uint token1Amt_);

    /// @dev This function allows users to borrow tokens in equal proportion to the current debt pool ratio
    /// @param shares_ The number of shares to borrow
    /// @param minToken0Borrow_ Minimum amount of token0 to borrow
    /// @param minToken1Borrow_ Minimum amount of token1 to borrow
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with token0Amt_ & token1Amt_
    /// @return token0Amt_ Amount of token0 borrowed
    /// @return token1Amt_ Amount of token1 borrowed
    function borrowPerfect(
        uint shares_,
        uint minToken0Borrow_,
        uint minToken1Borrow_,
        address to_
    ) external returns (uint token0Amt_, uint token1Amt_);

    /// @dev This function allows users to pay back borrowed tokens in equal proportion to the current debt pool ratio
    /// @param shares_ The number of shares to pay back
    /// @param maxToken0Payback_ Maximum amount of token0 to pay back
    /// @param maxToken1Payback_ Maximum amount of token1 to pay back
    /// @param estimate_ If true, function will revert with estimated payback amounts without executing the payback
    /// @return token0Amt_ Amount of token0 paid back
    /// @return token1Amt_ Amount of token1 paid back
    function paybackPerfect(
        uint shares_,
        uint maxToken0Payback_,
        uint maxToken1Payback_,
        bool estimate_
    ) external payable returns (uint token0Amt_, uint token1Amt_);

    /// @dev This function allows users to deposit tokens in any proportion into the col pool
    /// @param token0Amt_ The amount of token0 to deposit
    /// @param token1Amt_ The amount of token1 to deposit
    /// @param minSharesAmt_ The minimum amount of shares the user expects to receive
    /// @param estimate_ If true, function will revert with estimated shares without executing the deposit
    /// @return shares_ The amount of shares minted for the deposit
    function deposit(
        uint token0Amt_,
        uint token1Amt_,
        uint minSharesAmt_,
        bool estimate_
    ) external payable returns (uint shares_);

    /// @dev This function allows users to withdraw tokens in any proportion from the col pool
    /// @param token0Amt_ The amount of token0 to withdraw
    /// @param token1Amt_ The amount of token1 to withdraw
    /// @param maxSharesAmt_ The maximum number of shares the user is willing to burn
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with shares_
    /// @return shares_ The number of shares burned for the withdrawal
    function withdraw(
        uint token0Amt_,
        uint token1Amt_,
        uint maxSharesAmt_,
        address to_
    ) external returns (uint shares_);

    /// @dev This function allows users to borrow tokens in any proportion from the debt pool
    /// @param token0Amt_ The amount of token0 to borrow
    /// @param token1Amt_ The amount of token1 to borrow
    /// @param maxSharesAmt_ The maximum amount of shares the user is willing to receive
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with shares_
    /// @return shares_ The amount of borrow shares minted to represent the borrowed amount
    function borrow(
        uint token0Amt_,
        uint token1Amt_,
        uint maxSharesAmt_,
        address to_
    ) external returns (uint shares_);

    /// @dev This function allows users to payback tokens in any proportion to the debt pool
    /// @param token0Amt_ The amount of token0 to payback
    /// @param token1Amt_ The amount of token1 to payback
    /// @param minSharesAmt_ The minimum amount of shares the user expects to burn
    /// @param estimate_ If true, function will revert with estimated shares without executing the payback
    /// @return shares_ The amount of borrow shares burned for the payback
    function payback(
        uint token0Amt_,
        uint token1Amt_,
        uint minSharesAmt_,
        bool estimate_
    ) external payable returns (uint shares_);

    /// @dev This function allows users to withdraw their collateral with perfect shares in one token
    /// @param shares_ The number of shares to burn for withdrawal
    /// @param minToken0_ The minimum amount of token0 the user expects to receive (set to 0 if withdrawing in token1)
    /// @param minToken1_ The minimum amount of token1 the user expects to receive (set to 0 if withdrawing in token0)
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with withdrawAmt_
    /// @return withdrawAmt_ The amount of tokens withdrawn in the chosen token
    function withdrawPerfectInOneToken(
        uint shares_,
        uint minToken0_,
        uint minToken1_,
        address to_
    ) external returns (
        uint withdrawAmt_
    );

    /// @dev This function allows users to payback their debt with perfect shares in one token
    /// @param shares_ The number of shares to burn for payback
    /// @param maxToken0_ The maximum amount of token0 the user is willing to pay (set to 0 if paying back in token1)
    /// @param maxToken1_ The maximum amount of token1 the user is willing to pay (set to 0 if paying back in token0)
    /// @param estimate_ If true, the function will revert with the estimated payback amount without executing the payback
    /// @return paybackAmt_ The amount of tokens paid back in the chosen token
    function paybackPerfectInOneToken(
        uint shares_,
        uint maxToken0_,
        uint maxToken1_,
        bool estimate_
    ) external payable returns (
        uint paybackAmt_
    );

    /// @dev the oracle assumes last set price of pool till the next swap happens.
    /// There's a possibility that during that time some interest is generated hence the last stored price is not the 100% correct price for the whole duration
    /// but the difference due to interest will be super low so this difference is ignored
    /// For example 2 swaps happened 10min (600 seconds) apart and 1 token has 10% higher interest than other.
    /// then that token will accrue about 10% * 600 / secondsInAYear = ~0.0002%
    /// @param secondsAgos_ array of seconds ago for which TWAP is needed. If user sends [10, 30, 60] then twaps_ will return [10-0, 30-10, 60-30]
    /// @return twaps_ twap price, lowest price (aka minima) & highest price (aka maxima) between secondsAgo checkpoints
    /// @return currentPrice_ price of pool after the most recent swap
    function oraclePrice(
        uint[] memory secondsAgos_
    ) external view returns (
        Oracle[] memory twaps_,
        uint currentPrice_
    );
}
","keccak256":"0xa405007cdd45409412ce0fe02a991cadbae17760bc15402c839375505f69d7b2","license":"MIT"},"contracts/protocols/dex/poolT1/adminModule/events.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { Structs } from "./structs.sol";

abstract contract Events is Structs {
    /// @dev Emitted when smart collateral is turned on
    /// @param token0Amt The amount of token0 used for smart collateral
    event LogTurnOnSmartCol(uint token0Amt);

    /// @dev Emitted when smart debt is turned on
    /// @param token0Amt The amount of token0 used for smart debt
    event LogTurnOnSmartDebt(uint token0Amt);

    /// @dev Emitted when fee and revenue cut are updated
    /// @param fee The new fee value
    /// @param revenueCut The new revenue cut value
    event LogUpdateFeeAndRevenueCut(uint fee, uint revenueCut);

    /// @dev Emitted when range percents are updated
    /// @param upperPercent The new upper percent value
    /// @param lowerPercent The new lower percent value
    /// @param shiftTime The new shift time value
    event LogUpdateRangePercents(uint upperPercent, uint lowerPercent, uint shiftTime);

    /// @dev Emitted when threshold percent is updated
    /// @param upperThresholdPercent The new upper threshold percent value
    /// @param lowerThresholdPercent The new lower threshold percent value
    /// @param thresholdShiftTime The new threshold shift time value
    /// @param shiftTime The new shift time value
    event LogUpdateThresholdPercent(
        uint upperThresholdPercent,
        uint lowerThresholdPercent,
        uint thresholdShiftTime,
        uint shiftTime
    );

    /// @dev Emitted when center price address is updated
    /// @param centerPriceAddress The new center price address nonce
    /// @param percent The new percent value
    /// @param time The new time value
    event LogUpdateCenterPriceAddress(uint centerPriceAddress, uint percent, uint time);

    /// @dev Emitted when hook address is updated
    /// @param hookAddress The new hook address nonce
    event LogUpdateHookAddress(uint hookAddress);

    /// @dev Emitted when center price limits are updated
    /// @param maxCenterPrice The new maximum center price
    /// @param minCenterPrice The new minimum center price
    event LogUpdateCenterPriceLimits(uint maxCenterPrice, uint minCenterPrice);

    /// @dev Emitted when utilization limit is updated
    /// @param token0UtilizationLimit The new utilization limit for token0
    /// @param token1UtilizationLimit The new utilization limit for token1
    event LogUpdateUtilizationLimit(uint token0UtilizationLimit, uint token1UtilizationLimit);

    /// @dev Emitted when user supply configs are updated
    /// @param userSupplyConfigs The array of updated user supply configurations
    event LogUpdateUserSupplyConfigs(UserSupplyConfig[] userSupplyConfigs);

    /// @dev Emitted when user borrow configs are updated
    /// @param userBorrowConfigs The array of updated user borrow configurations
    event LogUpdateUserBorrowConfigs(UserBorrowConfig[] userBorrowConfigs);

    /// @dev Emitted when a user is paused
    /// @param user The address of the paused user
    /// @param pauseSupply Whether supply operations are paused
    /// @param pauseBorrow Whether borrow operations are paused
    event LogPauseUser(address user, bool pauseSupply, bool pauseBorrow);

    /// @dev Emitted when a user is unpaused
    /// @param user The address of the unpaused user
    /// @param unpauseSupply Whether supply operations are unpaused
    /// @param unpauseBorrow Whether borrow operations are unpaused
    event LogUnpauseUser(address user, bool unpauseSupply, bool unpauseBorrow);

    /// @notice Emitted when the pool configuration is initialized
    /// @param smartCol Whether smart collateral is enabled
    /// @param smartDebt Whether smart debt is enabled
    /// @param token0ColAmt The amount of token0 collateral
    /// @param token0DebtAmt The amount of token0 debt
    /// @param fee The fee percentage (in 4 decimals, 10000 = 1%)
    /// @param revenueCut The revenue cut percentage (in 4 decimals, 100000 = 10%)
    /// @param centerPriceAddress The nonce for the center price contract address
    /// @param hookAddress The nonce for the hook contract address
    event LogInitializePoolConfig(
        bool smartCol,
        bool smartDebt,
        uint token0ColAmt,
        uint token0DebtAmt,
        uint fee,
        uint revenueCut,
        uint centerPriceAddress,
        uint hookAddress
    );

    /// @notice Emitted when the price parameters are initialized
    /// @param upperPercent The upper range percent (in 4 decimals, 10000 = 1%)
    /// @param lowerPercent The lower range percent (in 4 decimals, 10000 = 1%)
    /// @param upperShiftThreshold The upper shift threshold (in 4 decimals, 10000 = 1%)
    /// @param lowerShiftThreshold The lower shift threshold (in 4 decimals, 10000 = 1%)
    /// @param thresholdShiftTime The time for threshold shift (in seconds)
    /// @param maxCenterPrice The maximum center price
    /// @param minCenterPrice The minimum center price
    event LogInitializePriceParams(
        uint upperPercent,
        uint lowerPercent,
        uint upperShiftThreshold,
        uint lowerShiftThreshold,
        uint thresholdShiftTime,
        uint maxCenterPrice,
        uint minCenterPrice
    );

    /// @dev Emitted when swap and arbitrage are paused
    event LogPauseSwapAndArbitrage();

    /// @dev Emitted when swap and arbitrage are unpaused
    event LogUnpauseSwapAndArbitrage();

    /// @notice emitted when user withdrawal limit is updated
    event LogUpdateUserWithdrawalLimit(address user, uint256 newLimit);

    /// @dev Emitted when funds are rescued
    /// @param token The address of the token
    event LogRescueFunds(address token);

    /// @dev Emitted when max supply shares are updated
    /// @param maxSupplyShares The new maximum supply shares
    event LogUpdateMaxSupplyShares(uint maxSupplyShares);

    /// @dev Emitted when max borrow shares are updated
    /// @param maxBorrowShares The new maximum borrow shares
    event LogUpdateMaxBorrowShares(uint maxBorrowShares);

    /// @dev Emitted when oracle activation is toggled
    /// @param turnOn Whether oracle is turned on
    event LogToggleOracleActivation(bool turnOn);
}
","keccak256":"0x994b70cf2b277da5b12e3f01dea21625002ee88b0bd4b985130a9192de7f6c01","license":"BUSL-1.1"},"contracts/protocols/dex/poolT1/adminModule/main.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { Variables } from "../common/variables.sol";
import { Events } from "./events.sol";
import { ErrorTypes } from "../../errorTypes.sol";
import { Error } from "../../error.sol";
import { BigMathMinified } from "../../../../libraries/bigMathMinified.sol";
import { ConstantVariables } from "../common/constantVariables.sol";
import { Structs } from "./structs.sol";
import { IFluidDexT1 } from "../../interfaces/iDexT1.sol";
import { IFluidLiquidity } from "../../../../liquidity/interfaces/iLiquidity.sol";
import { SafeTransfer } from "../../../../libraries/safeTransfer.sol";
import { AddressCalcs } from "../../../../libraries/addressCalcs.sol";
import { DexSlotsLink } from "../../../../libraries/dexSlotsLink.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @notice Fluid Dex protocol Admin Module contract.
///         Implements admin related methods to set pool configs
///         Methods are limited to be called via delegateCall only. Dex CoreModule ("DexT1" contract)
///         is expected to call the methods implemented here after checking the msg.sender is authorized.
contract FluidDexT1Admin is ConstantVariables, Variables, Structs, Events, Error {
    using BigMathMinified for uint256;

    address private immutable ADDRESS_THIS;

    constructor() {
        ADDRESS_THIS = address(this);
    }

    modifier _onlyDelegateCall() {
        // also indirectly checked by `_check` because pool can never be initialized as long as the initialize method
        // is delegate call only, but just to be sure on Admin logic we add the modifier everywhere nonetheless.
        if (address(this) == ADDRESS_THIS) {
            revert FluidDexError(ErrorTypes.DexT1Admin__OnlyDelegateCallAllowed);
        }
        _;
    }

    modifier _check() {
        if ((dexVariables2 & 3) == 0) {
            revert FluidDexError(ErrorTypes.DexT1Admin__PoolNotInitialized);
        }
        _;
    }

    /// @dev checks that `value_` address is a contract (which includes address zero check) or native address
    function _checkIsContractOrNativeAddress(address value_) internal view {
        if (value_.code.length == 0 && value_ != NATIVE_TOKEN) {
            revert FluidDexError(ErrorTypes.DexT1Admin__AddressNotAContract);
        }
    }

    /// @dev checks that `value_` address is a contract (which includes address zero check)
    function _checkIsContract(address value_) internal view {
        if (value_.code.length == 0) {
            revert FluidDexError(ErrorTypes.DexT1Admin__AddressNotAContract);
        }
    }

    function turnOnSmartCol(uint token0Amt_) public payable _check _onlyDelegateCall {
        if (dexVariables2 & 1 == 1) {
            revert FluidDexError(ErrorTypes.DexT1Admin__SmartColIsAlreadyOn);
        }
        uint centerPrice_ = (dexVariables >> 81) & X40;
        centerPrice_ = (centerPrice_ >> DEFAULT_EXPONENT_SIZE) << (centerPrice_ & DEFAULT_EXPONENT_MASK);
        _turnOnSmartCol(token0Amt_, centerPrice_);

        dexVariables2 = dexVariables2 | 1;

        emit LogTurnOnSmartCol(token0Amt_);
    }

    function _turnOnSmartCol(uint token0Amt_, uint centerPrice_) internal {
        IFluidDexT1.ConstantViews memory c_ = IFluidDexT1(address(this)).constantsView();
        IFluidDexT1.ConstantViews2 memory c2_ = IFluidDexT1(address(this)).constantsView2();

        uint token0AmtAdjusted_ = (token0Amt_ * c2_.token0NumeratorPrecision) / c2_.token0DenominatorPrecision;

        uint token1AmtAdjusted_ = (centerPrice_ * token0AmtAdjusted_) / 1e27;

        uint token1Amt_ = (token1AmtAdjusted_ * c2_.token1DenominatorPrecision) / c2_.token1NumeratorPrecision;

        IFluidLiquidity liquidity_ = IFluidLiquidity(c_.liquidity);

        // if both tokens are not native token and msg.value is sent, revert
        if (msg.value > 0 && c_.token0 != NATIVE_TOKEN && c_.token1 != NATIVE_TOKEN) {
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }

        address token_;
        uint amt_;
        for (uint i = 0; i < 2; i++) {
            if (i == 0) {
                token_ = c_.token0;
                amt_ = token0Amt_;
            } else {
                token_ = c_.token1;
                amt_ = token1Amt_;
            }
            if (token_ == NATIVE_TOKEN) {
                if (msg.value > amt_) {
                    SafeTransfer.safeTransferNative(msg.sender, msg.value - amt_);
                } else if (msg.value < amt_) {
                    revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
                }
                // deposit
                liquidity_.operate{ value: amt_ }(
                    token_,
                    int(amt_),
                    0,
                    address(0),
                    address(0),
                    abi.encode(amt_, false, msg.sender)
                );
            } else {
                // deposit
                liquidity_.operate(token_, int(amt_), 0, address(0), address(0), abi.encode(amt_, false, msg.sender));
            }
        }

        // minting shares according to whatever tokenAmt is bigger
        // adding shares on storage but not adding shares for any user, hence locking these shares forever
        // adjusted amounts are in 12 decimals, making shares in 18 decimals
        uint totalSupplyShares_ = (token0AmtAdjusted_ > token1AmtAdjusted_)
            ? token0AmtAdjusted_ * 10 ** (18 - TOKENS_DECIMALS_PRECISION)
            : token1AmtAdjusted_ * 10 ** (18 - TOKENS_DECIMALS_PRECISION);

        if (totalSupplyShares_ < NINE_DECIMALS) {
            revert FluidDexError(ErrorTypes.DexT1Admin__UnexpectedPoolState);
        }

        // setting initial max shares as X128
        totalSupplyShares_ = (totalSupplyShares_ & X128) | (X128 << 128);
        // storing in storage
        _totalSupplyShares = totalSupplyShares_;
    }

    function turnOnSmartDebt(uint token0Amt_) public _check _onlyDelegateCall {
        if (dexVariables2 & 2 == 2) {
            revert FluidDexError(ErrorTypes.DexT1Admin__SmartDebtIsAlreadyOn);
        }
        uint centerPrice_ = (dexVariables >> 81) & X40;
        centerPrice_ = (centerPrice_ >> DEFAULT_EXPONENT_SIZE) << (centerPrice_ & DEFAULT_EXPONENT_MASK);
        _turnOnSmartDebt(token0Amt_, centerPrice_);

        dexVariables2 = dexVariables2 | 2;

        emit LogTurnOnSmartDebt(token0Amt_);
    }

    /// @dev Can only borrow if DEX pool address borrow config is added in Liquidity Layer for both the tokens else Liquidity Layer will revert
    /// governance will have access to _turnOnSmartDebt, technically governance here can borrow as much as limits are set
    /// so it's governance responsibility that it borrows small amount between $100 - $10,000
    /// Borrowing in 50:50 ratio (doesn't matter if pool configuration is set to 20:80, 30:70, etc, external swap will arbitrage & balance the pool)
    function _turnOnSmartDebt(uint token0Amt_, uint centerPrice_) internal {
        IFluidDexT1.ConstantViews memory c_ = IFluidDexT1(address(this)).constantsView();
        IFluidDexT1.ConstantViews2 memory c2_ = IFluidDexT1(address(this)).constantsView2();

        uint token0AmtAdjusted_ = (token0Amt_ * c2_.token0NumeratorPrecision) / c2_.token0DenominatorPrecision;

        uint token1AmtAdjusted_ = (centerPrice_ * token0AmtAdjusted_) / 1e27;

        uint token1Amt_ = (token1AmtAdjusted_ * c2_.token1DenominatorPrecision) / c2_.token1NumeratorPrecision;

        IFluidLiquidity liquidity_ = IFluidLiquidity(c_.liquidity);

        liquidity_.operate(c_.token0, 0, int(token0Amt_), address(0), TEAM_MULTISIG, new bytes(0));
        liquidity_.operate(c_.token1, 0, int(token1Amt_), address(0), TEAM_MULTISIG, new bytes(0));

        // minting shares as whatever tokenAmt is bigger
        // adding shares on storage but not adding shares for any user, hence locking these shares forever
        // adjusted amounts are in 12 decimals, making shares in 18 decimals
        uint totalBorrowShares_ = (token0AmtAdjusted_ > token1AmtAdjusted_)
            ? token0AmtAdjusted_ * 10 ** (18 - TOKENS_DECIMALS_PRECISION)
            : token1AmtAdjusted_ * 10 ** (18 - TOKENS_DECIMALS_PRECISION);

        if (totalBorrowShares_ < NINE_DECIMALS) {
            revert FluidDexError(ErrorTypes.DexT1Admin__UnexpectedPoolState);
        }

        // setting initial max shares as X128
        totalBorrowShares_ = (totalBorrowShares_ & X128) | (X128 << 128);
        // storing in storage
        _totalBorrowShares = totalBorrowShares_;
    }

    /// @param fee_ in 4 decimals, 10000 = 1%
    /// @param revenueCut_ in 4 decimals, 100000 = 10%, 10% cut on fee_, so if fee is 1% and cut is 10% then cut in swap amount will be 10% of 1% = 0.1%
    function updateFeeAndRevenueCut(uint fee_, uint revenueCut_) public _check _onlyDelegateCall {
        // cut is an integer in storage slot which is more than enough
        // but from UI we are allowing to send in 4 decimals to maintain consistency & avoid human error in future
        if (revenueCut_ != 0 && revenueCut_ < FOUR_DECIMALS) {
            // human input error. should send 0 for wanting 0, not 0 because of precision reduction.
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }

        revenueCut_ = revenueCut_ / FOUR_DECIMALS;

        if (fee_ > FIVE_DECIMALS || revenueCut_ > TWO_DECIMALS) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        dexVariables2 =
            (dexVariables2 & 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFC000003) |
            (fee_ << 2) |
            (revenueCut_ << 19);

        emit LogUpdateFeeAndRevenueCut(fee_, revenueCut_ * FOUR_DECIMALS);
    }

    /// @param upperPercent_ in 4 decimals, 10000 = 1%
    /// @param lowerPercent_ in 4 decimals, 10000 = 1%
    /// @param shiftTime_ in secs, in how much time the upper percent configs change should be fully done
    function updateRangePercents(
        uint upperPercent_,
        uint lowerPercent_,
        uint shiftTime_
    ) public _check _onlyDelegateCall {
        uint dexVariables2_ = dexVariables2;
        if (
            (upperPercent_ > (SIX_DECIMALS - FOUR_DECIMALS)) || // capping range to 99%.
            (lowerPercent_ > (SIX_DECIMALS - FOUR_DECIMALS)) || // capping range to 99%.
            (upperPercent_ == 0) ||
            (lowerPercent_ == 0) ||
            (shiftTime_ > X20) ||
            (((dexVariables2_ >> 26) & 1) == 1) // if last shift is still active then don't allow a newer shift
        ) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        dexVariables2 =
            (dexVariables2_ & 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF80000000003FFFFFF) |
            (uint((shiftTime_ > 0) ? 1 : 0) << 26) |
            (upperPercent_ << 27) |
            (lowerPercent_ << 47);

        uint oldUpperPercent_ = (dexVariables2_ >> 27) & X20;
        uint oldLowerPercent_ = (dexVariables2_ >> 47) & X20;

        if (shiftTime_ > 0) {
            _rangeShift = uint128(
                oldUpperPercent_ | (oldLowerPercent_ << 20) | (shiftTime_ << 40) | (block.timestamp << 60)
            );
        }
        // Note _rangeShift is reset when the previous shift is fully completed, which is forced to have happened through if check above

        emit LogUpdateRangePercents(upperPercent_, lowerPercent_, shiftTime_);
    }

    /// @param upperThresholdPercent_ in 4 decimals, 10000 = 1%
    /// @param lowerThresholdPercent_ in 4 decimals, 10000 = 1%
    /// @param thresholdShiftTime_ in secs, in how much time the threshold percent should take to shift the ranges
    /// @param shiftTime_ in secs, in how much time the upper config changes should be fully done.
    function updateThresholdPercent(
        uint upperThresholdPercent_,
        uint lowerThresholdPercent_,
        uint thresholdShiftTime_,
        uint shiftTime_
    ) public _check _onlyDelegateCall {
        uint dexVariables2_ = dexVariables2;

        // thresholds are with 0.1% precision, hence removing last 3 decimals.
        // we are allowing to send in 4 decimals to maintain consistency with other params
        upperThresholdPercent_ = upperThresholdPercent_ / THREE_DECIMALS;
        lowerThresholdPercent_ = lowerThresholdPercent_ / THREE_DECIMALS;
        if (
            (upperThresholdPercent_ > THREE_DECIMALS) ||
            (lowerThresholdPercent_ > THREE_DECIMALS) ||
            (thresholdShiftTime_ == 0) ||
            (thresholdShiftTime_ > X24) ||
            ((upperThresholdPercent_ == 0) && (lowerThresholdPercent_ > 0)) ||
            ((upperThresholdPercent_ > 0) && (lowerThresholdPercent_ == 0)) ||
            (shiftTime_ > X20) ||
            (((dexVariables2_ >> 67) & 1) == 1) // if last shift is still active then don't allow a newer shift
        ) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        dexVariables2 =
            (dexVariables2_ & 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF000000000007FFFFFFFFFFFFFFFF) |
            (uint((shiftTime_ > 0) ? 1 : 0) << 67) |
            (upperThresholdPercent_ << 68) |
            (lowerThresholdPercent_ << 78) |
            (thresholdShiftTime_ << 88);

        uint oldUpperThresholdPercent_ = (dexVariables2_ >> 68) & X10;
        uint oldLowerThresholdPercent_ = (dexVariables2_ >> 78) & X10;
        uint oldThresholdTime_ = (dexVariables2_ >> 88) & X24;

        if (shiftTime_ > 0) {
            _thresholdShift = uint128(
                oldUpperThresholdPercent_ |
                    (oldLowerThresholdPercent_ << 20) |
                    (shiftTime_ << 40) |
                    (block.timestamp << 60) |
                    (oldThresholdTime_ << 93)
            );
        }
        // Note _thresholdShift is reset when the previous shift is fully completed, which is forced to have happened through if check above

        emit LogUpdateThresholdPercent(
            upperThresholdPercent_ * THREE_DECIMALS,
            lowerThresholdPercent_ * THREE_DECIMALS,
            thresholdShiftTime_,
            shiftTime_
        );
    }

    /// @dev we are storing uint nonce from which we will calculate the contract address, to store an address we need 160 bits
    /// which is quite a lot of storage slot
    /// @param centerPriceAddress_ nonce < X30, this nonce will be used to calculate contract address
    function updateCenterPriceAddress(
        uint centerPriceAddress_,
        uint percent_,
        uint time_
    ) public _check _onlyDelegateCall {
        if ((centerPriceAddress_ > X30) || (percent_ == 0) || (percent_ > X20) || (time_ == 0) || (time_ > X20)) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        if (centerPriceAddress_ > 0) {
            IFluidDexT1.ConstantViews memory c_ = IFluidDexT1(address(this)).constantsView();
            address centerPrice_ = AddressCalcs.addressCalc(c_.deployerContract, centerPriceAddress_);
            _checkIsContract(centerPrice_);
            // note: if address is made 0 then as well in the last swap currentPrice is updated on storage, so code will start using that automatically
            dexVariables2 =
                (dexVariables2 & 0xFeFFFFFFFFFFFFFFFFFFFFFFFFFFC0000000FFFFFFFFFFFFFFFFFFFFFFFFFFFF) |
                (centerPriceAddress_ << 112) |
                (uint(1) << 248);

            _centerPriceShift = block.timestamp | (percent_ << 33) | (time_ << 53);
        } else {
            dexVariables2 = (dexVariables2 & 0xFeFFFFFFFFFFFFFFFFFFFFFFFFFFC0000000FFFFFFFFFFFFFFFFFFFFFFFFFFFF);

            _centerPriceShift = 0;
        }

        emit LogUpdateCenterPriceAddress(centerPriceAddress_, percent_, time_);
    }

    /// @dev we are storing uint nonce from which we will calculate the contract address, to store an address we need 160 bits
    /// which is quite a lot of storage slot
    /// @param hookAddress_ nonce < X30, this nonce will be used to calculate contract address
    function updateHookAddress(uint hookAddress_) public _check _onlyDelegateCall {
        if (hookAddress_ > X30) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        if (hookAddress_ > 0) {
            IFluidDexT1.ConstantViews memory c_ = IFluidDexT1(address(this)).constantsView();
            address hook_ = AddressCalcs.addressCalc(c_.deployerContract, hookAddress_);
            _checkIsContract(hook_);
        }

        dexVariables2 =
            (dexVariables2 & 0xFFFFFFFFFFFFFFFFFFFFF00000003FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF) |
            (hookAddress_ << 142);

        emit LogUpdateHookAddress(hookAddress_);
    }

    function updateCenterPriceLimits(uint maxCenterPrice_, uint minCenterPrice_) public _check _onlyDelegateCall {
        uint centerPrice_ = (dexVariables >> 81) & X40;
        centerPrice_ = (centerPrice_ >> DEFAULT_EXPONENT_SIZE) << (centerPrice_ & DEFAULT_EXPONENT_MASK);

        if (
            (maxCenterPrice_ <= minCenterPrice_) ||
            (centerPrice_ <= minCenterPrice_) ||
            (centerPrice_ >= maxCenterPrice_) ||
            (minCenterPrice_ == 0)
        ) {
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }

        dexVariables2 =
            (dexVariables2 & 0xFFFFFFF00000000000000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF) |
            (maxCenterPrice_.toBigNumber(20, 8, BigMathMinified.ROUND_UP) << 172) |
            (minCenterPrice_.toBigNumber(20, 8, BigMathMinified.ROUND_DOWN) << 200);

        emit LogUpdateCenterPriceLimits(maxCenterPrice_, minCenterPrice_);
    }

    function updateUtilizationLimit(
        uint token0UtilizationLimit_,
        uint token1UtilizationLimit_
    ) public _check _onlyDelegateCall {
        if (
            (token0UtilizationLimit_ != 0 && token0UtilizationLimit_ < THREE_DECIMALS) ||
            (token1UtilizationLimit_ != 0 && token1UtilizationLimit_ < THREE_DECIMALS)
        ) {
            // human input error. should send 0 for wanting 0, not 0 because of precision reduction.
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }

        token0UtilizationLimit_ = token0UtilizationLimit_ / THREE_DECIMALS;
        token1UtilizationLimit_ = token1UtilizationLimit_ / THREE_DECIMALS;

        if (token0UtilizationLimit_ > THREE_DECIMALS || token1UtilizationLimit_ > THREE_DECIMALS) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        dexVariables2 =
            (dexVariables2 & 0xFF00000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF) |
            (token0UtilizationLimit_ << 228) |
            (token1UtilizationLimit_ << 238);

        emit LogUpdateUtilizationLimit(
            token0UtilizationLimit_ * THREE_DECIMALS,
            token1UtilizationLimit_ * THREE_DECIMALS
        );
    }

    function updateUserSupplyConfigs(UserSupplyConfig[] memory userSupplyConfigs_) external _check _onlyDelegateCall {
        uint256 userSupplyData_;

        for (uint256 i; i < userSupplyConfigs_.length; ) {
            _checkIsContract(userSupplyConfigs_[i].user);
            if (userSupplyConfigs_[i].expandDuration == 0) {
                // can not set expand duration to 0 as that could cause a division by 0 in LiquidityCalcs.
                // having expand duration as 0 is anyway not an expected config so removing the possibility for that.
                // if no expansion is wanted, simply set expandDuration to 1 and expandPercent to 0.
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
            }
            if (userSupplyConfigs_[i].expandPercent > FOUR_DECIMALS) {
                revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
            }
            if (userSupplyConfigs_[i].expandDuration > X24) {
                // duration is max 24 bits
                revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
            }
            if (userSupplyConfigs_[i].baseWithdrawalLimit == 0) {
                // base withdrawal limit can not be 0. As a side effect, this ensures that there is no supply config
                // where all values would be 0, so configured users can be differentiated in the mapping.
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
            }
            // @dev baseWithdrawalLimit has no max bits amount as it is in normal token amount & converted to BigNumber

            // get current user config data from storage
            userSupplyData_ = _userSupplyData[userSupplyConfigs_[i].user];

            // Updating user data on storage
            _userSupplyData[userSupplyConfigs_[i].user] =
                // mask to update first bit + bits 162-217 (expand percentage, expand duration, base limit)
                (userSupplyData_ & 0xfffffffffc00000000000003ffffffffffffffffffffffffffffffffffffffff) |
                (1) |
                (userSupplyConfigs_[i].expandPercent << DexSlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) |
                (userSupplyConfigs_[i].expandDuration << DexSlotsLink.BITS_USER_SUPPLY_EXPAND_DURATION) |
                // convert base withdrawal limit to BigNumber for storage (10 | 8). (below this, 100% can be withdrawn)
                (userSupplyConfigs_[i].baseWithdrawalLimit.toBigNumber(
                    SMALL_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                ) << DexSlotsLink.BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT);

            unchecked {
                ++i;
            }
        }

        emit LogUpdateUserSupplyConfigs(userSupplyConfigs_);
    }

    /// @notice sets a new withdrawal limit as the current limit for a certain user
    /// @param user_ user address for which to update the withdrawal limit
    /// @param newLimit_ new limit until which user supply can decrease to.
    ///                  Important: input in raw. Must account for exchange price in input param calculation.
    ///                  Note any limit that is < max expansion or > current user supply will set max expansion limit or
    ///                  current user supply as limit respectively.
    ///                  - set 0 to make maximum possible withdrawable: instant full expansion, and if that goes
    ///                  below base limit then fully down to 0.
    ///                  - set type(uint256).max to make current withdrawable 0 (sets current user supply as limit).
    function updateUserWithdrawalLimit(address user_, uint256 newLimit_) external _check _onlyDelegateCall {
        _checkIsContract(user_);

        // get current user config data from storage
        uint256 userSupplyData_ = _userSupplyData[user_];
        if (userSupplyData_ == 0) {
            revert FluidDexError(ErrorTypes.DexT1Admin__UserNotDefined);
        }

        // get current user supply amount
        uint256 userSupply_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_AMOUNT) & X64;
        userSupply_ = (userSupply_ >> DEFAULT_EXPONENT_SIZE) << (userSupply_ & DEFAULT_EXPONENT_MASK);

        // maxExpansionLimit_ => withdrawal limit expandPercent (is in 1e2 decimals)
        uint256 maxExpansionLimit_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_EXPAND_PERCENT) & X14;
        maxExpansionLimit_ = userSupply_ - ((userSupply_ * maxExpansionLimit_) / FOUR_DECIMALS);

        if (newLimit_ == 0 || newLimit_ < maxExpansionLimit_) {
            // instant full expansion, and if that goes below base limit then fully down to 0.
            // if we were to set a limit that goes below max expansion limit, then after 1 deposit or 1 withdrawal it would
            // become based on the max expansion limit again (unless it goes below base limit), which can be confusing.
            // Also updating base limit here to avoid the change after 1 interaction might have undesired effects.
            // So limiting update to max. full expansion. If more is desired, this must be called again after some withdraws.
            newLimit_ = maxExpansionLimit_;
        } else if (newLimit_ == type(uint256).max || newLimit_ > userSupply_) {
            // current withdrawable 0 (sets current user supply as limit).
            newLimit_ = userSupply_;
        }
        // else => new limit is between > max expansion and < user supply.

        // set input limit as new current limit. instant withdrawable will be userSupply_ - newLimit_

        uint256 baseLimit_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_BASE_WITHDRAWAL_LIMIT) & X18;
        baseLimit_ = (baseLimit_ >> DEFAULT_EXPONENT_SIZE) << (baseLimit_ & DEFAULT_EXPONENT_MASK);
        if (userSupply_ < baseLimit_) {
            newLimit_ = 0;
            // Note if new limit goes below base limit, it follows default behavior: first there must be a withdrawal
            // that brings user supply below base limit, then the limit will be set to 0.
            // otherwise we would have the same problem as described above after 1 interaction.
        }

        // Update on storage
        _userSupplyData[user_] =
            // mask to update bits 65-161 (withdrawal limit, timestamp)
            (userSupplyData_ & 0xFFFFFFFFFFFFFFFFFFFFFFFC000000000000000000000001FFFFFFFFFFFFFFFF) |
            (newLimit_.toBigNumber(DEFAULT_COEFFICIENT_SIZE, DEFAULT_EXPONENT_SIZE, BigMathMinified.ROUND_DOWN) <<
                DexSlotsLink.BITS_USER_SUPPLY_PREVIOUS_WITHDRAWAL_LIMIT) | // converted to BigNumber can not overflow
            (block.timestamp << DexSlotsLink.BITS_USER_SUPPLY_LAST_UPDATE_TIMESTAMP);

        emit LogUpdateUserWithdrawalLimit(user_, newLimit_);
    }

    function updateUserBorrowConfigs(UserBorrowConfig[] memory userBorrowConfigs_) external _check _onlyDelegateCall {
        uint256 userBorrowData_;

        for (uint256 i; i < userBorrowConfigs_.length; ) {
            _checkIsContract(userBorrowConfigs_[i].user);
            if (
                // max debt ceiling must not be smaller than base debt ceiling. Also covers case where max = 0 but base > 0
                userBorrowConfigs_[i].baseDebtCeiling > userBorrowConfigs_[i].maxDebtCeiling ||
                // can not set expand duration to 0 as that could cause a division by 0 in LiquidityCalcs.
                // having expand duration as 0 is anyway not an expected config so removing the possibility for that.
                // if no expansion is wanted, simply set expandDuration to 1 and expandPercent to 0.
                userBorrowConfigs_[i].expandDuration == 0
            ) {
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
            }
            if (userBorrowConfigs_[i].expandPercent > X14) {
                // expandPercent is max 14 bits
                revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
            }
            if (userBorrowConfigs_[i].expandDuration > X24) {
                // duration is max 24 bits
                revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
            }
            if (userBorrowConfigs_[i].baseDebtCeiling == 0 || userBorrowConfigs_[i].maxDebtCeiling == 0) {
                // limits can not be 0. As a side effect, this ensures that there is no borrow config
                // where all values would be 0, so configured users can be differentiated in the mapping.
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
            }
            // @dev baseDebtCeiling & maxDebtCeiling have no max bits amount as they are in normal token amount
            // and then converted to BigNumber

            // get current user config data from storage
            userBorrowData_ = _userBorrowData[userBorrowConfigs_[i].user];

            // Updating user data on storage

            _userBorrowData[userBorrowConfigs_[i].user] =
                // mask to update first bit (mode) + bits 162-235 (debt limit values)
                (userBorrowData_ & 0xfffff0000000000000000003ffffffffffffffffffffffffffffffffffffffff) |
                (1) |
                (userBorrowConfigs_[i].expandPercent << DexSlotsLink.BITS_USER_BORROW_EXPAND_PERCENT) |
                (userBorrowConfigs_[i].expandDuration << DexSlotsLink.BITS_USER_BORROW_EXPAND_DURATION) |
                // convert base debt limit to BigNumber for storage (10 | 8). (borrow is always possible below this)
                (userBorrowConfigs_[i].baseDebtCeiling.toBigNumber(
                    SMALL_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                ) << DexSlotsLink.BITS_USER_BORROW_BASE_BORROW_LIMIT) |
                // convert max debt limit to BigNumber for storage (10 | 8). (no borrowing ever possible above this)
                (userBorrowConfigs_[i].maxDebtCeiling.toBigNumber(
                    SMALL_COEFFICIENT_SIZE,
                    DEFAULT_EXPONENT_SIZE,
                    BigMathMinified.ROUND_DOWN
                ) << DexSlotsLink.BITS_USER_BORROW_MAX_BORROW_LIMIT);

            unchecked {
                ++i;
            }
        }

        emit LogUpdateUserBorrowConfigs(userBorrowConfigs_);
    }

    function pauseUser(address user_, bool pauseSupply_, bool pauseBorrow_) public _onlyDelegateCall {
        _checkIsContract(user_);

        uint256 userData_;

        if (pauseSupply_) {
            // userData_ => userSupplyData_
            userData_ = _userSupplyData[user_];
            if (userData_ == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__UserNotDefined);
            }
            if (userData_ & 1 == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidPauseToggle);
            }
            // set first bit as 0, meaning all user's supply operations are paused
            _userSupplyData[user_] = userData_ & (~uint(1));
        }

        if (pauseBorrow_) {
            // userData_ => userBorrowData_
            userData_ = _userBorrowData[user_];
            if (userData_ == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__UserNotDefined);
            }
            if (userData_ & 1 == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidPauseToggle);
            }
            // set first bit as 0, meaning all user's borrow operations are paused
            _userBorrowData[user_] = userData_ & (~uint(1));
        }

        emit LogPauseUser(user_, pauseSupply_, pauseBorrow_);
    }

    function unpauseUser(address user_, bool unpauseSupply_, bool unpauseBorrow_) public _onlyDelegateCall {
        _checkIsContract(user_);

        uint256 userData_;

        if (unpauseSupply_) {
            // userData_ => userSupplyData_
            userData_ = _userSupplyData[user_];
            if (userData_ == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__UserNotDefined);
            }
            if (userData_ & 1 == 1) {
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidPauseToggle);
            }

            // set first bit as 1, meaning unpause
            _userSupplyData[user_] = userData_ | 1;
        }

        if (unpauseBorrow_) {
            // userData_ => userBorrowData_
            userData_ = _userBorrowData[user_];
            if (userData_ == 0) {
                revert FluidDexError(ErrorTypes.DexT1Admin__UserNotDefined);
            }
            if (userData_ & 1 == 1) {
                revert FluidDexError(ErrorTypes.DexT1Admin__InvalidPauseToggle);
            }

            // set first bit as 1, meaning unpause
            _userBorrowData[user_] = userData_ | 1;
        }

        emit LogUnpauseUser(user_, unpauseSupply_, unpauseBorrow_);
    }

    /// note we have not added updateUtilizationLimit in the params here because struct of InitializeVariables already has 16 variables
    /// we might skip adding it and let it update through the indepdent function to keep initialize struct simple
    function initialize(InitializeVariables memory i_) public payable _onlyDelegateCall {
        _checkIsContract(TEAM_MULTISIG);

        if (!(i_.smartCol || i_.smartDebt)) {
            // either 1 should be on upon pool initialization
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }
        // cut is an integer in storage slot which is more than enough
        // but from UI we are allowing to send in 4 decimals to maintain consistency & avoid human error in future
        if (i_.revenueCut != 0 && i_.revenueCut < FOUR_DECIMALS) {
            // human input error. should send 0 for wanting 0, not 0 because of precision reduction.
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }

        // revenue cut has no decimals
        i_.revenueCut = i_.revenueCut / FOUR_DECIMALS;
        i_.upperShiftThreshold = i_.upperShiftThreshold / THREE_DECIMALS;
        i_.lowerShiftThreshold = i_.lowerShiftThreshold / THREE_DECIMALS;

        if (
            (i_.fee > FIVE_DECIMALS) || // fee cannot be more than 10%
            (i_.revenueCut > TWO_DECIMALS) ||
            (i_.upperPercent > (SIX_DECIMALS - FOUR_DECIMALS)) || // capping range to 99%.
            (i_.lowerPercent > (SIX_DECIMALS - FOUR_DECIMALS)) || // capping range to 99%.
            (i_.upperPercent == 0) ||
            (i_.lowerPercent == 0) ||
            (i_.upperShiftThreshold > THREE_DECIMALS) ||
            (i_.lowerShiftThreshold > THREE_DECIMALS) ||
            ((i_.upperShiftThreshold == 0) && (i_.lowerShiftThreshold > 0)) ||
            ((i_.upperShiftThreshold > 0) && (i_.lowerShiftThreshold == 0)) ||
            (i_.thresholdShiftTime == 0) ||
            (i_.thresholdShiftTime > X24) ||
            (i_.centerPriceAddress > X30) ||
            (i_.hookAddress > X30) ||
            (i_.centerPrice <= i_.minCenterPrice) ||
            (i_.centerPrice >= i_.maxCenterPrice) ||
            (i_.minCenterPrice == 0)
        ) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }

        uint dexVariables2_;

        if (i_.smartCol) {
            _turnOnSmartCol(i_.token0ColAmt, i_.centerPrice);
            dexVariables2_ = dexVariables2_ | 1;
        }

        if (i_.smartDebt) {
            _turnOnSmartDebt(i_.token0DebtAmt, i_.centerPrice);
            dexVariables2_ = dexVariables2_ | 2;
        }

        i_.centerPrice = i_.centerPrice.toBigNumber(32, 8, BigMathMinified.ROUND_DOWN);
        // setting up initial dexVariables
        dexVariables =
            (i_.centerPrice << 1) |
            (i_.centerPrice << 41) |
            (i_.centerPrice << 81) |
            (block.timestamp << 121) |
            (60 << 154) | // just setting 60 seconds, no particular reason for it why "60"
            (7 << 176);

        dexVariables2 =
            dexVariables2_ |
            (i_.fee << 2) |
            (i_.revenueCut << 19) |
            (i_.upperPercent << 27) |
            (i_.lowerPercent << 47) |
            (i_.upperShiftThreshold << 68) |
            (i_.lowerShiftThreshold << 78) |
            (i_.thresholdShiftTime << 88) |
            (i_.centerPriceAddress << 112) |
            (i_.hookAddress << 142) |
            (i_.maxCenterPrice.toBigNumber(20, 8, BigMathMinified.ROUND_UP) << 172) |
            (i_.minCenterPrice.toBigNumber(20, 8, BigMathMinified.ROUND_DOWN) << 200) |
            (THREE_DECIMALS << 228) | // setting initial token0 max utilization to 100%
            (THREE_DECIMALS << 238); // setting initial token1 max utilization to 100%

        emit LogInitializePoolConfig(
            i_.smartCol,
            i_.smartDebt,
            i_.token0ColAmt,
            i_.token0DebtAmt,
            i_.fee,
            i_.revenueCut * FOUR_DECIMALS,
            i_.centerPriceAddress,
            i_.hookAddress
        );

        emit LogInitializePriceParams(
            i_.upperPercent,
            i_.lowerPercent,
            i_.upperShiftThreshold * THREE_DECIMALS,
            i_.lowerShiftThreshold * THREE_DECIMALS,
            i_.thresholdShiftTime,
            i_.maxCenterPrice,
            i_.minCenterPrice
        );
    }

    function pauseSwapAndArbitrage() public _onlyDelegateCall {
        uint dexVariables2_ = dexVariables2;
        if ((dexVariables2_ >> 255) == 1) {
            // already paused
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }
        dexVariables2 = dexVariables2_ | (uint(1) << 255);

        emit LogPauseSwapAndArbitrage();
    }

    function unpauseSwapAndArbitrage() public _onlyDelegateCall {
        uint dexVariables2_ = dexVariables2;
        if ((dexVariables2_ >> 255) == 0) {
            // already unpaused
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }
        dexVariables2 = (dexVariables2_ << 1) >> 1;

        emit LogUnpauseSwapAndArbitrage();
    }

    /// @notice sends any potentially stuck funds to Liquidity contract.
    /// @dev this contract never holds any funds as all operations send / receive funds from user <-> Liquidity.
    function rescueFunds(address token_) external _onlyDelegateCall {
        address liquidity_ = IFluidDexT1(address(this)).constantsView().liquidity;
        if (token_ == NATIVE_TOKEN) {
            SafeTransfer.safeTransferNative(liquidity_, address(this).balance);
        } else {
            SafeTransfer.safeTransfer(token_, liquidity_, IERC20(token_).balanceOf(address(this)));
        }

        emit LogRescueFunds(token_);
    }

    function updateMaxSupplyShares(uint maxSupplyShares_) external _onlyDelegateCall {
        uint totalSupplyShares_ = _totalSupplyShares;

        // totalSupplyShares_ can only be 0 when smart col pool is not initialized
        if ((maxSupplyShares_ > X128) || (totalSupplyShares_ == 0)) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }
        _totalSupplyShares = (totalSupplyShares_ & X128) | (maxSupplyShares_ << 128);

        emit LogUpdateMaxSupplyShares(maxSupplyShares_);
    }

    function updateMaxBorrowShares(uint maxBorrowShares_) external _onlyDelegateCall {
        uint totalBorrowShares_ = _totalBorrowShares;

        // totalBorrowShares_ can only be 0 when smart debt pool is not initialized
        if ((maxBorrowShares_ > X128) || (totalBorrowShares_ == 0)) {
            revert FluidDexError(ErrorTypes.DexT1Admin__ConfigOverflow);
        }
        _totalBorrowShares = (totalBorrowShares_ & X128) | (maxBorrowShares_ << 128);

        emit LogUpdateMaxBorrowShares(maxBorrowShares_);
    }

    /// @notice Toggles the oracle activation
    /// @param turnOn_ Whether to turn on or off the oracle
    function toggleOracleActivation(bool turnOn_) external _onlyDelegateCall {
        uint dexVariables_ = dexVariables;
        if ((((dexVariables_ >> 195) & 1 == 1) && turnOn_) || (((dexVariables_ >> 195) & 1 == 0) && !turnOn_)) {
            // already active
            revert FluidDexError(ErrorTypes.DexT1Admin__InvalidParams);
        }
        if (turnOn_) {
            dexVariables = dexVariables_ | (uint(1) << 195);
        } else {
            dexVariables = dexVariables_ & (~(uint(1) << 195));
        }

        emit LogToggleOracleActivation(turnOn_);
    }
}
","keccak256":"0xd1cd0627f3af278d8c0dcaed8e2be74738431b019291444e2c669fcd8e136b25","license":"BUSL-1.1"},"contracts/protocols/dex/poolT1/adminModule/structs.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

abstract contract Structs {
    struct InitializeVariables {
        bool smartCol;
        uint token0ColAmt;
        bool smartDebt;
        uint token0DebtAmt;
        uint centerPrice;
        uint fee;
        uint revenueCut;
        uint upperPercent;
        uint lowerPercent;
        uint upperShiftThreshold;
        uint lowerShiftThreshold;
        uint thresholdShiftTime;
        uint centerPriceAddress;
        uint hookAddress;
        uint maxCenterPrice;
        uint minCenterPrice;
    }

    /// @notice struct to set user supply & withdrawal config
    struct UserSupplyConfig {
        ///
        /// @param user address
        address user;
        ///
        /// @param expandPercent withdrawal limit expand percent. in 1e2: 100% = 10_000; 1% = 100
        /// Also used to calculate rate at which withdrawal limit should decrease (instant).
        uint256 expandPercent;
        ///
        /// @param expandDuration withdrawal limit expand duration in seconds.
        /// used to calculate rate together with expandPercent
        uint256 expandDuration;
        ///
        /// @param baseWithdrawalLimit base limit, below this, user can withdraw the entire amount.
        /// amount in raw (to be multiplied with exchange price) or normal depends on configured mode in user config for the token:
        /// with interest -> raw, without interest -> normal
        uint256 baseWithdrawalLimit;
    }

    /// @notice struct to set user borrow & payback config
    struct UserBorrowConfig {
        ///
        /// @param user address
        address user;
        ///
        /// @param expandPercent debt limit expand percent. in 1e2: 100% = 10_000; 1% = 100
        /// Also used to calculate rate at which debt limit should decrease (instant).
        uint256 expandPercent;
        ///
        /// @param expandDuration debt limit expand duration in seconds.
        /// used to calculate rate together with expandPercent
        uint256 expandDuration;
        ///
        /// @param baseDebtCeiling base borrow limit. until here, borrow limit remains as baseDebtCeiling
        /// (user can borrow until this point at once without stepped expansion). Above this, automated limit comes in place.
        /// amount in raw (to be multiplied with exchange price) or normal depends on configured mode in user config for the token:
        /// with interest -> raw, without interest -> normal
        uint256 baseDebtCeiling;
        ///
        /// @param maxDebtCeiling max borrow ceiling, maximum amount the user can borrow.
        /// amount in raw (to be multiplied with exchange price) or normal depends on configured mode in user config for the token:
        /// with interest -> raw, without interest -> normal
        uint256 maxDebtCeiling;
    }
}
