// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { StorageRead } from "../../../../libraries/storageRead.sol";

interface ITokenDecimals {
    function decimals() external view returns (uint8);
}

abstract contract ConstantVariables is StorageRead {
    /*//////////////////////////////////////////////////////////////
                          CONSTANTS / IMMUTABLES
    //////////////////////////////////////////////////////////////*/

    address internal constant TEAM_MULTISIG = 0x4F6F977aCDD1177DCD81aB83074855EcB9C2D49e;

    address internal constant NATIVE_TOKEN = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    uint256 internal constant NATIVE_TOKEN_DECIMALS = 18;
    address internal constant ADDRESS_DEAD = 0x000000000000000000000000000000000000dEaD;
    uint256 internal constant TOKENS_DECIMALS_PRECISION = 12;
    uint256 internal constant TOKENS_DECIMALS = 1e12;

    uint256 internal constant SMALL_COEFFICIENT_SIZE = 10;
    uint256 internal constant DEFAULT_COEFFICIENT_SIZE = 56;
    uint256 internal constant DEFAULT_EXPONENT_SIZE = 8;
    uint256 internal constant DEFAULT_EXPONENT_MASK = 0xFF;

    uint256 internal constant X2 = 0x3;
    uint256 internal constant X3 = 0x7;
    uint256 internal constant X5 = 0x1f;
    uint256 internal constant X7 = 0x7f;
    uint256 internal constant X8 = 0xff;
    uint256 internal constant X9 = 0x1ff;
    uint256 internal constant X10 = 0x3ff;
    uint256 internal constant X11 = 0x7ff;
    uint256 internal constant X14 = 0x3fff;
    uint256 internal constant X16 = 0xffff;
    uint256 internal constant X17 = 0x1ffff;
    uint256 internal constant X18 = 0x3ffff;
    uint256 internal constant X20 = 0xfffff;
    uint256 internal constant X22 = 0x3fffff;
    uint256 internal constant X23 = 0x7fffff;
    uint256 internal constant X24 = 0xffffff;
    uint256 internal constant X28 = 0xfffffff;
    uint256 internal constant X30 = 0x3fffffff;
    uint256 internal constant X32 = 0xffffffff;
    uint256 internal constant X33 = 0x1ffffffff;
    uint256 internal constant X40 = 0xffffffffff;
    uint256 internal constant X64 = 0xffffffffffffffff;
    uint256 internal constant X96 = 0xffffffffffffffffffffffff;
    uint256 internal constant X128 = 0xffffffffffffffffffffffffffffffff;

    uint256 internal constant TWO_DECIMALS = 1e2;
    uint256 internal constant THREE_DECIMALS = 1e3;
    uint256 internal constant FOUR_DECIMALS = 1e4;
    uint256 internal constant FIVE_DECIMALS = 1e5;
    uint256 internal constant SIX_DECIMALS = 1e6;
    uint256 internal constant EIGHT_DECIMALS = 1e8;
    uint256 internal constant NINE_DECIMALS = 1e9;

    uint256 internal constant PRICE_PRECISION = 1e27;

    uint256 internal constant ORACLE_PRECISION = 1e18; // 100%
    uint256 internal constant ORACLE_LIMIT = 5 * 1e16; // 5%

    /// after swap token0 reserves should not be less than token1InToken0 / MINIMUM_LIQUIDITY_SWAP
    /// after swap token1 reserves should not be less than token0InToken1 / MINIMUM_LIQUIDITY_SWAP
    uint256 internal constant MINIMUM_LIQUIDITY_SWAP = 1e4;

    /// after user operations (deposit, withdraw, borrow, payback) token0 reserves should not be less than token1InToken0 / MINIMUM_LIQUIDITY_USER_OPERATIONS
    /// after user operations (deposit, withdraw, borrow, payback) token1 reserves should not be less than token0InToken0 / MINIMUM_LIQUIDITY_USER_OPERATIONS
    uint256 internal constant MINIMUM_LIQUIDITY_USER_OPERATIONS = 1e6;

    /// To skip transfers in liquidity layer if token in & out is same and liquidity layer is on the winning side
    bytes32 internal constant SKIP_TRANSFERS = keccak256(bytes("SKIP_TRANSFERS"));

    function _decimals(address token_) internal view returns (uint256) {
        return (token_ == NATIVE_TOKEN) ? NATIVE_TOKEN_DECIMALS : ITokenDecimals(token_).decimals();
    }
}

contracts/protocols/dex/poolT1/common/va.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

abstract contract Variables {
    /*//////////////////////////////////////////////////////////////
                          STORAGE VARIABLES
    //////////////////////////////////////////////////////////////*/

    /// First 1 bit  => 0 => re-entrancy. If 0 then allow transaction to go, else throw.
    /// Next 40 bits => 1-40 => last to last stored price. BigNumber (32 bits precision, 8 bits exponent)
    /// Next 40 bits => 41-80 => last stored price of pool. BigNumber (32 bits precision, 8 bits exponent)
    /// Next 40 bits => 81-120 => center price. Center price from where the ranges will be calculated. BigNumber (32 bits precision, 8 bits exponent)
    /// Next 33 bits => 121-153 => last interaction time stamp
    /// Next 22 bits => 154-175 => max 4194303 seconds (~1165 hrs, ~48.5 days), time difference between last to last and last price stored
    /// Next 3 bits  => 176-178 => oracle checkpoint, if 0 then first slot, if 7 then last slot
    /// Next 16 bits => 179-194 => current mapping or oracle, after every 8 transaction it will increase by 1. Max capacity is 65535 but it can be lower than that check dexVariables2
    /// Next 1 bit  => 195 => is oracle active?
    uint internal dexVariables;

    /// Next  1 bit  => 0 => is smart collateral enabled?
    /// Next  1 bit  => 1 => is smart debt enabled?
    /// Next 17 bits => 2-18 => fee (1% = 10000, max value: 100000 = 10%, fee should not be more than 10%)
    /// Next  7 bits => 19-25 => revenue cut from fee (1 = 1%, 100 = 100%). If fee is 1000 = 0.1% and revenue cut is 10 = 10% then governance get 0.01% of every swap
    /// Next  1 bit  => 26 => percent active change going on or not, 0 = false, 1 = true, if true than that means governance has updated the below percents and the update should happen with a specified time.
    /// Next 20 bits => 27-46 => upperPercent (1% = 10000, max value: 104.8575%) upperRange - upperRange * upperPercent = centerPrice. Hence, upperRange = centerPrice / (1 - upperPercent)
    /// Next 20 bits => 47-66 => lowerPercent. lowerRange = centerPrice - centerPrice * lowerPercent.
    /// Next  1 bit  => 67 => threshold percent active change going on or not, 0 = false, 1 = true, if true than that means governance has updated the below percents and the update should happen with a specified time.
    /// Next 10 bits => 68-77 => upper shift threshold percent, 1 = 0.1%. 1000 = 100%. if currentPrice > (centerPrice + (upperRange - centerPrice) * (1000 - upperShiftThresholdPercent) / 1000) then trigger shift
    /// Next 10 bits => 78-87 => lower shift threshold percent, 1 = 0.1%. 1000 = 100%. if currentPrice < (centerPrice - (centerPrice - lowerRange) * (1000 - lowerShiftThresholdPercent) / 1000) then trigger shift
    /// Next 24 bits => 88-111 => Shifting time (~194 days) (rate = (% up + % down) / time ?)
    /// Next 30 bits => 112-131 => Address of center price if center price should be fetched externally, for example, for wstETH <> ETH pool, fetch wstETH exchange rate into stETH from wstETH contract.
    /// Why fetch it externally? Because let's say pool width is 0.1% and wstETH temporarily got depeg of 0.5% then pool will start to shift to newer pricing
    /// but we don't want pool to shift to 0.5% because we know the depeg will recover so to avoid the loss for users.
    /// Next 30 bits => 142-171 => Hooks bits, calculate hook address by storing deployment nonce from factory.
    /// Next 28 bits => 172-199 => max center price. BigNumber (20 bits precision, 8 bits exponent)
    /// Next 28 bits => 200-227 => min center price. BigNumber (20 bits precision, 8 bits exponent)
    /// Next 10 bits => 228-237 => utilization limit of token0. Max value 1000 = 100%, if 100% then no need to check the utilization.
    /// Next 10 bits => 238-247 => utilization limit of token1. Max value 1000 = 100%, if 100% then no need to check the utilization.
    /// Next 1  bit  => 248     => is center price shift active
    /// Last 1  bit  => 255     => Pause swap & arbitrage (only perfect functions will be usable), if we need to pause entire DEX then that can be done through pausing DEX on Liquidity Layer
    uint internal dexVariables2;

    /// first 128 bits => 0-127 => total supply shares
    /// last 128 bits => 128-255 => max supply shares
    uint internal _totalSupplyShares;

    /// @dev user supply data: user -> data
    /// Aside from 1st bit, entire bits here are same as liquidity layer _userSupplyData. Hence exact same supply & borrow limit library can be used
    /// First  1 bit  =>       0 => is user allowed to supply? 0 = not allowed, 1 = allowed
    /// Next  64 bits =>   1- 64 => user supply amount/shares; BigMath: 56 | 8
    /// Next  64 bits =>  65-128 => previous user withdrawal limit; BigMath: 56 | 8
    /// Next  33 bits => 129-161 => last triggered process timestamp (enough until 16 March 2242 -> max value 8589934591)
    /// Next  14 bits => 162-175 => expand withdrawal limit percentage (in 1e2: 100% = 10_000; 1% = 100 -> max value 16_383).
    ///                             @dev shrinking is instant
    /// Next  24 bits => 176-199 => withdrawal limit expand duration in seconds.(Max value 16_777_215; ~4_660 hours, ~194 days)
    /// Next  18 bits => 200-217 => base withdrawal limit: below this, 100% withdrawals can be done (aka shares can be burned); BigMath: 10 | 8
    /// Next  38 bits => 218-255 => empty for future use
    mapping(address => uint) internal _userSupplyData;

    /// first 128 bits => 0-127 => total borrow shares
    /// last 128 bits => 128-255 => max borrow shares
    uint internal _totalBorrowShares;

    /// @dev user borrow data: user -> data
    /// Aside from 1st bit, entire bits here are same as liquidity layer _userBorrowData. Hence exact same supply & borrow limit library function can be used
    /// First  1 bit  =>       0 => is user allowed to borrow? 0 = not allowed, 1 = allowed
    /// Next  64 bits =>   1- 64 => user debt amount/shares; BigMath: 56 | 8
    /// Next  64 bits =>  65-128 => previous user debt ceiling; BigMath: 56 | 8
    /// Next  33 bits => 129-161 => last triggered process timestamp (enough until 16 March 2242 -> max value 8589934591)
    /// Next  14 bits => 162-175 => expand debt ceiling percentage (in 1e2: 100% = 10_000; 1% = 100 -> max value 16_383)
    ///                             @dev shrinking is instant
    /// Next  24 bits => 176-199 => debt ceiling expand duration in seconds (Max value 16_777_215; ~4_660 hours, ~194 days)
    /// Next  18 bits => 200-217 => base debt ceiling: below this, there's no debt ceiling limits; BigMath: 10 | 8
    /// Next  18 bits => 218-235 => max debt ceiling: absolute maximum debt ceiling can expand to; BigMath: 10 | 8
    /// Next  20 bits => 236-255 => empty for future use
    mapping(address => uint) internal _userBorrowData;

    /// Price difference between last swap of last block & last swap of new block
    /// If last swap happened at Block B - 4 and next swap happened after 4 blocks at Block B then it will store that difference
    /// considering time difference between these 4 blocks is 48 seconds, hence time will be stored as 48
    /// New oracle update:
    /// time to 9 bits and precision to 22 bits
    /// if time exceeds 9 bits which is 511 sec or ~8.5 min then we will use 2 oracle slot to store the data
    /// we will leave the both time slot as 0 and on first sign + precision slot we will store time and
    /// on second sign + precision slot we will store sign & precision
    /// First 9 bits =>   0-  8 => time, 511 seconds
    /// Next   1 bit  =>  9     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits =>  10- 31 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits =>  32- 40 => time, 511 seconds
    /// Next   1 bit  =>  41     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits =>  42- 63 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits =>  64- 72 => time, 511 seconds
    /// Next   1 bit  =>  73     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits =>  74- 95 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits =>  96-104 => time, 511 seconds
    /// Next   1 bit  => 105     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits => 106-127 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits => 128-136 => time, 511 seconds
    /// Next   1 bit  => 137     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits => 138-159 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits => 160-168 => time, 511 seconds
    /// Next   1 bit  => 169     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits => 170-191 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits => 192-200 => time, 511 seconds
    /// Next   1 bit  => 201     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits => 202-223 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    /// Next  9 bits => 224-232 => time, 511 seconds
    /// Next   1 bit  => 233     => sign of percent in change, if 1 then 0 or positive, else negative
    /// Next  22 bits => 234-255 => 4194303, change in price, max change is capped to 5%, so 4194303 = 5%, 1 = 0.0000011920931797249746%
    mapping(uint => uint) internal _oracle;

    /// First 20 bits =>  0-19 => old upper shift
    /// Next  20 bits => 20-39 => old lower shift
    /// Next  20 bits => 40-59 => in seconds, ~12 days max, shift can last for max ~12 days
    /// Next  33 bits => 60-92 => timestamp of when the shift has started.
    uint128 internal _rangeShift;

    /// First 10 bits =>  0- 9 => old upper shift
    /// Next  10 bits => 10-19 => empty so we can use same helper function
    /// Next  10 bits => 20-29 => old lower shift
    /// Next  10 bits => 30-39 => empty so we can use same helper function
    /// Next  20 bits => 40-59 => in seconds, ~12 days max, shift can last for max ~12 days
    /// Next  33 bits => 60-92 => timestamp of when the shift has started.
    /// Next  24 bits => 93-116 => old threshold time
    uint128 internal _thresholdShift;

    /// Shifting is fuzzy and with time it'll keep on getting closer and then eventually get over
    /// First 33 bits => 0 -32 => starting timestamp
    /// Next  20 bits => 33-52 => % shift
    /// Next  20 bits => 53-72 => time to shift that percent
    uint256 internal _centerPriceShift;
}

contracts/protocols/dex/poolT1/coreModul.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { SecondaryHelpers } from "../helpers/secondaryHelpers.sol";
import { SafeTransfer } from "../../../../../libraries/safeTransfer.sol";
import { DexSlotsLink } from "../../../../../libraries/dexSlotsLink.sol";
import { DexCalcs } from "../../../../../libraries/dexCalcs.sol";
import { BigMathMinified } from "../../../../../libraries/bigMathMinified.sol";
import { ErrorTypes } from "../../../errorTypes.sol";

/// @title FluidDexT1
/// @notice Implements core logics for Fluid Dex protocol.
/// Note Token transfers happen directly from user to Liquidity contract and vice-versa.
contract FluidDexT1OperationsCol is SecondaryHelpers {
    using BigMathMinified for uint256;

    constructor(ConstantViews memory constantViews_) SecondaryHelpers(constantViews_) {
        // all implementations should be zero other than shift
        if (
            constantViews_.implementations.shift == address(0) ||
            constantViews_.implementations.admin != address(0) ||
            constantViews_.implementations.colOperations != address(0) ||
            constantViews_.implementations.debtOperations != address(0) ||
            constantViews_.implementations.perfectOperationsAndSwapOut != address(0)
        ) {
            revert FluidDexError(ErrorTypes.DexT1__InvalidImplementation);
        }
    }

    modifier _onlyDelegateCall() {
        // also indirectly checked by `_check` because pool can never be initialized as long as the initialize method
        // is delegate call only, but just to be sure on Admin logic we add the modifier everywhere nonetheless.
        if (address(this) == THIS_CONTRACT) {
            revert FluidDexError(ErrorTypes.DexT1__OnlyDelegateCallAllowed);
        }
        _;
    }

    /// @dev This function allows users to deposit tokens in any proportion into the col pool
    /// @param token0Amt_ The amount of token0 to deposit
    /// @param token1Amt_ The amount of token1 to deposit
    /// @param minSharesAmt_ The minimum amount of shares the user expects to receive
    /// @param estimate_ If true, function will revert with estimated shares without executing the deposit
    /// @return shares_ The amount of shares minted for the deposit
    function deposit(
        uint token0Amt_,
        uint token1Amt_,
        uint minSharesAmt_,
        bool estimate_
    ) public payable _onlyDelegateCall returns (uint shares_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userSupplyData_ = _userSupplyData[msg.sender];

        if (userSupplyData_ & 1 == 0 && !estimate_) revert FluidDexError(ErrorTypes.DexT1__UserSupplyInNotOn);

        // is smart collateral pool enabled
        if ((dexVariables2_ & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            DepositColMemory memory d_;

            CollateralReserves memory c_ = _getCollateralReserves(
                pex_.geometricMean,
                pex_.upperRange,
                pex_.lowerRange,
                pex_.supplyToken0ExchangePrice,
                pex_.supplyToken1ExchangePrice
            );
            CollateralReserves memory c2_ = c_;

            if (token0Amt_ > 0) {
                d_.token0AmtAdjusted =
                    (((token0Amt_ - 1) * TOKEN_0_NUMERATOR_PRECISION) / TOKEN_0_DENOMINATOR_PRECISION) -
                    1;
                _verifySwapAndNonPerfectActions(d_.token0AmtAdjusted, token0Amt_);
                _verifyMint(d_.token0AmtAdjusted, c_.token0RealReserves);
            }

            if (token1Amt_ > 0) {
                d_.token1AmtAdjusted =
                    (((token1Amt_ - 1) * TOKEN_1_NUMERATOR_PRECISION) / TOKEN_1_DENOMINATOR_PRECISION) -
                    1;
                _verifySwapAndNonPerfectActions(d_.token1AmtAdjusted, token1Amt_);
                _verifyMint(d_.token1AmtAdjusted, c_.token1RealReserves);
            }

            uint temp_;
            uint temp2_;

            uint totalSupplyShares_ = _totalSupplyShares & X128;
            if ((c_.token0RealReserves > 0) && (c_.token1RealReserves > 0)) {
                if (d_.token0AmtAdjusted > 0 && d_.token1AmtAdjusted > 0) {
                    // mint shares in equal proportion
                    // temp_ => expected shares from token0 deposit
                    temp_ = (d_.token0AmtAdjusted * 1e18) / c_.token0RealReserves;
                    // temp2_ => expected shares from token1 deposit
                    temp2_ = (d_.token1AmtAdjusted * 1e18) / c_.token1RealReserves;
                    if (temp_ > temp2_) {
                        // use temp2_ shares
                        shares_ = (temp2_ * totalSupplyShares_) / 1e18;
                        // temp_ => token0 to swap
                        temp_ = ((temp_ - temp2_) * c_.token0RealReserves) / 1e18;
                        temp2_ = 0;
                    } else if (temp2_ > temp_) {
                        // use temp_ shares
                        shares_ = (temp_ * totalSupplyShares_) / 1e18;
                        // temp2_ => token1 to swap
                        temp2_ = ((temp2_ - temp_) * c_.token1RealReserves) / 1e18;
                        temp_ = 0;
                    } else {
                        // if equal then revert as swap will not be needed anymore which can create some issue, better to use depositPerfect in this case
                        revert FluidDexError(ErrorTypes.DexT1__InvalidDepositAmts);
                    }

                    // User deposited in equal proportion here. Hence updating col reserves and the swap will happen on updated col reserves
                    c2_ = _getUpdatedColReserves(shares_, totalSupplyShares_, c_, true);

                    totalSupplyShares_ += shares_;
                } else if (d_.token0AmtAdjusted > 0) {
                    temp_ = d_.token0AmtAdjusted;
                    temp2_ = 0;
                } else if (d_.token1AmtAdjusted > 0) {
                    temp_ = 0;
                    temp2_ = d_.token1AmtAdjusted;
                } else {
                    // user sent both amounts as 0
                    revert FluidDexError(ErrorTypes.DexT1__InvalidDepositAmts);
                }

                if (temp_ > 0) {
                    // swap token0
                    temp_ = _getSwapAndDeposit(
                        temp_, // token0 to divide and swap
                        c2_.token1ImaginaryReserves, // token1 imaginary reserves
                        c2_.token0ImaginaryReserves, // token0 imaginary reserves
                        c2_.token0RealReserves, // token0 real reserves
                        c2_.token1RealReserves // token1 real reserves
                    );
                } else if (temp2_ > 0) {
                    // swap token1
                    temp_ = _getSwapAndDeposit(
                        temp2_, // token1 to divide and swap
                        c2_.token0ImaginaryReserves, // token0 imaginary reserves
                        c2_.token1ImaginaryReserves, // token1 imaginary reserves
                        c2_.token1RealReserves, // token1 real reserves
                        c2_.token0RealReserves // token0 real reserves
                    );
                } else {
                    // maybe possible to happen due to some precision issue that both are 0
                    revert FluidDexError(ErrorTypes.DexT1__DepositAmtsZero);
                }

                // new shares minted from swap & deposit
                temp_ = (temp_ * totalSupplyShares_) / 1e18;
                // adding fee in case of swap & deposit
                // 1 - fee. If fee is 1% then without fee will be 1e6 - 1e4
                // temp_ => withdraw fee
                temp_ = (temp_ * (SIX_DECIMALS - ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;
                // final new shares to mint for user
                shares_ += temp_;
                // final new collateral shares
                totalSupplyShares_ += temp_;
            } else {
                revert FluidDexError(ErrorTypes.DexT1__InvalidCollateralReserves);
            }

            if (estimate_) revert FluidDexLiquidityOutput(shares_);

            if (shares_ < minSharesAmt_) revert FluidDexError(ErrorTypes.DexT1__SharesMintedLess);

            if (token0Amt_ > 0) {
                _verifyToken1Reserves(
                    (c_.token0RealReserves + d_.token0AmtAdjusted),
                    (c_.token1RealReserves + d_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                temp_ = token0Amt_;
                _depositOrPaybackInLiquidity(TOKEN_0, temp_, 0);
            }

            if (token1Amt_ > 0) {
                _verifyToken0Reserves(
                    (c_.token0RealReserves + d_.token0AmtAdjusted),
                    (c_.token1RealReserves + d_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                temp_ = token1Amt_;
                _depositOrPaybackInLiquidity(TOKEN_1, temp_, 0);
            }

            // userSupply_ => temp_
            temp_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_AMOUNT) & X64;
            // extracting exisiting shares and then adding new shares in it
            temp_ = ((temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK));

            // calculate current, updated (expanded etc.) withdrawal limit
            // newWithdrawalLimit_ => temp2_
            temp2_ = DexCalcs.calcWithdrawalLimitBeforeOperate(userSupplyData_, temp_);

            temp_ += shares_;

            _updatingUserSupplyDataOnStorage(userSupplyData_, temp_, temp2_);

            // updating total col shares in storage
            _updateSupplyShares(totalSupplyShares_);

            emit LogDepositColLiquidity(token0Amt_, token1Amt_, shares_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartColNotEnabled);
        }
    }

    /// @dev This function allows users to withdraw tokens in any proportion from the col pool
    /// @param token0Amt_ The amount of token0 to withdraw
    /// @param token1Amt_ The amount of token1 to withdraw
    /// @param maxSharesAmt_ The maximum number of shares the user is willing to burn
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with shares_
    /// @return shares_ The number of shares burned for the withdrawal
    function withdraw(
        uint token0Amt_,
        uint token1Amt_,
        uint maxSharesAmt_,
        address to_
    ) public _onlyDelegateCall returns (uint shares_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userSupplyData_ = _userSupplyData[msg.sender];

        if (userSupplyData_ & 1 == 0 && to_ != ADDRESS_DEAD) revert FluidDexError(ErrorTypes.DexT1__UserSupplyInNotOn);

        WithdrawColMemory memory w_;

        w_.to = (to_ == address(0)) ? msg.sender : to_;

        // is smart collateral pool enabled
        if ((dexVariables2_ & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            uint token0Reserves_ = _getLiquidityCollateral(SUPPLY_TOKEN_0_SLOT, pex_.supplyToken0ExchangePrice, true);
            uint token1Reserves_ = _getLiquidityCollateral(SUPPLY_TOKEN_1_SLOT, pex_.supplyToken1ExchangePrice, false);
            w_.token0ReservesInitial = token0Reserves_;
            w_.token1ReservesInitial = token1Reserves_;

            if (token0Amt_ > 0) {
                w_.token0AmtAdjusted =
                    (((token0Amt_ + 1) * TOKEN_0_NUMERATOR_PRECISION) / TOKEN_0_DENOMINATOR_PRECISION) +
                    1;
                _verifySwapAndNonPerfectActions(w_.token0AmtAdjusted, token0Amt_);
                _verifyRedeem(w_.token0AmtAdjusted, token0Reserves_);
            }

            if (token1Amt_ > 0) {
                w_.token1AmtAdjusted =
                    (((token1Amt_ + 1) * TOKEN_1_NUMERATOR_PRECISION) / TOKEN_1_DENOMINATOR_PRECISION) +
                    1;
                _verifySwapAndNonPerfectActions(w_.token1AmtAdjusted, token1Amt_);
                _verifyRedeem(w_.token1AmtAdjusted, token1Reserves_);
            }

            uint temp_;
            uint temp2_;

            uint totalSupplyShares_ = _totalSupplyShares & X128;
            if ((token0Reserves_ > 0) && (token1Reserves_ > 0)) {
                if (w_.token0AmtAdjusted > 0 && w_.token1AmtAdjusted > 0) {
                    // mint shares in equal proportion
                    // temp_ => expected shares from token0 withdraw
                    temp_ = (w_.token0AmtAdjusted * 1e18) / token0Reserves_;
                    // temp2_ => expected shares from token1 withdraw
                    temp2_ = (w_.token1AmtAdjusted * 1e18) / token1Reserves_;
                    if (temp_ > temp2_) {
                        // use temp2_ shares
                        shares_ = ((temp2_ * totalSupplyShares_) / 1e18);
                        // temp_ => token0 to swap
                        temp_ = ((temp_ - temp2_) * token0Reserves_) / 1e18;
                        temp2_ = 0;
                    } else if (temp2_ > temp_) {
                        // use temp1_ shares
                        shares_ = ((temp_ * totalSupplyShares_) / 1e18);
                        // temp2_ => token1 to swap
                        temp2_ = ((temp2_ - temp_) * token1Reserves_) / 1e18;
                        temp_ = 0;
                    } else {
                        // if equal then revert as swap will not be needed anymore which can create some issue, better to use withdraw in perfect proportion for this
                        revert FluidDexError(ErrorTypes.DexT1__InvalidWithdrawAmts);
                    }

                    // User withdrew in equal proportion here. Hence updating col reserves and the swap will happen on updated col reserves
                    token0Reserves_ = token0Reserves_ - ((token0Reserves_ * shares_) / totalSupplyShares_);
                    token1Reserves_ = token1Reserves_ - ((token1Reserves_ * shares_) / totalSupplyShares_);
                    totalSupplyShares_ -= shares_;
                } else if (w_.token0AmtAdjusted > 0) {
                    temp_ = w_.token0AmtAdjusted;
                    temp2_ = 0;
                } else if (w_.token1AmtAdjusted > 0) {
                    temp_ = 0;
                    temp2_ = w_.token1AmtAdjusted;
                } else {
                    // user sent both amounts as 0
                    revert FluidDexError(ErrorTypes.DexT1__WithdrawAmtsZero);
                }

                uint token0ImaginaryReservesOutsideRangpex_;
                uint token1ImaginaryReservesOutsideRangpex_;

                if (pex_.geometricMean < 1e27) {
                    (
                        token0ImaginaryReservesOutsideRangpex_,
                        token1ImaginaryReservesOutsideRangpex_
                    ) = _calculateReservesOutsideRange(
                        pex_.geometricMean,
                        pex_.upperRange,
                        (token0Reserves_ - temp_),
                        (token1Reserves_ - temp2_)
                    );
                } else {
                    // inversing, something like `xy = k` so for calculation we are making everything related to x into y & y into x
                    // 1 / geometricMean for new geometricMean
                    // 1 / lowerRange will become upper range
                    // 1 / upperRange will become lower range
                    (
                        token1ImaginaryReservesOutsideRangpex_,
                        token0ImaginaryReservesOutsideRangpex_
                    ) = _calculateReservesOutsideRange(
                        (1e54 / pex_.geometricMean),
                        (1e54 / pex_.lowerRange),
                        (token1Reserves_ - temp2_),
                        (token0Reserves_ - temp_)
                    );
                }

                if (temp_ > 0) {
                    // swap into token0
                    temp_ = _getWithdrawAndSwap(
                        token0Reserves_, // token0 real reserves
                        token1Reserves_, // token1 real reserves
                        token0ImaginaryReservesOutsideRangpex_, // token0 imaginary reserves
                        token1ImaginaryReservesOutsideRangpex_, // token1 imaginary reserves
                        temp_ // token0 to divide and swap into
                    );
                } else if (temp2_ > 0) {
                    // swap into token1
                    temp_ = _getWithdrawAndSwap(
                        token1Reserves_, // token1 real reserves
                        token0Reserves_, // token0 real reserves
                        token1ImaginaryReservesOutsideRangpex_, // token1 imaginary reserves
                        token0ImaginaryReservesOutsideRangpex_, // token0 imaginary reserves
                        temp2_ // token0 to divide and swap into
                    );
                } else {
                    // maybe possible to happen due to some precision issue that both are 0
                    revert FluidDexError(ErrorTypes.DexT1__WithdrawAmtsZero);
                }

                // shares to burn from withdraw & swap
                temp_ = ((temp_ * totalSupplyShares_) / 1e18);
                // adding fee in case of withdraw & swap
                // 1 + fee. If fee is 1% then withdrawing withFepex_ will be 1e6 + 1e4
                temp_ = (temp_ * (SIX_DECIMALS + ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;
                // updating shares to burn for user
                shares_ += temp_;
                // final new collateral shares
                totalSupplyShares_ -= temp_;
            } else {
                revert FluidDexError(ErrorTypes.DexT1__InvalidCollateralReserves);
            }

            if (to_ == ADDRESS_DEAD) revert FluidDexLiquidityOutput(shares_);

            if (shares_ > maxSharesAmt_) revert FluidDexError(ErrorTypes.DexT1__WithdrawExcessSharesBurn);

            // userSupply_ => temp_
            temp_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_AMOUNT) & X64;
            temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

            // calculate current, updated (expanded etc.) withdrawal limit
            // newWithdrawalLimit_ => temp2_
            temp2_ = DexCalcs.calcWithdrawalLimitBeforeOperate(userSupplyData_, temp_);

            temp_ -= shares_;

            // withdrawal limit reached
            if (temp_ < temp2_) revert FluidDexError(ErrorTypes.DexT1__WithdrawLimitReached);

            _updatingUserSupplyDataOnStorage(userSupplyData_, temp_, temp2_);

            // updating total col shares in storage
            _updateSupplyShares(totalSupplyShares_);

            if (w_.token0AmtAdjusted > 0) {
                _verifyToken0Reserves(
                    (w_.token0ReservesInitial - w_.token0AmtAdjusted),
                    (w_.token1ReservesInitial - w_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // withdraw
                temp_ = token0Amt_;
                LIQUIDITY.operate(TOKEN_0, -int(temp_), 0, w_.to, address(0), new bytes(0));
            }

            if (w_.token1AmtAdjusted > 0) {
                _verifyToken1Reserves(
                    (w_.token0ReservesInitial - w_.token0AmtAdjusted),
                    (w_.token1ReservesInitial - w_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // withdraw
                temp_ = token1Amt_;
                LIQUIDITY.operate(TOKEN_1, -int(temp_), 0, w_.to, address(0), new bytes(0));
            }

            emit LogWithdrawColLiquidity(token0Amt_, token1Amt_, shares_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartColNotEnabled);
        }
    }

    /// @dev This function allows users to withdraw their collateral with perfect shares in one token
    /// @param shares_ The number of shares to burn for withdrawal
    /// @param minToken0_ The minimum amount of token0 the user expects to receive (set to 0 if withdrawing in token1)
    /// @param minToken1_ The minimum amount of token1 the user expects to receive (set to 0 if withdrawing in token0)
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with shares_
    /// @return withdrawAmt_ The amount of tokens withdrawn in the chosen token
    function withdrawPerfectInOneToken(
        uint shares_,
        uint minToken0_,
        uint minToken1_,
        address to_
    ) public _onlyDelegateCall returns (uint withdrawAmt_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userSupplyData_ = _userSupplyData[msg.sender];

        if (userSupplyData_ & 1 == 0 && to_ != ADDRESS_DEAD) {
            revert FluidDexError(ErrorTypes.DexT1__UserSupplyInNotOn);
        }

        to_ = (to_ == address(0)) ? msg.sender : to_;

        if ((minToken0_ > 0 && minToken1_ > 0) || (minToken0_ == 0 && minToken1_ == 0)) {
            // only 1 token should be > 0
            revert FluidDexError(ErrorTypes.DexT1__InvalidWithdrawAmts);
        }

        // is smart collateral pool enabled
        if ((dexVariables2_ & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            uint totalSupplyShares_ = _totalSupplyShares & X128;

            _verifyRedeem(shares_, totalSupplyShares_);

            uint token0Amt_;
            uint token1Amt_;

            CollateralReserves memory c_ = _getCollateralReserves(
                pex_.geometricMean,
                pex_.upperRange,
                pex_.lowerRange,
                pex_.supplyToken0ExchangePrice,
                pex_.supplyToken1ExchangePrice
            );

            if ((c_.token0RealReserves == 0) || (c_.token1RealReserves == 0)) {
                revert FluidDexError(ErrorTypes.DexT1__InvalidCollateralReserves);
            }

            CollateralReserves memory c2_ = _getUpdatedColReserves(shares_, totalSupplyShares_, c_, false);
            // Storing exact token0 & token1 raw/adjusted withdrawal amount after burning shares
            token0Amt_ = c_.token0RealReserves - c2_.token0RealReserves - 1;
            token1Amt_ = c_.token1RealReserves - c2_.token1RealReserves - 1;

            if (minToken0_ > 0) {
                // user wants to withdraw entirely in token0, hence swapping token1 into token0
                token0Amt_ += _getAmountOut(token1Amt_, c2_.token1ImaginaryReserves, c2_.token0ImaginaryReserves);
                token1Amt_ = 0;
                _verifyToken0Reserves(
                    (c_.token0RealReserves - token0Amt_),
                    c_.token1RealReserves,
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );

                // converting token0Amt_ from raw/adjusted to normal token amount
                token0Amt_ = (((token0Amt_ - 1) * TOKEN_0_DENOMINATOR_PRECISION) / TOKEN_0_NUMERATOR_PRECISION) - 1;

                // deducting fee on withdrawing in 1 token
                token0Amt_ = (token0Amt_ * (SIX_DECIMALS - ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;

                withdrawAmt_ = token0Amt_;
                if (to_ == ADDRESS_DEAD) revert FluidDexLiquidityOutput(shares_);
                if (withdrawAmt_ < minToken0_) revert FluidDexError(ErrorTypes.DexT1__WithdrawalNotEnough);
            } else {
                // user wants to withdraw entirely in token1, hence swapping token0 into token1
                token1Amt_ += _getAmountOut(token0Amt_, c2_.token0ImaginaryReserves, c2_.token1ImaginaryReserves);
                token0Amt_ = 0;
                _verifyToken1Reserves(
                    c_.token0RealReserves,
                    (c_.token1RealReserves - token1Amt_),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );

                // converting token1Amt_ from raw/adjusted to normal token amount
                token1Amt_ = (((token1Amt_ - 1) * TOKEN_1_DENOMINATOR_PRECISION) / TOKEN_1_NUMERATOR_PRECISION) - 1;

                // deducting fee on withdrawing in 1 token
                token1Amt_ = (token1Amt_ * (SIX_DECIMALS - ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;

                withdrawAmt_ = token1Amt_;
                if (to_ == ADDRESS_DEAD) revert FluidDexLiquidityOutput(shares_);
                if (withdrawAmt_ < minToken1_) revert FluidDexError(ErrorTypes.DexT1__WithdrawalNotEnough);
            }

            uint256 userSupply_ = (userSupplyData_ >> DexSlotsLink.BITS_USER_SUPPLY_AMOUNT) & X64;
            userSupply_ = (userSupply_ >> DEFAULT_EXPONENT_SIZE) << (userSupply_ & DEFAULT_EXPONENT_MASK);

            // calculate current, updated (expanded etc.) withdrawal limit
            // temp_ => newWithdrawalLimit_
            uint256 temp_ = DexCalcs.calcWithdrawalLimitBeforeOperate(userSupplyData_, userSupply_);

            userSupply_ -= shares_;

            // withdraws below limit
            if (userSupply_ < temp_) revert FluidDexError(ErrorTypes.DexT1__WithdrawLimitReached);

            _updatingUserSupplyDataOnStorage(userSupplyData_, userSupply_, temp_);

            totalSupplyShares_ = totalSupplyShares_ - shares_;
            _updateSupplyShares(totalSupplyShares_);

            // to avoid stack-too-deep error
            temp_ = uint160(to_);
            if (minToken0_ > 0) {
                // withdraw
                LIQUIDITY.operate(TOKEN_0, -int(token0Amt_), 0, address(uint160(temp_)), address(0), new bytes(0));
            } else {
                // withdraw
                LIQUIDITY.operate(TOKEN_1, -int(token1Amt_), 0, address(uint160(temp_)), address(0), new bytes(0));
            }

            // to avoid stack-too-deep error
            temp_ = shares_;
            emit LogWithdrawColInOneToken(temp_, token0Amt_, token1Amt_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartColNotEnabled);
        }
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { SecondaryHelpers } from "../helpers/secondaryHelpers.sol";
import { SafeTransfer } from "../../../../../libraries/safeTransfer.sol";
import { DexSlotsLink } from "../../../../../libraries/dexSlotsLink.sol";
import { DexCalcs } from "../../../../../libraries/dexCalcs.sol";
import { BigMathMinified } from "../../../../../libraries/bigMathMinified.sol";
import { ErrorTypes } from "../../../errorTypes.sol";

/// @title FluidDexT1
/// @notice Implements core logics for Fluid Dex protocol.
/// Note Token transfers happen directly from user to Liquidity contract and vice-versa.
contract FluidDexT1OperationsDebt is SecondaryHelpers {
    using BigMathMinified for uint256;

    constructor(ConstantViews memory constantViews_) SecondaryHelpers(constantViews_) {
        // all implementations should be zero other than shift
        if (
            constantViews_.implementations.shift == address(0) ||
            constantViews_.implementations.admin != address(0) ||
            constantViews_.implementations.colOperations != address(0) ||
            constantViews_.implementations.debtOperations != address(0) ||
            constantViews_.implementations.perfectOperationsAndSwapOut != address(0)
        ) {
            revert FluidDexError(ErrorTypes.DexT1__InvalidImplementation);
        }
    }

    modifier _onlyDelegateCall() {
        // also indirectly checked by `_check` because pool can never be initialized as long as the initialize method
        // is delegate call only, but just to be sure on Admin logic we add the modifier everywhere nonetheless.
        if (address(this) == THIS_CONTRACT) {
            revert FluidDexError(ErrorTypes.DexT1__OnlyDelegateCallAllowed);
        }
        _;
    }

    /// @dev This function allows users to borrow tokens in any proportion from the debt pool
    /// @param token0Amt_ The amount of token0 to borrow
    /// @param token1Amt_ The amount of token1 to borrow
    /// @param maxSharesAmt_ The maximum amount of shares the user is willing to receive
    /// @param to_ Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with shares_ 
    /// @return shares_ The amount of borrow shares minted to represent the borrowed amount
    function borrow(
        uint token0Amt_,
        uint token1Amt_,
        uint maxSharesAmt_,
        address to_
    ) public _onlyDelegateCall returns (uint shares_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userBorrowData_ = _userBorrowData[msg.sender];

        if (userBorrowData_ & 1 == 0 && to_ != ADDRESS_DEAD) revert FluidDexError(ErrorTypes.DexT1__UserDebtInNotOn);

        BorrowDebtMemory memory b_;

        b_.to = (to_ == address(0)) ? msg.sender : to_;

        // is smart debt pool enabled
        if (((dexVariables2_ >> 1) & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            uint token0Debt_ = _getLiquidityDebt(BORROW_TOKEN_0_SLOT, pex_.borrowToken0ExchangePrice, true);
            uint token1Debt_ = _getLiquidityDebt(BORROW_TOKEN_1_SLOT, pex_.borrowToken1ExchangePrice, false);
            b_.token0DebtInitial = token0Debt_;
            b_.token1DebtInitial = token1Debt_;

            if (token0Amt_ > 0) {
                b_.token0AmtAdjusted =
                    (((token0Amt_ + 1) * TOKEN_0_NUMERATOR_PRECISION) / TOKEN_0_DENOMINATOR_PRECISION) +
                    1;
                _verifySwapAndNonPerfectActions(b_.token0AmtAdjusted, token0Amt_);
                _verifyMint(b_.token0AmtAdjusted, token0Debt_);
            }

            if (token1Amt_ > 0) {
                b_.token1AmtAdjusted =
                    (((token1Amt_ + 1) * TOKEN_1_NUMERATOR_PRECISION) / TOKEN_1_DENOMINATOR_PRECISION) +
                    1;
                _verifySwapAndNonPerfectActions(b_.token1AmtAdjusted, token1Amt_);
                _verifyMint(b_.token1AmtAdjusted, token1Debt_);
            }

            uint temp_;
            uint temp2_;

            uint totalBorrowShares_ = _totalBorrowShares & X128;
            if ((token0Debt_ > 0) && (token1Debt_ > 0)) {
                if (b_.token0AmtAdjusted > 0 && b_.token1AmtAdjusted > 0) {
                    // mint shares in equal proportion
                    // temp_ => expected shares from token0 payback
                    temp_ = (b_.token0AmtAdjusted * 1e18) / token0Debt_;
                    // temp2_ => expected shares from token1 payback
                    temp2_ = (b_.token1AmtAdjusted * 1e18) / token1Debt_;
                    if (temp_ > temp2_) {
                        // use temp2_ shares
                        shares_ = (temp2_ * totalBorrowShares_) / 1e18;
                        // temp_ => token0 to swap
                        temp_ = ((temp_ - temp2_) * token0Debt_) / 1e18;
                        temp2_ = 0;
                    } else if (temp2_ > temp_) {
                        // use temp1_ shares
                        shares_ = (temp_ * totalBorrowShares_) / 1e18;
                        // temp2_ => token1 to swap
                        temp2_ = ((temp2_ - temp_) * token1Debt_) / 1e18;
                        temp_ = 0;
                    } else {
                        // if equal then revert as swap will not be needed anymore which can create some issue, better to use perfect borrow in this case
                        revert FluidDexError(ErrorTypes.DexT1__InvalidBorrowAmts);
                    }

                    // User borrowed in equal proportion here. Hence updating col reserves and the swap will happen on updated col reserves
                    token0Debt_ = token0Debt_ + (token0Debt_ * shares_) / totalBorrowShares_;
                    token1Debt_ = token1Debt_ + (token1Debt_ * shares_) / totalBorrowShares_;
                    totalBorrowShares_ += shares_;
                } else if (b_.token0AmtAdjusted > 0) {
                    temp_ = b_.token0AmtAdjusted;
                    temp2_ = 0;
                } else if (b_.token1AmtAdjusted > 0) {
                    temp_ = 0;
                    temp2_ = b_.token1AmtAdjusted;
                } else {
                    // user sent both amounts as 0
                    revert FluidDexError(ErrorTypes.DexT1__InvalidBorrowAmts);
                }

                uint token0FinalImaginaryReserves_;
                uint token1FinalImaginaryReserves_;

                if (pex_.geometricMean < 1e27) {
                    (, , token0FinalImaginaryReserves_, token1FinalImaginaryReserves_) = _calculateDebtReserves(
                        pex_.geometricMean,
                        pex_.lowerRange,
                        (token0Debt_ + temp_),
                        (token1Debt_ + temp2_)
                    );
                } else {
                    // inversing, something like `xy = k` so for calculation we are making everything related to x into y & y into x
                    // 1 / geometricMean for new geometricMean
                    // 1 / lowerRange will become upper range
                    // 1 / upperRange will become lower range
                    (, , token1FinalImaginaryReserves_, token0FinalImaginaryReserves_) = _calculateDebtReserves(
                        (1e54 / pex_.geometricMean),
                        (1e54 / pex_.upperRange),
                        (token1Debt_ + temp2_),
                        (token0Debt_ + temp_)
                    );
                }

                if (temp_ > 0) {
                    // swap into token0
                    temp_ = _getBorrowAndSwap(
                        token0Debt_, // token0 debt
                        token1Debt_, // token1 debt
                        token0FinalImaginaryReserves_, // token0 imaginary reserves
                        token1FinalImaginaryReserves_, // token1 imaginary reserves
                        temp_ // token0 to divide and swap into
                    );
                } else if (temp2_ > 0) {
                    // swap into token1
                    temp_ = _getBorrowAndSwap(
                        token1Debt_, // token1 debt
                        token0Debt_, // token0 debt
                        token1FinalImaginaryReserves_, // token1 imaginary reserves
                        token0FinalImaginaryReserves_, // token0 imaginary reserves
                        temp2_ // token1 to divide and swap into
                    );
                } else {
                    // maybe possible to happen due to some precision issue that both are 0
                    revert FluidDexError(ErrorTypes.DexT1__BorrowAmtsZero);
                }

                // new shares to mint from borrow & swap
                temp_ = (temp_ * totalBorrowShares_) / 1e18;
                // adding fee in case of borrow & swap
                // 1 + fee. If fee is 1% then withdrawing withFepex_ will be 1e6 + 1e4
                temp_ = (temp_ * (SIX_DECIMALS + ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;
                // final new shares to mint for user
                shares_ += temp_;
                // final new debt shares
                totalBorrowShares_ += temp_;
            } else {
                revert FluidDexError(ErrorTypes.DexT1__InvalidDebtReserves);
            }

            if (to_ == ADDRESS_DEAD) revert FluidDexLiquidityOutput(shares_);

            if (shares_ > maxSharesAmt_) revert FluidDexError(ErrorTypes.DexT1__BorrowExcessSharesMinted);

            // extract user borrow amount
            // userBorrow_ => temp_
            temp_ = (userBorrowData_ >> DexSlotsLink.BITS_USER_BORROW_AMOUNT) & X64;
            temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

            // calculate current, updated (expanded etc.) borrow limit
            // newBorrowLimit_ => temp2_
            temp2_ = DexCalcs.calcBorrowLimitBeforeOperate(userBorrowData_, temp_);

            temp_ += shares_;

            // user above debt limit
            if (temp_ > temp2_) revert FluidDexError(ErrorTypes.DexT1__DebtLimitReached);

            _updatingUserBorrowDataOnStorage(userBorrowData_, temp_, temp2_);

            if (b_.token0AmtAdjusted > 0) {
                // comparing debt here rather than reserves to simply code, impact won't be much overall
                _verifyToken1Reserves(
                    (b_.token0DebtInitial + b_.token0AmtAdjusted),
                    (b_.token1DebtInitial + b_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // assigning token0Amt_ to temp_ to avoid compilation error (I don't know why it's throwing when using token0Amt_ directly)
                temp_ = token0Amt_;
                // borrow
                LIQUIDITY.operate(TOKEN_0, 0, int(temp_), address(0), b_.to, new bytes(0));
            }

            if (b_.token1AmtAdjusted > 0) {
                // comparing debt here rather than reserves to simply code, impact won't be much overall
                _verifyToken0Reserves(
                    (b_.token0DebtInitial + b_.token0AmtAdjusted),
                    (b_.token1DebtInitial + b_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // assigning token1Amt_ to temp_ to avoid compilation error (I don't know why it's throwing when using token0Amt_ directly)
                temp_ = token1Amt_;
                // borrow
                LIQUIDITY.operate(TOKEN_1, 0, int(temp_), address(0), b_.to, new bytes(0));
            }

            // updating total debt shares in storage
            _updateBorrowShares(totalBorrowShares_);

            emit LogBorrowDebtLiquidity(token0Amt_, token1Amt_, shares_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartDebtNotEnabled);
        }
    }

    /// @dev This function allows users to payback tokens in any proportion to the debt pool
    /// @param token0Amt_ The amount of token0 to payback
    /// @param token1Amt_ The amount of token1 to payback
    /// @param minSharesAmt_ The minimum amount of shares the user expects to burn
    /// @param estimate_ If true, function will revert with estimated shares without executing the payback
    /// @return shares_ The amount of borrow shares burned for the payback
    function payback(
        uint token0Amt_,
        uint token1Amt_,
        uint minSharesAmt_,
        bool estimate_
    ) public payable _onlyDelegateCall returns (uint shares_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userBorrowData_ = _userBorrowData[msg.sender];

        if (userBorrowData_ & 1 == 0 && !estimate_) revert FluidDexError(ErrorTypes.DexT1__UserDebtInNotOn);

        // is smart debt pool enabled
        if (((dexVariables2_ >> 1) & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            PaybackDebtMemory memory p_;

            DebtReserves memory d_ = _getDebtReserves(
                pex_.geometricMean,
                pex_.upperRange,
                pex_.lowerRange,
                pex_.borrowToken0ExchangePrice,
                pex_.borrowToken1ExchangePrice
            );
            DebtReserves memory d2_ = d_;

            if (token0Amt_ > 0) {
                p_.token0AmtAdjusted =
                    (((token0Amt_ - 1) * TOKEN_0_NUMERATOR_PRECISION) / TOKEN_0_DENOMINATOR_PRECISION) -
                    1;
                _verifySwapAndNonPerfectActions(p_.token0AmtAdjusted, token0Amt_);
                _verifyRedeem(p_.token0AmtAdjusted, d_.token0Debt);
            }

            if (token1Amt_ > 0) {
                p_.token1AmtAdjusted =
                    (((token1Amt_ - 1) * TOKEN_1_NUMERATOR_PRECISION) / TOKEN_1_DENOMINATOR_PRECISION) -
                    1;
                _verifySwapAndNonPerfectActions(p_.token1AmtAdjusted, token1Amt_);
                _verifyRedeem(p_.token1AmtAdjusted, d_.token1Debt);
            }

            uint temp_;
            uint temp2_;

            uint totalBorrowShares_ = _totalBorrowShares & X128;
            if ((d_.token0Debt > 0) && (d_.token1Debt > 0)) {
                if (p_.token0AmtAdjusted > 0 && p_.token1AmtAdjusted > 0) {
                    // burn shares in equal proportion
                    // temp_ => expected shares from token0 payback
                    temp_ = (p_.token0AmtAdjusted * 1e18) / d_.token0Debt;
                    // temp2_ => expected shares from token1 payback
                    temp2_ = (p_.token1AmtAdjusted * 1e18) / d_.token1Debt;
                    if (temp_ > temp2_) {
                        // use temp2_ shares
                        shares_ = ((temp2_ * totalBorrowShares_) / 1e18);
                        // temp_ => token0 to swap
                        temp_ = p_.token0AmtAdjusted - (temp2_ * p_.token0AmtAdjusted) / temp_;
                        temp2_ = 0;
                    } else if (temp2_ > temp_) {
                        // use temp_ shares
                        shares_ = ((temp_ * totalBorrowShares_) / 1e18);
                        // temp2_ => token1 to swap
                        temp2_ = p_.token1AmtAdjusted - ((temp_ * p_.token1AmtAdjusted) / temp2_); // to this
                        temp_ = 0;
                    } else {
                        // if equal then revert as swap will not be needed anymore which can create some issue, better to use perfect payback in this case
                        revert FluidDexError(ErrorTypes.DexT1__InvalidPaybackAmts);
                    }

                    // User paid back in equal proportion here. Hence updating debt reserves and the swap will happen on updated debt reserves
                    d2_ = _getUpdateDebtReserves(
                        shares_,
                        totalBorrowShares_,
                        d_,
                        false // true if mint, false if burn
                    );
                    totalBorrowShares_ -= shares_;
                } else if (p_.token0AmtAdjusted > 0) {
                    temp_ = p_.token0AmtAdjusted;
                    temp2_ = 0;
                } else if (p_.token1AmtAdjusted > 0) {
                    temp_ = 0;
                    temp2_ = p_.token1AmtAdjusted;
                } else {
                    // user sent both amounts as 0
                    revert FluidDexError(ErrorTypes.DexT1__InvalidPaybackAmts);
                }

                if (temp_ > 0) {
                    // swap token0 into token1 and payback equally
                    temp_ = _getSwapAndPayback(
                        d2_.token0Debt,
                        d2_.token1Debt,
                        d2_.token0ImaginaryReserves,
                        d2_.token1ImaginaryReserves,
                        temp_
                    );
                } else if (temp2_ > 0) {
                    // swap token1 into token0 and payback equally
                    temp_ = _getSwapAndPayback(
                        d2_.token1Debt,
                        d2_.token0Debt,
                        d2_.token1ImaginaryReserves,
                        d2_.token0ImaginaryReserves,
                        temp2_
                    );
                } else {
                    // maybe possible to happen due to some precision issue that both are 0
                    revert FluidDexError(ErrorTypes.DexT1__PaybackAmtsZero);
                }

                // new shares to burn from payback & swap
                temp_ = ((temp_ * totalBorrowShares_) / 1e18);

                // adding fee in case of payback & swap
                // 1 - fee. If fee is 1% then withdrawing withFepex_ will be 1e6 - 1e4
                temp_ = (temp_ * (SIX_DECIMALS - ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;
                // final shares to burn for user
                shares_ += temp_;
                // final new debt shares
                totalBorrowShares_ -= temp_;
            } else {
                revert FluidDexError(ErrorTypes.DexT1__InvalidDebtReserves);
            }

            if (estimate_) revert FluidDexLiquidityOutput(shares_);

            if (shares_ < minSharesAmt_) revert FluidDexError(ErrorTypes.DexT1__PaybackSharedBurnedLess);

            if (token0Amt_ > 0) {
                // comparing debt here rather than reserves to simply code, impact won't be much overall
                _verifyToken0Reserves(
                    (d_.token0Debt - p_.token0AmtAdjusted),
                    (d_.token1Debt - p_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // payback
                temp_ = token0Amt_;
                _depositOrPaybackInLiquidity(TOKEN_0, 0, temp_);
            }

            if (token1Amt_ > 0) {
                // comparing debt here rather than reserves to simply code, impact won't be much overall
                _verifyToken1Reserves(
                    (d_.token0Debt - p_.token0AmtAdjusted),
                    (d_.token1Debt - p_.token1AmtAdjusted),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );
                // payback
                temp_ = token1Amt_;
                _depositOrPaybackInLiquidity(TOKEN_1, 0, temp_);
            }

            // extract user borrow amount
            // userBorrow_ => temp_
            temp_ = (userBorrowData_ >> DexSlotsLink.BITS_USER_BORROW_AMOUNT) & X64;
            temp_ = (temp_ >> DEFAULT_EXPONENT_SIZE) << (temp_ & DEFAULT_EXPONENT_MASK);

            // calculate current, updated (expanded etc.) borrow limit
            // newBorrowLimit_ => temp2_
            temp2_ = DexCalcs.calcBorrowLimitBeforeOperate(userBorrowData_, temp_);

            temp_ -= shares_;

            _updatingUserBorrowDataOnStorage(userBorrowData_, temp_, temp2_);
            // updating total debt shares in storage
            _updateBorrowShares(totalBorrowShares_);

            emit LogPaybackDebtLiquidity(token0Amt_, token1Amt_, shares_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartDebtNotEnabled);
        }
    }

    /// @dev This function allows users to payback their debt with perfect shares in one token
    /// @param shares_ The number of shares to burn for payback
    /// @param maxToken0_ The maximum amount of token0 the user is willing to pay (set to 0 if paying back in token1)
    /// @param maxToken1_ The maximum amount of token1 the user is willing to pay (set to 0 if paying back in token0)
    /// @param estimate_ If true, the function will revert with the estimated payback amount without executing the payback
    /// @return paybackAmt_ The amount of tokens paid back in the chosen token
    function paybackPerfectInOneToken(
        uint shares_,
        uint maxToken0_,
        uint maxToken1_,
        bool estimate_
    ) public payable _onlyDelegateCall returns (uint paybackAmt_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        _check(dexVariables_, dexVariables2_);

        uint userBorrowData_ = _userBorrowData[msg.sender];

        if (userBorrowData_ & 1 == 0 && !estimate_) revert FluidDexError(ErrorTypes.DexT1__UserDebtInNotOn);

        if ((maxToken0_ > 0 && maxToken1_ > 0) || (maxToken0_ == 0 && maxToken1_ == 0)) {
            // only 1 token should be > 0
            revert FluidDexError(ErrorTypes.DexT1__InvalidWithdrawAmts);
        }

        // is smart debt pool enabled
        if (((dexVariables2_ >> 1) & 1) == 1) {
            PricesAndExchangePrice memory pex_ = _getPricesAndExchangePrices(dexVariables_, dexVariables2_);

            uint totalBorrowShares_ = _totalBorrowShares & X128;

            _verifyRedeem(shares_, totalBorrowShares_);

            uint token0Amt_;
            uint token1Amt_;

            // smart debt in enabled
            DebtReserves memory d_ = _getDebtReserves(
                pex_.geometricMean,
                pex_.upperRange,
                pex_.lowerRange,
                pex_.borrowToken0ExchangePrice,
                pex_.borrowToken1ExchangePrice
            );

            if ((d_.token0Debt == 0) || (d_.token1Debt == 0)) {
                revert FluidDexError(ErrorTypes.DexT1__InvalidDebtReserves);
            }

            // Removing debt liquidity in equal proportion
            DebtReserves memory d2_ = _getUpdateDebtReserves(shares_, totalBorrowShares_, d_, false);

            if (maxToken0_ > 0) {
                // entire payback is in token0_
                token0Amt_ = _getSwapAndPaybackOneTokenPerfectShares(
                    d2_.token0ImaginaryReserves,
                    d2_.token1ImaginaryReserves,
                    d_.token0Debt,
                    d_.token1Debt,
                    d2_.token0RealReserves,
                    d2_.token1RealReserves
                );
                _verifyToken0Reserves(
                    (d_.token0Debt - token0Amt_),
                    d_.token1Debt,
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );

                // converting from raw/adjusted to normal token amounts
                token0Amt_ = (((token0Amt_ + 1) * TOKEN_0_DENOMINATOR_PRECISION) / TOKEN_0_NUMERATOR_PRECISION) + 1;

                // adding fee on paying back in 1 token
                token0Amt_ = (token0Amt_ * (SIX_DECIMALS + ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;

                paybackAmt_ = token0Amt_;
                if (estimate_) revert FluidDexSingleTokenOutput(paybackAmt_);
                if (paybackAmt_ > maxToken0_) revert FluidDexError(ErrorTypes.DexT1__PaybackAmtTooHigh);
                _depositOrPaybackInLiquidity(TOKEN_0, 0, paybackAmt_);
            } else {
                // entire payback is in token1_
                token1Amt_ = _getSwapAndPaybackOneTokenPerfectShares(
                    d2_.token1ImaginaryReserves,
                    d2_.token0ImaginaryReserves,
                    d_.token1Debt,
                    d_.token0Debt,
                    d2_.token1RealReserves,
                    d2_.token0RealReserves
                );
                _verifyToken1Reserves(
                    d_.token0Debt,
                    (d_.token1Debt - token1Amt_),
                    pex_.centerPrice,
                    MINIMUM_LIQUIDITY_USER_OPERATIONS
                );

                // converting from raw/adjusted to normal token amounts
                token1Amt_ = (((token1Amt_ + 1) * TOKEN_1_DENOMINATOR_PRECISION) / TOKEN_1_NUMERATOR_PRECISION) + 1;

                // adding fee on paying back in 1 token
                token1Amt_ = (token1Amt_ * (SIX_DECIMALS + ((dexVariables2_ >> 2) & X17))) / SIX_DECIMALS;

                paybackAmt_ = token1Amt_;
                if (estimate_) revert FluidDexSingleTokenOutput(paybackAmt_);
                if (paybackAmt_ > maxToken1_) revert FluidDexError(ErrorTypes.DexT1__PaybackAmtTooHigh);
                _depositOrPaybackInLiquidity(TOKEN_1, 0, paybackAmt_);
            }

            // extract user borrow amount
            uint256 userBorrow_ = (userBorrowData_ >> DexSlotsLink.BITS_USER_BORROW_AMOUNT) & X64;
            userBorrow_ = (userBorrow_ >> DEFAULT_EXPONENT_SIZE) << (userBorrow_ & DEFAULT_EXPONENT_MASK);

            // calculate current, updated (expanded etc.) borrow limit
            // temp_ => newBorrowLimit_
            uint256 temp_ = DexCalcs.calcBorrowLimitBeforeOperate(userBorrowData_, userBorrow_);
            userBorrow_ -= shares_;

            _updatingUserBorrowDataOnStorage(userBorrowData_, userBorrow_, temp_);

            totalBorrowShares_ = totalBorrowShares_ - shares_;
            _updateBorrowShares(totalBorrowShares_);

            // to avoid stack-too-deep error
            temp_ = shares_;
            emit LogPaybackDebtInOneToken(temp_, token0Amt_, token1Amt_);

            _arbitrage(dexVariables_, dexVariables2_, pex_);
        } else {
            revert FluidDexError(ErrorTypes.DexT1__SmartDebtNotEnabled);
        }
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { CoreHelpers } from "../helpers/coreHelpers.sol";
import { SafeTransfer } from "../../../../../libraries/safeTransfer.sol";
import { DexSlotsLink } from "../../../../../libraries/dexSlotsLink.sol";
import { DexCalcs } from "../../../../../libraries/dexCalcs.sol";
import { BigMathMinified } from "../../../../../libraries/bigMathMinified.sol";
import { ErrorTypes } from "../../../errorTypes.sol";
import { IFluidDexT1 } from "../../../interfaces/iDexT1.sol";

interface IDexCallback {
    function dexCallback(address token_, uint256 amount_) external;
}

/// @title FluidDexT1
/// @notice Implements core logics for Fluid Dex protocol.
/// Note Token transfers happen directly from user to Liquidity contract and vice-versa.
contract FluidDexT1 is CoreHelpers {
    using BigMathMinified for uint256;

    constructor(ConstantViews memory constantViews_) CoreHelpers(constantViews_) {
        // any implementations should not be zero
        if (
            constantViews_.implementations.shift == address(0) ||
            constantViews_.implementations.admin == address(0) ||
            constantViews_.implementations.colOperations == address(0) ||
            constantViews_.implementations.debtOperations == address(0) ||
            constantViews_.implementations.perfectOperationsAndSwapOut == address(0)
        ) {
            revert FluidDexError(ErrorTypes.DexT1__InvalidImplementation);
        }
    }

    struct SwapInExtras {
        address to;
        uint amountOutMin;
        bool isCallback;
    }

    /// @dev This function allows users to swap a specific amount of input tokens for output tokens
    /// @param swap0to1_ Direction of swap. If true, swaps token0 for token1; if false, swaps token1 for token0
    /// @param amountIn_ The exact amount of input tokens to swap
    /// @param extras_ Additional parameters for the swap:
    ///   - to: Recipient of swapped tokens. If to_ == address(0) then out tokens will be sent to msg.sender. If to_ == ADDRESS_DEAD then function will revert with amountOut_
    ///   - amountOutMin: The minimum amount of output tokens the user expects to receive
    ///   - isCallback: If true, indicates that the input tokens should be transferred via a callback
    /// @return amountOut_ The amount of output tokens received from the swap
    function _swapIn(
        bool swap0to1_,
        uint256 amountIn_,
        SwapInExtras memory extras_
    ) internal returns (uint256 amountOut_) {
        uint dexVariables_ = dexVariables;
        uint dexVariables2_ = dexVariables2;

        if ((dexVariables2_ >> 255) == 1) revert FluidDexError(ErrorTypes.DexT1__SwapAndArbitragePaused);

        _check(dexVariables_, dexVariables2_);

        if (extras_.to == address(0)) extras_.to = msg.sender;

        SwapInMemory memory s_;

        if (swap0to1_) {
            (s_.tokenIn, s_.tokenOut) = (TOKEN_0, TOKEN_1);
            unchecked {
