        _CHAINLINK2_INVERT_PRICE_DIVIDEND1 = 10 ** (OracleUtils.RATE_OUTPUT_DECIMALS + params_.feed1.token0Decimals);

        _CHAINLINK2_PRICE_SCALER_MULTIPLIER2 = params_.hops > 1
