// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { ErrorTypes } from "../../errorTypes.sol";
import { FluidVault } from "../../vaultTypesCommon/coreModule/main.sol";

/// @notice Fluid "VaultT4" (Vault Type 4). Fluid vault protocol main contract. T4 -> Smart collateral | Smart debt
///         Fluid Vault protocol is a borrow / lending protocol, allowing users to create collateral / borrow positions.
///         All funds are deposited into / borrowed from Fluid Liquidity layer.
///         Positions are represented through NFTs minted by the VaultFactory.
///         Deployed by "VaultFactory" and linked together with Vault AdminModule `ADMIN_IMPLEMENTATION` and
///         FluidVaultSecondary (main2.sol) `SECONDARY_IMPLEMENTATION`.
///         AdminModule & FluidVaultSecondary methods are delegateCalled, if the msg.sender has the required authorization.
///         This contract links to an Oracle, which is used to assess collateral / debt value. Oracles implement the
///         "FluidOracle" base contract and return the price in 1e27 precision.
/// @dev    For view methods / accessing data, use the "VaultResolver" periphery contract.
//
// vaults can only be deployed for tokens that are listed at Liquidity (constructor reverts otherwise
// if either the exchange price for the supply token or the borrow token is still not set at Liquidity).
abstract contract Internals is FluidVault {
    function _debtLiquidateBefore(
        uint token0DebtAmt_,
        uint token1DebtAmt_,
        uint debtSharesMin_
    ) internal returns (uint shares_) {
        // paying back and burning shares
        if (token0DebtAmt_ == 0 && token1DebtAmt_ == 0) {
            // when burning shares, debt amount should always be > 0 (aka payback)
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }

        shares_ = BORROW.payback{
            value: (BORROW_TOKEN0 == NATIVE_TOKEN)
                ? token0DebtAmt_
                : (BORROW_TOKEN1 == NATIVE_TOKEN)
                    ? token1DebtAmt_
                    : 0
        }(token0DebtAmt_, token1DebtAmt_, debtSharesMin_, false);
    }

    function _colLiquidatePerfectAfter(
        uint perfectColShares_,
        uint token0ColAmtPerUnitShares_,
        uint token1ColAmtPerUnitShares_,
        address to_
    ) internal returns (uint newColToken0_, uint newColToken1_) {
        uint colToken0Min_ = (token0ColAmtPerUnitShares_ * perfectColShares_) / 1e18;
        uint colToken1Min_ = (token1ColAmtPerUnitShares_ * perfectColShares_) / 1e18;

        if (colToken0Min_ > 0 && colToken1Min_ > 0) {
            (newColToken0_, newColToken1_) = SUPPLY.withdrawPerfect(
                perfectColShares_,
                colToken0Min_,
                colToken1Min_,
                to_
            );
        } else if (colToken0Min_ > 0 && colToken1Min_ == 0) {
            // withdraw only in token0, newColToken1_ remains 0
            (newColToken0_) = SUPPLY.withdrawPerfectInOneToken(perfectColShares_, colToken0Min_, colToken1Min_, to_);
        } else if (colToken0Min_ == 0 && colToken1Min_ > 0) {
            // withdraw only in token1, newColToken0_ remains 0
            (newColToken1_) = SUPPLY.withdrawPerfectInOneToken(perfectColShares_, colToken0Min_, colToken1Min_, to_);
        } else {
            // both sent as 0
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }
    }

    function _debtLiquidatePerfectPayback(
        uint perfectDebtShares_,
        uint token0DebtAmtPerUnitShares_,
        uint token1DebtAmtPerUnitShares_
    ) internal returns (uint token0DebtPaid_, uint token1DebtPaid_) {
        uint debtToken0Min_ = (token0DebtAmtPerUnitShares_ * perfectDebtShares_) / 1e18;
        uint debtToken1Min_ = (token1DebtAmtPerUnitShares_ * perfectDebtShares_) / 1e18;

        if (debtToken0Min_ > 0 && debtToken1Min_ > 0) {
            (token0DebtPaid_, token1DebtPaid_) = BORROW.paybackPerfect{
                value: (BORROW_TOKEN0 == NATIVE_TOKEN)
                    ? debtToken0Min_
                    : (BORROW_TOKEN1 == NATIVE_TOKEN)
                        ? debtToken1Min_
                        : 0
            }(perfectDebtShares_, debtToken0Min_, debtToken1Min_, false);
        } else if (debtToken0Min_ > 0 && debtToken1Min_ == 0) {
            // payback only in token0, token1DebtPaid_ remains 0
            (token0DebtPaid_) = BORROW.paybackPerfectInOneToken{
                value: (BORROW_TOKEN0 == NATIVE_TOKEN) ? debtToken0Min_ : 0
            }(perfectDebtShares_, debtToken0Min_, debtToken1Min_, false);
        } else if (debtToken0Min_ == 0 && debtToken1Min_ > 0) {
            // payback only in token1, token0DebtPaid_ remains 0
            (token1DebtPaid_) = BORROW.paybackPerfectInOneToken{
                value: (BORROW_TOKEN1 == NATIVE_TOKEN) ? debtToken1Min_ : 0
            }(perfectDebtShares_, debtToken0Min_, debtToken1Min_, false);
        } else {
            // both sent as 0
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }
    }

    constructor(ConstantViews memory constants_) FluidVault(constants_) {}
}

contract FluidVaultT4 is Internals {
    /// @notice Performs operations on a vault position
    /// @dev This function allows users to modify their vault position by adjusting collateral and debt
    /// @param nftId_ The ID of the NFT representing the vault position
    /// @param newColToken0_ The change in collateral amount for token0 (positive for deposit, negative for withdrawal)
    /// @param newColToken1_ The change in collateral amount for token1 (positive for deposit, negative for withdrawal)
    /// @param colSharesMinMax_ Min or max collateral shares to mint or burn (positive for deposit, negative for withdrawal)
    /// @param newDebtToken0_ The change in debt amount for token0 (positive for borrowing, negative for repayment)
    /// @param newDebtToken1_ The change in debt amount for token1 (positive for borrowing, negative for repayment)
    /// @param debtSharesMinMax_ Min or max debt shares to burn or mint (positive for borrowing, negative for repayment)
    /// @param to_ The address to receive funds (if address(0), defaults to msg.sender)
    /// @return supplyAmt_ Final supply amount (negative if withdrawal occurred)
    /// @return borrowAmt_ Final borrow amount (negative if repayment occurred)
    function operate(
        uint nftId_,
        int newColToken0_,
        int newColToken1_,
        int colSharesMinMax_,
        int newDebtToken0_,
        int newDebtToken1_,
        int debtSharesMinMax_,
        address to_
    )
        external
        payable
        _dexFromAddress
        returns (
            uint256, // nftId_
            int256, // final supply amount. if - then withdraw
            int256 // final borrow amount. if - then payback
        )
    {
        return abi.decode(_spell(OPERATE_IMPLEMENTATION, msg.data), (uint, int, int));
    }

    /// @notice Performs operations on a vault position with perfect collateral shares
    /// @dev This function allows users to modify their vault position by adjusting collateral and debt
    /// @param nftId_ The ID of the NFT representing the vault position
    /// @param perfectColShares_ The change in collateral shares (positive for deposit, negative for withdrawal)
    /// @param colToken0MinMax_ Min or max collateral amount of token0 to withdraw or deposit (positive for deposit, negative for withdrawal)
    /// @param colToken1MinMax_ Min or max collateral amount of token1 to withdraw or deposit (positive for deposit, negative for withdrawal)
    /// @param perfectDebtShares_ The change in debt shares (positive for borrowing, negative for repayment)
    /// @param debtToken0MinMax_ Min or max debt amount for token0 to borrow or payback (positive for borrowing, negative for repayment)
    /// @param debtToken1MinMax_ Min or max debt amount for token1 to borrow or payback (positive for borrowing, negative for repayment)
    /// @param to_ The address to receive funds (if address(0), defaults to msg.sender)
    /// @return nftId_ The ID of the NFT representing the updated vault position
    /// @return r_ int256 array of return values:
    ///              0 - final col shares amount (can only change on max withdrawal)
    ///              1 - token0 deposit or withdraw amount
    ///              2 - token1 deposit or withdraw amount
    ///              3 - final debt shares amount (can only change on max payback)
    ///              4 - token0 borrow or payback amount
    ///              5 - token1 borrow or payback amount
    function operatePerfect(
        uint nftId_,
        int perfectColShares_,
        int colToken0MinMax_,
        int colToken1MinMax_,
        int perfectDebtShares_,
        int debtToken0MinMax_,
        int debtToken1MinMax_,
        address to_
    )
        external
        payable
        _dexFromAddress
        returns (
            uint256, // nftId_
            int256[] memory r_
        )
    {
        return abi.decode(_spell(OPERATE_IMPLEMENTATION, msg.data), (uint, int256[]));
    }

    /// @notice Liquidates a vault position
    /// @dev This function allows users to liquidate a vault position by adjusting collateral and debt
    /// @param token0DebtAmt_ The amount of debt in token0 to payback
    /// @param token1DebtAmt_ The amount of debt in token1 to payback
    /// @param debtSharesMin_ The minimum number of debt shares to liquidate
    /// @param colPerUnitDebt_ The collateral amount per unit of debt shares
    /// @param token0ColAmtPerUnitShares_ The collateral amount per unit of debt shares for token0 (in 1e18)
    /// @param token1ColAmtPerUnitShares_ The collateral amount per unit of debt shares for token1 (in 1e18)
    /// @param to_ The address to receive withdrawn collateral (if address(0), defaults to msg.sender)
    /// @param absorb_ Whether to liquidate absorbed liquidity as well
    /// @return actualDebtShares_ The actual number of debt shares liquidated
    /// @return actualColShares_ The actual number of collateral shares liquidated
    /// @return token0Col_ The amount of token0 collateral withdrawn
    /// @return token1Col_ The amount of token1 collateral withdrawn
    function liquidate(
        uint256 token0DebtAmt_,
        uint256 token1DebtAmt_,
        uint256 debtSharesMin_,
        uint256 colPerUnitDebt_, // col per unit is w.r.t debt shares and not token0/1 debt amount
        uint256 token0ColAmtPerUnitShares_, // in 1e18
        uint256 token1ColAmtPerUnitShares_, // in 1e18
        address to_,
        bool absorb_
    )
        external
        payable
        _dexFromAddress
        returns (uint256 actualDebtShares_, uint256 actualColShares_, uint256 token0Col_, uint256 token1Col_)
    {
        uint vaultVariables_ = vaultVariables;
        // ############# turning re-entrancy bit on #############
        if (vaultVariables_ & 1 == 0) {
            // Updating on storage
            vaultVariables = vaultVariables_ | 1;
        } else {
            revert FluidVaultError(ErrorTypes.Vault__AlreadyEntered);
        }

        uint initialEth_ = address(this).balance - msg.value;

        to_ = to_ == address(0) ? msg.sender : to_;

        uint sharesPaid_ = _debtLiquidateBefore(token0DebtAmt_, token1DebtAmt_, debtSharesMin_);
        (actualDebtShares_, actualColShares_, vaultVariables_) = abi.decode(
            _liquidate(sharesPaid_, colPerUnitDebt_, to_, absorb_, vaultVariables_),
            (uint, uint, uint)
        );

        if (actualDebtShares_ < sharesPaid_) {
            // shares paid should never be more than actual liquidation available
            revert FluidVaultError(ErrorTypes.VaultDex__DebtSharesPaidMoreThanAvailableLiquidation);
        }

        (token0Col_, token1Col_) = _colLiquidatePerfectAfter(
            actualColShares_,
            token0ColAmtPerUnitShares_,
            token1ColAmtPerUnitShares_,
            to_
        );

        // disabling re-entrancy and updating on storage
        vaultVariables = vaultVariables_;

        _validateEth(initialEth_);
    }

    struct LiquidatePerfect {
        uint256 vaultVariables;
        uint256 initialEth;
    }

    /// @notice Liquidates a vault position with perfect collateral shares
    /// @dev This function allows users to liquidate a vault position by adjusting collateral and debt
    /// @param debtShares_ The number of debt shares to liquidate
    /// @param token0DebtAmtPerUnitShares_ The debt amount per unit of debt shares for token0 (in 1e18)
    /// @param token1DebtAmtPerUnitShares_ The debt amount per unit of debt shares for token1 (in 1e18)
    /// @param colPerUnitDebt_ The collateral amount per unit of debt shares (in 1e18)
    /// @param token0ColAmtPerUnitShares_ The collateral amount per unit of debt shares for token0 (in 1e18)
    /// @param token1ColAmtPerUnitShares_ The collateral amount per unit of debt shares for token1 (in 1e18)
    /// @param to_ The address to receive withdrawn collateral or borrowed tokens (if address(0), defaults to msg.sender)
    /// @param absorb_ Whether to liquidate absorbed liquidity as well
    /// @return actualDebtShares_ The actual number of debt shares liquidated
    /// @return token0Debt_ The amount of debt in token0 that was paid back
    /// @return token1Debt_ The amount of debt in token1 that was paid back
    /// @return actualColShares_ The actual number of collateral shares liquidated
    /// @return token0Col_ The amount of collateral in token0 that was withdrawn
    /// @return token1Col_ The amount of collateral in token1 that was withdrawn
    function liquidatePerfect(
        uint256 debtShares_,
        uint256 token0DebtAmtPerUnitShares_,
        uint256 token1DebtAmtPerUnitShares_,
        uint256 colPerUnitDebt_, // col per unit is w.r.t debt shares and not token0/1 debt amount
        uint256 token0ColAmtPerUnitShares_, // in 1e18
        uint256 token1ColAmtPerUnitShares_, // in 1e18
        address to_,
        bool absorb_
    )
        external
        payable
        _dexFromAddress
        returns (
            uint256 actualDebtShares_,
            uint256 token0Debt_,
            uint256 token1Debt_,
            uint256 actualColShares_,
            uint256 token0Col_,
            uint256 token1Col_
        )
    {
        LiquidatePerfect memory lp_;
        lp_.vaultVariables = vaultVariables;
        // ############# turning re-entrancy bit on #############
        if (lp_.vaultVariables & 1 == 0) {
            // Updating on storage
            vaultVariables = lp_.vaultVariables | 1;
        } else {
            revert FluidVaultError(ErrorTypes.Vault__AlreadyEntered);
        }

        lp_.initialEth = address(this).balance - msg.value;

        to_ = to_ == address(0) ? msg.sender : to_;

        (actualDebtShares_, actualColShares_, lp_.vaultVariables) = abi.decode(
            _liquidate(debtShares_, colPerUnitDebt_, to_, absorb_, lp_.vaultVariables),
            (uint, uint, uint)
        );

        (token0Debt_, token1Debt_) = _debtLiquidatePerfectPayback(
            actualDebtShares_,
            token0DebtAmtPerUnitShares_,
            token1DebtAmtPerUnitShares_
        );

        (token0Col_, token1Col_) = _colLiquidatePerfectAfter(
            actualColShares_,
            token0ColAmtPerUnitShares_,
            token1ColAmtPerUnitShares_,
            to_
        );

        // disabling re-entrancy and updating on storage
        vaultVariables = lp_.vaultVariables;

        _validateEth(lp_.initialEth);
    }

    constructor(ConstantViews memory constants_) Internals(constants_) {
        // Note that vaults are deployed by VaultFactory so we somewhat trust the values being passed in
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { ErrorTypes } from "../../errorTypes.sol";
import { FluidVaultOperate } from "../../vaultTypesCommon/coreModule/mainOperate.sol";

/// @notice Fluid "VaultT4" (Vault Type 4). Fluid vault protocol main operate contract. T4 -> Smart collateral | Smart debt
abstract contract Internals is FluidVaultOperate {
    function _colOperateBefore(
        int newColToken0_,
        int newColToken1_,
        int colSharesMinMax_,
        address to_
    ) internal returns (int shares_) {
        if (colSharesMinMax_ > 0) {
            // deposit & minting shares
            if (newColToken0_ < 0 || newColToken1_ < 0 || (newColToken0_ == 0 && newColToken1_ == 0)) {
                // when minting shares, collateral amount should always be > 0 (aka deposit)
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
            shares_ = int(
                SUPPLY.deposit{
                    value: (SUPPLY_TOKEN0 == NATIVE_TOKEN)
                        ? uint(newColToken0_)
                        : (SUPPLY_TOKEN1 == NATIVE_TOKEN)
                            ? uint(newColToken1_)
                            : 0
                }(uint(newColToken0_), uint(newColToken1_), uint(colSharesMinMax_), false)
            );
        } else if (colSharesMinMax_ < 0) {
            // withdrawing and burning shares
            if (newColToken0_ > 0 || newColToken1_ > 0 || (newColToken0_ == 0 && newColToken1_ == 0)) {
                // when burning shares, collateral amount should always be < 0 (aka withdraw)
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
            // withdraw both tokens from DEX protocol and update shares_
            shares_ = -int(SUPPLY.withdraw(uint(-newColToken0_), uint(-newColToken1_), uint(-colSharesMinMax_), to_));
        } else {
            // if 0 then user does not want to deposit or withdraw, hence shares remain 0
            if (newColToken0_ != 0 || newColToken1_ != 0) {
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
        }
    }

    function _debtOperateBefore(
        int newDebtToken0_,
        int newDebtToken1_,
        int debtSharesMinMax_,
        address to_
    ) internal returns (int shares_) {
        if (debtSharesMinMax_ > 0) {
            // borrowing & minting shares
            if (newDebtToken0_ < 0 || newDebtToken1_ < 0 || (newDebtToken0_ == 0 && newDebtToken1_ == 0)) {
                // when minting shares, debt amount should always be > 0 (aka borrow)
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
            // borrowing both tokens from DEX protocol and update shares_
            shares_ = int(BORROW.borrow(uint(newDebtToken0_), uint(newDebtToken1_), uint(debtSharesMinMax_), to_));
        } else if (debtSharesMinMax_ < 0) {
            // paying back and burning shares
            if (newDebtToken0_ > 0 || newDebtToken1_ > 0 || (newDebtToken0_ == 0 && newDebtToken1_ == 0)) {
                // when burning shares, debt amount should always be < 0 (aka payback)
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
            shares_ = -int(
                BORROW.payback{
                    value: (BORROW_TOKEN0 == NATIVE_TOKEN)
                        ? uint(-newDebtToken0_)
                        : (BORROW_TOKEN1 == NATIVE_TOKEN)
                            ? uint(-newDebtToken1_)
                            : 0
                }(uint(-newDebtToken0_), uint(-newDebtToken1_), uint(-debtSharesMinMax_), false)
            );
        } else {
            // if 0 then user does not want to borrow or payback, hence shares remain 0
            if (newDebtToken0_ != 0 || newDebtToken1_ != 0) {
                revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
            }
        }
    }

    function _colOperatePerfectBefore(
        int perfectColShares_,
        int colToken0MinMax_,
        int colToken1MinMax_
    ) internal returns (int newColToken0_, int newColToken1_) {
        if ((colToken0MinMax_ <= 0) || (colToken0MinMax_ <= 0)) {
            // max limit of token should be positive in case of deposit
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }

        (uint token0Amt_, uint token1Amt_) = SUPPLY.depositPerfect{
            value: (SUPPLY_TOKEN0 == NATIVE_TOKEN)
                ? uint(colToken0MinMax_)
                : (SUPPLY_TOKEN1 == NATIVE_TOKEN)
                    ? uint(colToken1MinMax_)
                    : 0
        }(uint(perfectColShares_), uint(colToken0MinMax_), uint(colToken1MinMax_), false);
        newColToken0_ = int(token0Amt_);
        newColToken1_ = int(token1Amt_);
    }

    function _debtOperatePerfectPayback(
        int perfectDebtShares_,
        int debtToken0MinMax_,
        int debtToken1MinMax_
    ) internal returns (int newDebtToken0_, int newDebtToken1_) {
        uint token0Amt_;
        uint token1Amt_;

        if (debtToken0MinMax_ < 0 && debtToken1MinMax_ < 0) {
            (token0Amt_, token1Amt_) = BORROW.paybackPerfect{
                value: (BORROW_TOKEN0 == NATIVE_TOKEN)
                    ? uint(-debtToken0MinMax_)
                    : (BORROW_TOKEN1 == NATIVE_TOKEN)
                        ? uint(-debtToken1MinMax_)
                        : 0
            }(uint(-perfectDebtShares_), uint(-debtToken0MinMax_), uint(-debtToken1MinMax_), false);
        } else if (debtToken0MinMax_ < 0 && debtToken1MinMax_ == 0) {
            // payback only in token0, token1Amt_ remains 0
            (token0Amt_) = BORROW.paybackPerfectInOneToken{
                value: (BORROW_TOKEN0 == NATIVE_TOKEN) ? uint(-debtToken0MinMax_) : 0
            }(uint(-perfectDebtShares_), uint(-debtToken0MinMax_), uint(-debtToken1MinMax_), false);
        } else if (debtToken0MinMax_ == 0 && debtToken1MinMax_ < 0) {
            // payback only in token1, token0Amt_ remains 0
            (token1Amt_) = BORROW.paybackPerfectInOneToken{
                value: (BORROW_TOKEN1 == NATIVE_TOKEN) ? uint(-debtToken1MinMax_) : 0
            }(uint(-perfectDebtShares_), uint(-debtToken0MinMax_), uint(-debtToken1MinMax_), false);
        } else {
            // meaning user sent both amount as >= 0 in case of payback
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }

        newDebtToken0_ = -int(token0Amt_);
        newDebtToken1_ = -int(token1Amt_);
    }

    function _colOperatePerfectAfter(
        int perfectColShares_,
        int colToken0MinMax_,
        int colToken1MinMax_,
        address to_
    ) internal returns (int newColToken0_, int newColToken1_) {
        uint token0Amt_;
        uint token1Amt_;
        if (colToken0MinMax_ < 0 && colToken1MinMax_ < 0) {
            (token0Amt_, token1Amt_) = SUPPLY.withdrawPerfect(
                uint(-perfectColShares_),
                uint(-colToken0MinMax_),
                uint(-colToken1MinMax_),
                to_
            );
        } else if (colToken0MinMax_ < 0 && colToken1MinMax_ == 0) {
            // withdraw only in token0, newColToken1_ remains 0
            (token0Amt_) = SUPPLY.withdrawPerfectInOneToken(
                uint(-perfectColShares_),
                uint(-colToken0MinMax_),
                uint(-colToken1MinMax_),
                to_
            );
        } else if (colToken0MinMax_ == 0 && colToken1MinMax_ < 0) {
            // withdraw only in token1, newColToken0_ remains 0
            (token1Amt_) = SUPPLY.withdrawPerfectInOneToken(
                uint(-perfectColShares_),
                uint(-colToken0MinMax_),
                uint(-colToken1MinMax_),
                to_
            );
        } else {
            // meaning user sent both amount as >= 0 in case of withdraw
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }

        newColToken0_ = -int(token0Amt_);
        newColToken1_ = -int(token1Amt_);
    }

    function _debtOperatePerfectBorrow(
        int perfectDebtShares_,
        int debtToken0MinMax_,
        int debtToken1MinMax_,
        address to_
    ) internal returns (int newDebtToken0_, int newDebtToken1_) {
        if ((debtToken0MinMax_ <= 0) || (debtToken1MinMax_ <= 0)) {
            // min limit of token should be positive in case of borrow
            revert FluidVaultError(ErrorTypes.VaultDex__InvalidOperateAmount);
        }

        (uint token0Amt_, uint token1Amt_) = BORROW.borrowPerfect(
            uint(perfectDebtShares_),
            uint(debtToken0MinMax_),
            uint(debtToken1MinMax_),
            to_
        );
        newDebtToken0_ = int(token0Amt_);
        newDebtToken1_ = int(token1Amt_);
    }

    constructor(ConstantViews memory constants_) FluidVaultOperate(constants_) {}
}

contract FluidVaultT4Operate is Internals {
    struct SmartOperate {
        uint initialEth;
        int colShares;
        int debtShares;
        uint256 vaultVariables;
    }

    /// @notice Performs operations on a vault position
    /// @dev This function allows users to modify their vault position by adjusting collateral and debt
    /// @param nftId_ The ID of the NFT representing the vault position
    /// @param newColToken0_ The change in collateral amount for token0 (positive for deposit, negative for withdrawal)
    /// @param newColToken1_ The change in collateral amount for token1 (positive for deposit, negative for withdrawal)
    /// @param colSharesMinMax_ Min or max collateral shares to mint or burn (positive for deposit, negative for withdrawal)
    /// @param newDebtToken0_ The change in debt amount for token0 (positive for borrowing, negative for repayment)
    /// @param newDebtToken1_ The change in debt amount for token1 (positive for borrowing, negative for repayment)
    /// @param debtSharesMinMax_ Min or max debt shares to burn or mint (positive for borrowing, negative for repayment)
    /// @param to_ The address to receive withdrawn collateral or borrowed tokens (if address(0), defaults to msg.sender)
    /// @return nftId_ The ID of the NFT representing the updated vault position
    /// @return supplyAmt_ Final supply amount (negative if withdrawal occurred)
    /// @return borrowAmt_ Final borrow amount (negative if repayment occurred)
    function operate(
        uint nftId_,
        int newColToken0_,
        int newColToken1_,
        int colSharesMinMax_,
        int newDebtToken0_,
        int newDebtToken1_,
        int debtSharesMinMax_,
        address to_
    )
        external
        payable
        _delegateCallCheck
        returns (
            uint256, // nftId_
            int256, // final supply amount. if - then withdraw
            int256 // final borrow amount. if - then payback
        )
    {
        SmartOperate memory so_;

        so_.vaultVariables = vaultVariables;
        // re-entrancy check
        if (so_.vaultVariables & 1 == 0) {
            // Updating on storage
            vaultVariables = so_.vaultVariables | 1;
        } else {
            revert FluidVaultError(ErrorTypes.Vault__AlreadyEntered);
        }

        so_.initialEth = address(this).balance - msg.value;

        to_ = to_ == address(0) ? msg.sender : to_;

        so_.colShares = _colOperateBefore(newColToken0_, newColToken1_, colSharesMinMax_, to_);
        so_.debtShares = _debtOperateBefore(newDebtToken0_, newDebtToken1_, debtSharesMinMax_, to_);

        // operate will throw is user tried to withdraw excess shares
        // so_.colShares returned after should remain same as before
        // so_.debtShares returned after should remain same as before
        (nftId_, so_.colShares, so_.debtShares, so_.vaultVariables) = _operate(
            nftId_,
            so_.colShares,
            so_.debtShares,
            to_,
            so_.vaultVariables
        );

        // disabling re-entrancy and updating vault variables
        vaultVariables = so_.vaultVariables;

        _validateEth(so_.initialEth);

        return (nftId_, so_.colShares, so_.debtShares);
    }

    struct SmartOperatePerfect {
        uint initialEth;
        int newColToken0;
        int newColToken1;
        int newDebtToken0;
        int newDebtToken1;
        uint vaultVariables;
    }

    /// @notice Performs operations on a vault position with perfect collateral shares
    /// @dev This function allows users to modify their vault position by adjusting collateral and debt
    /// @param nftId_ The ID of the NFT representing the vault position
    /// @param perfectColShares_ The change in collateral shares (positive for deposit, negative for withdrawal)
    /// @param colToken0MinMax_ Min or max collateral amount of token0 to withdraw or deposit (positive for deposit, negative for withdrawal)
    /// @param colToken1MinMax_ Min or max collateral amount of token1 to withdraw or deposit (positive for deposit, negative for withdrawal)
    /// @param perfectDebtShares_ The change in debt shares (positive for borrowing, negative for repayment)
    /// @param debtToken0MinMax_ Min or max debt amount for token0 to borrow or payback (positive for borrowing, negative for repayment)
    /// @param debtToken1MinMax_ Min or max debt amount for token1 to borrow or payback (positive for borrowing, negative for repayment)
    /// @return nftId_ The ID of the NFT representing the updated vault position
    /// @return r_ int256 array of return values:
    ///              0 - final col shares amount (can only change on max withdrawal)
    ///              1 - token0 deposit or withdraw amount
    ///              2 - token1 deposit or withdraw amount
    ///              3 - final debt shares amount (can only change on max payback)
    ///              4 - token0 borrow or payback amount
    ///              5 - token1 borrow or payback amount
    function operatePerfect(
        uint nftId_,
        int perfectColShares_,
        int colToken0MinMax_,
        int colToken1MinMax_,
        int perfectDebtShares_,
        int debtToken0MinMax_,
        int debtToken1MinMax_,
        address to_
    )
        external
        payable
        _delegateCallCheck
        returns (
            uint256, // nftId_
            int256[] memory r_
        )
    {
        SmartOperatePerfect memory sop_;
        r_ = new int256[](6);

        sop_.vaultVariables = vaultVariables;
        // re-entrancy check
        if (sop_.vaultVariables & 1 == 0) {
            // Updating on storage
            vaultVariables = sop_.vaultVariables | 1;
        } else {
            revert FluidVaultError(ErrorTypes.Vault__AlreadyEntered);
        }

        sop_.initialEth = address(this).balance - msg.value;

        to_ = to_ == address(0) ? msg.sender : to_;

        if (perfectColShares_ > 0) {
            (sop_.newColToken0, sop_.newColToken1) = _colOperatePerfectBefore(
                perfectColShares_,
                colToken0MinMax_,
                colToken1MinMax_
            );
        }

        // operate will throw if user tried to withdraw excess shares
        // if max withdrawal then perfectColShares_ will change from type(int).min to total user's col shares
        // if max payback then perfectDebtShares_ will change from type(int).min to total user's debt shares
        (nftId_, perfectColShares_, perfectDebtShares_, sop_.vaultVariables) = _operate(
            nftId_,
            perfectColShares_,
            perfectDebtShares_,
            to_,
            sop_.vaultVariables
        );

        if (perfectColShares_ < 0) {
            (sop_.newColToken0, sop_.newColToken1) = _colOperatePerfectAfter(
                perfectColShares_,
                colToken0MinMax_,
                colToken1MinMax_,
                to_
            );
        }

        // payback back after operate because user might want to payback max and in that case below function won't work
        if (perfectDebtShares_ < 0) {
            (sop_.newDebtToken0, sop_.newDebtToken1) = _debtOperatePerfectPayback(
                perfectDebtShares_,
                debtToken0MinMax_,
                debtToken1MinMax_
            );
        } else if (perfectDebtShares_ > 0) {
            (sop_.newDebtToken0, sop_.newDebtToken1) = _debtOperatePerfectBorrow(
                perfectDebtShares_,
                debtToken0MinMax_,
                debtToken1MinMax_,
                to_
            );
        }

        r_[0] = perfectColShares_;
        r_[1] = sop_.newColToken0;
        r_[2] = sop_.newColToken1;
        r_[3] = perfectDebtShares_;
        r_[4] = sop_.newDebtToken0;
        r_[5] = sop_.newDebtToken1;

        // disabling re-entrancy and updating vault variables
        vaultVariables = sop_.vaultVariables;

        _validateEth(sop_.initialEth);

        return (nftId_, r_);
    }

    constructor(ConstantViews memory constants_) Internals(constants_) {}
}

contracts/protocols/vault/vaultTypesComm.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

abstract contract Events {
    /// @notice emitted when the collateral factor config is updated
    event LogUpdateCollateralFactor(uint collateralFactor_);

    /// @notice emitted when the liquidation threshold config is updated
    event LogUpdateLiquidationThreshold(uint liquidationThreshold_);

    /// @notice emitted when the liquidation max limit config is updated
    event LogUpdateLiquidationMaxLimit(uint liquidationMaxLimit_);

    /// @notice emitted when the withdrawal gap config is updated
    event LogUpdateWithdrawGap(uint withdrawGap_);

    /// @notice emitted when the liquidation penalty config is updated
    event LogUpdateLiquidationPenalty(uint liquidationPenalty_);

    /// @notice emitted when the borrow fee config is updated
    event LogUpdateBorrowFee(uint borrowFee_);

    /// @notice emitted when the oracle is updated
    event LogUpdateOracle(uint indexed deploymentNonce_, address indexed newOracle_);

    /// @notice emitted when the allowed rebalancer is updated
    event LogUpdateRebalancer(address indexed newRebalancer_);

    /// @notice emitted when funds are rescued
    event LogRescueFunds(address indexed token_);

    /// @notice emitted when dust debt is absorbed for `nftIds_`
    event LogAbsorbDustDebt(uint256[] nftIds_, uint256 absorbedDustDebt_);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { IFluidOracle } from "../../../../oracle/fluidOracle.sol";

import { Variables } from "../common/variables.sol";
import { Events } from "./events.sol";
import { ErrorTypes } from "../../errorTypes.sol";
import { Error } from "../../error.sol";
import { IFluidVault } from "../../interfaces/iVault.sol";
import { BigMathMinified } from "../../../../libraries/bigMathMinified.sol";
import { TickMath } from "../../../../libraries/tickMath.sol";
import { AddressCalcs } from "../../../../libraries/addressCalcs.sol";
import { SafeTransfer } from "../../../../libraries/safeTransfer.sol";

/// @notice Fluid Vault protocol Admin Module contract.
///         Implements admin related methods to set configs such as liquidation params, rates
///         oracle address etc.
///         Methods are limited to be called via delegateCall only. Vault CoreModule ("VaultT1" contract)
///         is expected to call the methods implemented here after checking the msg.sender is authorized.
///         All methods update the exchange prices in storage before changing configs.
abstract contract FluidVaultAdmin is Variables, Events, Error {
    uint internal constant X8 = 0xff;
    uint internal constant X10 = 0x3ff;
    uint internal constant X15 = 0x7fff;
    uint internal constant X16 = 0xffff;
    uint internal constant X19 = 0x7ffff;
    uint internal constant X24 = 0xffffff;
    uint internal constant X30 = 0x3fffffff;
    uint internal constant X64 = 0xffffffffffffffff;
    address internal constant NATIVE_TOKEN = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    address internal immutable addressThis;

    constructor() {
        addressThis = address(this);
    }

    modifier _verifyCaller() {
        if (address(this) == addressThis) {
            revert FluidVaultError(ErrorTypes.VaultAdmin__OnlyDelegateCallAllowed);
        }
        _;
    }

    /// @dev updates exchange price on storage, called on all admin methods in combination with _verifyCaller modifier so
    /// only called by authorized delegatecall
    modifier _updateExchangePrice() {
        IFluidVault(address(this)).updateExchangePricesOnStorage();
        _;
    }

    function _checkLiquidationMaxLimitAndPenalty(uint liquidationMaxLimit_, uint liquidationPenalty_) internal pure {
        // liquidation max limit with penalty should not go above 99.7%
        // As liquidation with penalty can happen from liquidation Threshold to max limit
        // If it goes above 100% than that means liquidator is getting more collateral than user's available
        if ((liquidationMaxLimit_ + liquidationPenalty_) > 9970) {
            revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);
        }
    }

    /// @notice updates the collateral factor to `collateralFactor_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateCollateralFactor(uint collateralFactor_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateCollateralFactor(collateralFactor_);

        uint vaultVariables2_ = vaultVariables2;
        uint liquidationThreshold_ = ((vaultVariables2_ >> 42) & X10);

        collateralFactor_ = collateralFactor_ / 10;

        if (collateralFactor_ >= liquidationThreshold_) revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2_ & 0xfffffffffffffffffffffffffffffffffffffffffffffffffffffc00ffffffff) |
            (collateralFactor_ << 32);
    }

    /// @notice updates the liquidation threshold to `liquidationThreshold_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateLiquidationThreshold(uint liquidationThreshold_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateLiquidationThreshold(liquidationThreshold_);

        uint vaultVariables2_ = vaultVariables2;
        uint collateralFactor_ = ((vaultVariables2_ >> 32) & X10);
        uint liquidationMaxLimit_ = ((vaultVariables2_ >> 52) & X10);

        liquidationThreshold_ = liquidationThreshold_ / 10;

        if ((collateralFactor_ >= liquidationThreshold_) || (liquidationThreshold_ >= liquidationMaxLimit_))
            revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2_ & 0xfffffffffffffffffffffffffffffffffffffffffffffffffff003ffffffffff) |
            (liquidationThreshold_ << 42);
    }

    /// @notice updates the liquidation max limit to `liquidationMaxLimit_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateLiquidationMaxLimit(uint liquidationMaxLimit_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateLiquidationMaxLimit(liquidationMaxLimit_);

        uint vaultVariables2_ = vaultVariables2;
        uint liquidationThreshold_ = ((vaultVariables2_ >> 42) & X10);
        uint liquidationPenalty_ = ((vaultVariables2_ >> 72) & X10);

        // both are in 1e2 decimals (1e2 = 1%)
        _checkLiquidationMaxLimitAndPenalty(liquidationMaxLimit_, liquidationPenalty_);

        liquidationMaxLimit_ = liquidationMaxLimit_ / 10;

        if (liquidationThreshold_ >= liquidationMaxLimit_)
            revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2_ & 0xffffffffffffffffffffffffffffffffffffffffffffffffc00fffffffffffff) |
            (liquidationMaxLimit_ << 52);
    }

    /// @notice updates the withdrawal gap to `withdrawGap_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateWithdrawGap(uint withdrawGap_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateWithdrawGap(withdrawGap_);

        withdrawGap_ = withdrawGap_ / 10;

        // withdrawGap must not be > 100%
        if (withdrawGap_ > 1000) revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2 & 0xffffffffffffffffffffffffffffffffffffffffffffff003fffffffffffffff) |
            (withdrawGap_ << 62);
    }

    /// @notice updates the liquidation penalty to `liquidationPenalty_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateLiquidationPenalty(uint liquidationPenalty_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateLiquidationPenalty(liquidationPenalty_);

        uint vaultVariables2_ = vaultVariables2;
        uint liquidationMaxLimit_ = ((vaultVariables2_ >> 52) & X10);

        // Converting liquidationMaxLimit_ in 1e2 decimals (1e2 = 1%)
        _checkLiquidationMaxLimitAndPenalty((liquidationMaxLimit_ * 10), liquidationPenalty_);

        if (liquidationPenalty_ > X10) revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2_ & 0xfffffffffffffffffffffffffffffffffffffffffffc00ffffffffffffffffff) |
            (liquidationPenalty_ << 72);
    }

    /// @notice updates the borrow fee to `borrowFee_`. Input in 1e2 (1% = 100, 100% = 10_000).
    function updateBorrowFee(uint borrowFee_) public _updateExchangePrice _verifyCaller {
        emit LogUpdateBorrowFee(borrowFee_);

        if (borrowFee_ > X10) revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        vaultVariables2 =
            (vaultVariables2 & 0xfffffffffffffffffffffffffffffffffffffffff003ffffffffffffffffffff) |
            (borrowFee_ << 82);
    }

    /// @notice updates the Vault oracle to `newOracleNonce_`. Must implement the FluidOracle interface.
    function updateOracle(uint newOracleNonce_) public _updateExchangePrice _verifyCaller {
        if (newOracleNonce_ > X30) revert FluidVaultError(ErrorTypes.VaultAdmin__ValueAboveLimit);

        // masking to remove old oracle and keep all the other values intact
        vaultVariables2 =
            (vaultVariables2 & 0xfffffffffffffffffffffffffffffffffc0000000fffffffffffffffffffffff) |
            (newOracleNonce_ << 92);

        IFluidVault.ConstantViews memory c_ = IFluidVault(address(this)).constantsView();

        address oracle_ = AddressCalcs.addressCalc(c_.deployer, newOracleNonce_);

        // checking if oracle address follows the standard
        IFluidOracle(oracle_).getExchangeRateOperate();
        IFluidOracle(oracle_).getExchangeRateLiquidate();

        emit LogUpdateOracle(newOracleNonce_, oracle_);
    }

    /// @notice updates the allowed rebalancer to `newRebalancer_`.
    function updateRebalancer(address newRebalancer_) public _updateExchangePrice _verifyCaller {
        if (newRebalancer_ == address(0)) revert FluidVaultError(ErrorTypes.VaultAdmin__AddressZeroNotAllowed);

        rebalancer = newRebalancer_;

        emit LogUpdateRebalancer(newRebalancer_);
    }

    /// @notice sends any potentially stuck funds to Liquidity contract.
    /// @dev this contract never holds any funds as all operations send / receive funds from user <-> Liquidity.
    function rescueFunds(address token_) external _verifyCaller {
        if (token_ == NATIVE_TOKEN) {
            SafeTransfer.safeTransferNative(IFluidVault(address(this)).LIQUIDITY(), address(this).balance);
        } else {
            SafeTransfer.safeTransfer(
                token_,
                IFluidVault(address(this)).LIQUIDITY(),
                IERC20(token_).balanceOf(address(this))
            );
        }

        emit LogRescueFunds(token_);
    }

    /// @notice absorbs accumulated dust debt
    /// @dev in decades if a lot of positions are 100% liquidated (aka absorbed) then dust debt can mount up
    /// which is basically sort of an extra revenue for the protocol.
    //
    // this function might never come in use that's why adding it in admin module
    function absorbDustDebt(uint[] memory nftIds_) public _verifyCaller {
        uint256 vaultVariables_ = vaultVariables;
        // re-entrancy check
        if (vaultVariables_ & 1 == 0) {
            // Updating on storage
            vaultVariables = vaultVariables_ | 1;
        } else {
            revert FluidVaultError(ErrorTypes.Vault__AlreadyEntered);
        }

        uint nftId_;
        uint posData_;
        int posTick_;
        uint tickId_;
        uint posCol_;
        uint posDebt_;
        uint posDustDebt_;
        uint tickData_;

        uint absorbedDustDebt_ = absorbedDustDebt;

        for (uint i = 0; i < nftIds_.length; ) {
            nftId_ = nftIds_[i];
            if (nftId_ == 0) {
                revert FluidVaultError(ErrorTypes.VaultAdmin__NftIdShouldBeNonZero);
            }

            // user's position data
            posData_ = positionData[nftId_];

            if (posData_ == 0) {
                revert FluidVaultError(ErrorTypes.VaultAdmin__NftNotOfThisVault);
            }

            posCol_ = (posData_ >> 45) & X64;
            // Converting big number into normal number
            posCol_ = (posCol_ >> 8) << (posCol_ & X8);

            posDustDebt_ = (posData_ >> 109) & X64;
            // Converting big number into normal number
            posDustDebt_ = (posDustDebt_ >> 8) << (posDustDebt_ & X8);

            if (posDustDebt_ == 0) {
                revert FluidVaultError(ErrorTypes.VaultAdmin__DustDebtIsZero);
            }

            // borrow position (has collateral & debt)
            posTick_ = posData_ & 2 == 2 ? int((posData_ >> 2) & X19) : -int((posData_ >> 2) & X19);
            tickId_ = (posData_ >> 21) & X24;

            posDebt_ = (TickMath.getRatioAtTick(int24(posTick_)) * posCol_) >> 96;

            // Tick data from user's tick
            tickData_ = tickData[posTick_];

            // Checking if tick is liquidated OR if the total IDs of tick is greater than user's tick ID
            if (((tickData_ & 1) == 1) || (((tickData_ >> 1) & X24) > tickId_)) {
                // User got liquidated
                (, posDebt_, , , ) = IFluidVault(address(this)).fetchLatestPosition(
                    posTick_,
                    tickId_,
                    posDebt_,
                    tickData_
                );
                if (posDebt_ > 0) {
                    revert FluidVaultError(ErrorTypes.VaultAdmin__FinalDebtShouldBeZero);
                }
                // absorbing user's debt as it's 100% or almost 100% liquidated
                absorbedDustDebt_ = absorbedDustDebt_ + posDustDebt_;
                // making position as supply only
                positionData[nftId_] = 1;
            } else {
                revert FluidVaultError(ErrorTypes.VaultAdmin__NftNotLiquidated);
            }

            unchecked {
                i++;
            }
        }

        if (absorbedDustDebt_ == 0) {
            revert FluidVaultError(ErrorTypes.VaultAdmin__AbsorbedDustDebtIsZero);
        }

        uint totalBorrow_ = (vaultVariables_ >> 146) & X64;
        // Converting big number into normal number
        totalBorrow_ = (totalBorrow_ >> 8) << (totalBorrow_ & X8);
        // note: by default dust debt is not added into total borrow but on 100% liquidation (aka absorb) dust debt equivalent
        // is removed from total borrow so adding it back again here
        totalBorrow_ = totalBorrow_ + absorbedDustDebt_;
        totalBorrow_ = BigMathMinified.toBigNumber(totalBorrow_, 56, 8, BigMathMinified.ROUND_UP);

        // adding absorbed dust debt to total borrow so it will get included in the next rebalancing.
        // there is some fuzziness here as when the position got fully liquidated (aka absorbed) the exchange price was different
        // than what it'll be now. The fuzziness which will be extremely small so we can ignore it
        // updating on storage
        vaultVariables =
            (vaultVariables_ & 0xfffffffffffc0000000000000003ffffffffffffffffffffffffffffffffffff) |
            (totalBorrow_ << 146);

        // updating on storage
        absorbedDustDebt = 0;

        emit LogAbsorbDustDebt(nftIds_, absorbedDustDebt_);
    }
}

