// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

library ErrorTypes {
    /***********************************|
    |           FlashLender             | 
    |__________________________________*/

    /// @notice Thrown when a function encounters or is provided with an address set to the zero address.
    uint256 internal constant FlashLender__ZeroAddress = 80001;

    /// @notice Thrown when the requested flash loan amount exceeds the allowed maximum.
    uint256 internal constant FlashLender__MaxFlashLoanAmountExceeded = 80002;

    /// @notice Thrown when a function returns an unexpected value.
    uint256 internal constant FlashLender__InvalidReturnValue = 80003;

    /// @notice Thrown when an unauthorized action is attempted.
    uint256 internal constant FlashLender__Unauthorized = 80004;

    /// @notice Thrown when an operation involves a token that isn't supported.
    uint256 internal constant FlashLender__TokenNotSupported = 80005;

    /// @notice Thrown when a token is already being borrowed by flashloan protocol.
    uint256 internal constant FlashLender__DuplicateBorrow = 80006;
}

