// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.21 <=0.8.29;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { Address } from "@openzeppelin/contracts/utils/Address.sol";

import { Variables } from "../common/variables.sol";

interface IZtakingPool {
    ///@notice Stake a specified amount of a particular supported token into the Ztaking Pool
    ///@param _token The token to deposit/stake in the Ztaking Pool
    ///@param _for The user to deposit/stake on behalf of
    ///@param _amount The amount of token to deposit/stake into the Ztaking Pool
    function depositFor(address _token, address _for, uint256 _amount) external;

    ///@notice Withdraw a specified amount of a particular supported token previously staked into the Ztaking Pool
    ///@param _token The token to withdraw from the Ztaking Pool
    ///@param _amount The amount of token to withdraw from the Ztaking Pool
    function withdraw(address _token, uint256 _amount) external;

    function balance(address token_, address staker_) external view returns (uint256);
}

/// @dev This contract is for Ethereum mainnet only!
contract FluidLiquidityZircuitTransferModuleMainnet is Variables {
    address internal constant LIQUIDITY = 0x52Aa899454998Be5b000Ad077a46Bbe360F4e497;

    IERC20 internal constant WEETH = IERC20(0xCd5fE23C85820F7B72D0926FC9b05b43E359b7ee);
    IERC20 internal constant WEETHS = IERC20(0x917ceE801a67f933F2e6b33fC0cD1ED2d5909D88);
    IZtakingPool internal constant ZIRCUIT = IZtakingPool(0xF047ab4c75cebf0eB9ed34Ae2c186f3611aEAfa6);

    /// @dev Returns the current admin (governance).
    function _getGovernanceAddr() internal view returns (address governance_) {
        assembly {
            governance_ := sload(GOVERNANCE_SLOT)
        }
    }

    /// @notice deposit all WEETH funds to Zircuit and sets approved allowance to max uint256.
    /// @dev Only delegate callable on Liquidity, by Governance
    function depositZircuitWeETH() external {
        if (_getGovernanceAddr() != msg.sender || address(this) != LIQUIDITY) {
            revert();
        }

        SafeERC20.safeApprove(WEETH, address(ZIRCUIT), type(uint256).max);

        ZIRCUIT.depositFor(address(WEETH), address(this), WEETH.balanceOf(address(this)));
    }

    /// @notice withdraw all WEETH funds from Zircuit and sets approved allowance to 0.
    /// @dev Only delegate callable on Liquidity, Governance and Guardians (for emergency)
    function withdrawZircuitWeETH() external {
        if ((_isGuardian[msg.sender] & 1 != 1 && _getGovernanceAddr() != msg.sender) || address(this) != LIQUIDITY) {
            revert();
        }

        ZIRCUIT.withdraw(address(WEETH), ZIRCUIT.balance(address(WEETH), address(this)));

        // remove approval
        SafeERC20.safeApprove(WEETH, address(ZIRCUIT), 0);
    }

    /// @notice deposit all WEETHS funds to Zircuit and sets approved allowance to max uint256.
    /// @dev Only delegate callable on Liquidity, by Governance
    function depositZircuitWeETHs() external {
        if (_getGovernanceAddr() != msg.sender || address(this) != LIQUIDITY) {
            revert();
        }

        SafeERC20.safeApprove(WEETHS, address(ZIRCUIT), type(uint256).max);

        ZIRCUIT.depositFor(address(WEETHS), address(this), WEETHS.balanceOf(address(this)));
    }

    /// @notice withdraw all WEETHS funds from Zircuit and sets approved allowance to 0.
    /// @dev Only delegate callable on Liquidity, Governance and Guardians (for emergency)
    function withdrawZircuitWeETHs() external {
        if ((_isGuardian[msg.sender] & 1 != 1 && _getGovernanceAddr() != msg.sender) || address(this) != LIQUIDITY) {
            revert();
        }

        ZIRCUIT.withdraw(address(WEETHS), ZIRCUIT.balance(address(WEETHS), address(this)));

        // remove approval
        SafeERC20.safeApprove(WEETHS, address(ZIRCUIT), 0);
    }
}

contracts/mocks/mockProtocol.sol.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.21 <=0.8.29;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { FluidLiquidityUserModule } from "../liquidity/userModule/main.sol";
import { IFluidLiquidity } from "../liquidity/interfaces/iLiquidity.sol";

/// @title    Mock Protocol
/// @notice   Mock protocol for testing, implements:
///           function liquidityCallback(address token_, uint256 amount_, bytes calldata data_) external;
///           This callback method MUST transferFrom data_ decoded from address to the liquidity contract
contract MockProtocol {
    using SafeERC20 for IERC20;

    /// @dev address that is mapped to the chain native token
    address internal constant NATIVE_TOKEN_ADDRESS = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;

    address public immutable liquidityContract;

    /// @notice thrown when liquidity callback is called for a native token operation
    error MockProtocol__UnexpectedLiquidityCallback();

    bool transferInsufficientMode = false;
    bool transferExcessMode = false;
    bool reentrancyFromCallback = false;
    address transferFromAddress;

    /// @notice   Construct a new MockProtocol
    /// @param    liquidityContract_ The address of the liquidity contract
    constructor(address liquidityContract_) {
        liquidityContract = liquidityContract_;
    }

    receive() external payable {}

    function setTransferInsufficientMode(bool transferInsufficientMode_) public {
        transferInsufficientMode = transferInsufficientMode_;
    }

    function setTransferExcessMode(bool transferExcessMode_) public {
        transferExcessMode = transferExcessMode_;
    }

    function setReentrancyFromCallback(bool reentrancyFromCallback_) public {
        reentrancyFromCallback = reentrancyFromCallback_;
    }

    function setTransferFromAddress(address transferFromAddress_) public {
        transferFromAddress = transferFromAddress_;
    }

    /// @notice   Mock liquidity callback
    /// @param    token_ The token being transferred
    /// @param    amount_ The amount being transferred
    function liquidityCallback(address token_, uint256 amount_, bytes memory data_) external {
        if (reentrancyFromCallback) {
            // call operate with some random values (should not matter as it reverts anyway)
            IFluidLiquidity(liquidityContract).operate(
                token_,
                10,
                0,
                address(0),
                address(0),
                abi.encode(address(this))
            );
        }

        if (token_ == NATIVE_TOKEN_ADDRESS) {
            revert MockProtocol__UnexpectedLiquidityCallback();
        }

        address from_;
        if (transferFromAddress == address(0)) {
            // take the last 20 bytes of data_ and decode them to address. Gives more flexibility in type of
            // data that can be passed in to Liquidity at mock calls while ensuring mock Protocol can do what it
            // is supposed to do: transfer amount of token to liquidity.
            assembly {
                from_ := mload(
                    add(
                        // add padding for length as present for dynamic arrays in memory
                        add(data_, 32),
                        // assembly expects address with leading zeros / left padded so need to use 32 as length here
                        sub(mload(data_), 32)
                    )
                )
            }
        } else {
            from_ = transferFromAddress;
        }

        if (amount_ > 0) {
            if (transferExcessMode) {
                amount_ += (amount_ * 10101) / 10000; // max excess is 1%
            } else if (transferInsufficientMode) {
                amount_ -= 1;
            }
        }

        if (from_ == address(this)) {
            // use approve and transferFrom for more consistent testing of methods called
            // (always transferFrom instead of transfer)
            IERC20(token_).safeApprove(address(this), amount_);
            IERC20(token_).safeTransferFrom(address(this), liquidityContract, amount_);
        } else {
            IERC20(token_).safeTransferFrom(from_, liquidityContract, amount_);
        }
    }

    /// @notice   Proxy method for executing `operate` on the liquidity contract
    function operate(
        address token_,
        int256 supplyAmount_,
        int256 borrowAmount_,
        address withdrawTo_,
        address borrowTo_,
        bytes calldata callbackData_
    ) external payable returns (uint256 supplyExchangePrice_, uint256 borrowExchangePrice_) {
        uint256 valueAmount = msg.value;

        if (valueAmount > 0) {
            if (transferExcessMode) {
                valueAmount += (valueAmount * 10101) / 10000; // max excess is 1%
            } else if (transferInsufficientMode) {
                valueAmount -= 1;
            }
        }

        return
            FluidLiquidityUserModule(liquidityContract).operate{ value: valueAmount }(
                token_,
                supplyAmount_,
                borrowAmount_,
                withdrawTo_,
                borrowTo_,
                callbackData_
            );
    }

    function readFromStorage(bytes32 slot_) external view returns (uint256 result_) {
        result_ = IFluidLiquidity(liquidityContract).readFromStorage(slot_);
    }
}

