    constructor(
        string memory infoName_,
        uint8 colTokenDecimals_,
        uint8 debtTokenDecimals_,
        IERC4626 erc4626Feed_
    ) PegOracleImpl(colTokenDecimals_, debtTokenDecimals_, erc4626Feed_) FluidOracle(infoName_) {}

