//SPDX-License-Identifier: MIT
pragma solidity 0.8.21;

import { Structs as LiquidityStructs } from "../../../periphery/resolvers/liquidity/structs.sol";

interface IFluidLiquidityResolver {
    /// @notice gets the `revenueAmount_` for a `token_`.
    function getRevenue(address token_) external view returns (uint256 revenueAmount_);

    /// @notice address of contract that gets sent the revenue. Configurable by governance
    function getRevenueCollector() external view returns (address);

    /// @notice Liquidity contract paused status: status = 1 -> normal. status = 2 -> paused.
    function getStatus() external view returns (uint256);

    /// @notice checks if `auth_` is an allowed auth on Liquidity.
    /// Auths can set most config values. E.g. contracts that automate certain flows like e.g. adding a new fToken.
    /// Governance can add/remove auths. Governance is auth by default.
    function isAuth(address auth_) external view returns (uint256);

    /// @notice checks if `guardian_` is an allowed Guardian on Liquidity.
    /// Guardians can pause lower class users.
    /// Governance can add/remove guardians. Governance is guardian by default.
    function isGuardian(address guardian_) external view returns (uint256);

    /// @notice gets user class for `user_`. Class defines which protocols can be paused by guardians.
    /// Currently there are 2 classes: 0 can be paused by guardians. 1 cannot be paused by guardians.
    /// New protocols are added as class 0 and will be upgraded to 1 over time.
    function getUserClass(address user_) external view returns (uint256);

    /// @notice gets exchangePricesAndConfig packed uint256 storage slot for `token_`.
    function getExchangePricesAndConfig(address token_) external view returns (uint256);

    /// @notice gets rateConfig packed uint256 storage slot for `token_`.
    function getRateConfig(address token_) external view returns (uint256);

    /// @notice gets totalAmounts packed uint256 storage slot for `token_`.
    function getTotalAmounts(address token_) external view returns (uint256);

    /// @notice gets configs2 packed uint256 storage slot for `token_`.
    function getConfigs2(address token_) external view returns (uint256);

    /// @notice gets userSupply data packed uint256 storage slot for `user_` and `token_`.
    function getUserSupply(address user_, address token_) external view returns (uint256);

    /// @notice gets userBorrow data packed uint256 storage slot for `user_` and `token_`.
    function getUserBorrow(address user_, address token_) external view returns (uint256);

    /// @notice returns all `listedTokens_` at the Liquidity contract. Once configured, a token can never be removed.
    function listedTokens() external view returns (address[] memory listedTokens_);

    /// @notice get the Rate config data `rateData_` for a `token_` compiled from the packed uint256 rateConfig storage slot
    function getTokenRateData(address token_) external view returns (LiquidityStructs.RateData memory rateData_);

    /// @notice get the Rate config datas `rateDatas_` for multiple `tokens_` compiled from the packed uint256 rateConfig storage slot
    function getTokensRateData(
        address[] calldata tokens_
    ) external view returns (LiquidityStructs.RateData[] memory rateDatas_);

    /// @notice returns general data for `token_` such as rates, exchange prices, utilization, fee, total amounts etc.
    function getOverallTokenData(
        address token_
    ) external view returns (LiquidityStructs.OverallTokenData memory overallTokenData_);

    /// @notice returns general data for multiple `tokens_` such as rates, exchange prices, utilization, fee, total amounts etc.
    function getOverallTokensData(
        address[] calldata tokens_
    ) external view returns (LiquidityStructs.OverallTokenData[] memory overallTokensData_);

    /// @notice returns general data for all `listedTokens()` such as rates, exchange prices, utilization, fee, total amounts etc.
    function getAllOverallTokensData()
        external
        view
        returns (LiquidityStructs.OverallTokenData[] memory overallTokensData_);

    /// @notice returns `user_` supply data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for `token_`
    function getUserSupplyData(
        address user_,
        address token_
    )
        external
        view
        returns (
            LiquidityStructs.UserSupplyData memory userSupplyData_,
            LiquidityStructs.OverallTokenData memory overallTokenData_
        );

    /// @notice returns `user_` supply data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for multiple `tokens_`
    function getUserMultipleSupplyData(
        address user_,
        address[] calldata tokens_
    )
        external
        view
        returns (
            LiquidityStructs.UserSupplyData[] memory userSuppliesData_,
            LiquidityStructs.OverallTokenData[] memory overallTokensData_
        );

    /// @notice returns `user_` borrow data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for `token_`
    function getUserBorrowData(
        address user_,
        address token_
    )
        external
        view
        returns (
            LiquidityStructs.UserBorrowData memory userBorrowData_,
            LiquidityStructs.OverallTokenData memory overallTokenData_
        );

    /// @notice returns `user_` borrow data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for multiple `tokens_`
    function getUserMultipleBorrowData(
        address user_,
        address[] calldata tokens_
    )
        external
        view
        returns (
            LiquidityStructs.UserBorrowData[] memory userBorrowingsData_,
            LiquidityStructs.OverallTokenData[] memory overallTokensData_
        );

    /// @notice returns `user_` supply data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for multiple `supplyTokens_`
    ///     and returns `user_` borrow data and general data (such as rates, exchange prices, utilization, fee, total amounts etc.) for multiple `borrowTokens_`
    function getUserMultipleBorrowSupplyData(
        address user_,
        address[] calldata supplyTokens_,
        address[] calldata borrowTokens_
    )
        external
        view
        returns (
            LiquidityStructs.UserSupplyData[] memory userSuppliesData_,
            LiquidityStructs.OverallTokenData[] memory overallSupplyTokensData_,
            LiquidityStructs.UserBorrowData[] memory userBorrowingsData_,
            LiquidityStructs.OverallTokenData[] memory overallBorrowTokensData_
        );
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { Structs as AdminModuleStructs } from "../../../liquidity/adminModule/structs.sol";

abstract contract Structs {
    struct RateData {
        uint256 version;
        AdminModuleStructs.RateDataV1Params rateDataV1;
        AdminModuleStructs.RateDataV2Params rateDataV2;
    }

    struct OverallTokenData {
        uint256 borrowRate;
        uint256 supplyRate;
        uint256 fee; // revenue fee
        uint256 lastStoredUtilization;
        uint256 storageUpdateThreshold;
        uint256 lastUpdateTimestamp;
        uint256 supplyExchangePrice;
        uint256 borrowExchangePrice;
        uint256 supplyRawInterest;
        uint256 supplyInterestFree;
        uint256 borrowRawInterest;
        uint256 borrowInterestFree;
        uint256 totalSupply;
        uint256 totalBorrow;
        uint256 revenue;
        uint256 maxUtilization; // maximum allowed utilization
        RateData rateData;
    }

    // amounts are always in normal (for withInterest already multiplied with exchange price)
    struct UserSupplyData {
        bool modeWithInterest; // true if mode = with interest, false = without interest
        uint256 supply; // user supply amount
        // the withdrawal limit (e.g. if 10% is the limit, and 100M is supplied, it would be 90M)
        uint256 withdrawalLimit;
        uint256 lastUpdateTimestamp;
        uint256 expandPercent; // withdrawal limit expand percent in 1e2
        uint256 expandDuration; // withdrawal limit expand duration in seconds
        uint256 baseWithdrawalLimit;
        // the current actual max withdrawable amount (e.g. if 10% is the limit, and 100M is supplied, it would be 10M)
        uint256 withdrawableUntilLimit;
        uint256 withdrawable; // actual currently withdrawable amount (supply - withdrawal Limit) & considering balance
    }

    // amounts are always in normal (for withInterest already multiplied with exchange price)
    struct UserBorrowData {
        bool modeWithInterest; // true if mode = with interest, false = without interest
        uint256 borrow; // user borrow amount
        uint256 borrowLimit;
        uint256 lastUpdateTimestamp;
        uint256 expandPercent;
        uint256 expandDuration;
        uint256 baseBorrowLimit;
        uint256 maxBorrowLimit;
        uint256 borrowableUntilLimit; // borrowable amount until any borrow limit (incl. max utilization limit)
        uint256 borrowable; // actual currently borrowable amount (borrow limit - already borrowed) & considering balance, max utilization
        uint256 borrowLimitUtilization; // borrow limit for `maxUtilization`
    }
}

contracts/periphery/resolvers/smartLendi.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import { FluidDexResolver } from "../dex/main.sol";
import { FluidSmartLendingFactory } from "../../../protocols/dex/smartLending/factory/main.sol";
import { FluidSmartLending } from "../../../protocols/dex/smartLending/main.sol";
import { Structs } from "./structs.sol";
import { Structs as DexResolverStructs } from "../dex/structs.sol";

/// @notice Fluid Smart Lending resolver
/// Implements various view-only methods to give easy access to Smart Lending protocol data.
contract FluidSmartLendingResolver is Structs {
    uint256 internal constant SECONDS_PER_YEAR = 365 days;

    FluidDexResolver public immutable DEX_RESOLVER;

    FluidSmartLendingFactory public immutable SMART_LENDING_FACTORY;

    /// @notice thrown if an input param address is zero
    error FluidSmartLendingResolver__AddressZero();

    constructor(FluidDexResolver dexResolver_, FluidSmartLendingFactory smartLendingFactory_) {
        if (address(dexResolver_) == address(0) || address(smartLendingFactory_) == address(0)) {
            revert FluidSmartLendingResolver__AddressZero();
        }

        DEX_RESOLVER = dexResolver_;
        SMART_LENDING_FACTORY = smartLendingFactory_;
    }

    /// @notice List of all existing SmartLending tokens
    function getAllSmartLendingAddresses() public view returns (address[] memory) {
        return SMART_LENDING_FACTORY.allTokens();
    }

    /// @notice Get the address of a SmartLending for a certain dexId. address zero if no SmartLending exists for the dex.
    function getSmartLendingAddress(uint dexId_) public view returns (address smartLending) {
        return SMART_LENDING_FACTORY.getSmartLendingAddress(dexId_);
    }

    /// @notice Get the entire data for a SmartLending, EXCEPT underlying DexEntireData. use write method for that.
    /// @param smartLending_ The address of the SmartLending
    /// @return data_ A struct containing all the data for the SmartLending
    function getSmartLendingEntireViewData(
        address payable smartLending_
    ) public view returns (SmartLendingEntireData memory data_) {
        data_.smartLending = smartLending_;
        data_.name = FluidSmartLending(smartLending_).name();
        data_.symbol = FluidSmartLending(smartLending_).symbol();
        data_.decimals = 18;
        data_.totalSupply = uint256(FluidSmartLending(smartLending_).totalSupply());

        data_.token0 = FluidSmartLending(smartLending_).TOKEN0();
        data_.token1 = FluidSmartLending(smartLending_).TOKEN1();
        data_.dex = address(FluidSmartLending(smartLending_).DEX());

        data_.lastTimestamp = uint256(FluidSmartLending(smartLending_).lastTimestamp());
        data_.feeOrReward = int256(FluidSmartLending(smartLending_).feeOrReward());
        (data_.exchangePrice, ) = FluidSmartLending(smartLending_).getUpdateExchangePrice();
        // exchangePrice is in 1e18, shares are in 1e18, SmartLending is in 1e18
        data_.assetsPerShare = (1e18 * 1e18) / data_.exchangePrice;
        data_.sharesPerAsset = data_.exchangePrice; // just providing same value for extra clarity. would be 1e18 * exchangePrice / 1e18

        data_.totalUnderlyingShares = (data_.totalSupply * data_.exchangePrice) / 1e18;

        data_.rebalancer = FluidSmartLending(smartLending_).rebalancer();
        data_.rebalanceDiff = uint256(FluidSmartLending(smartLending_).rebalanceDiff());

        data_.dexUserSupplyData = DEX_RESOLVER.getUserSupplyData(data_.dex, smartLending_);
    }

    /// @notice Get the entire view data for multiple SmartLendings, EXCEPT underlying DexEntireData. use write method for that.
    /// @param smartLendings_ An array of SmartLending addresses
    /// @return datas_ An array of structs containing all the data for each SmartLending
    function getSmartLendingEntireViewDatas(
        address[] memory smartLendings_
    ) public view returns (SmartLendingEntireData[] memory datas_) {
        uint256 length_ = smartLendings_.length;
        datas_ = new SmartLendingEntireData[](length_);

        for (uint256 i; i < length_; i++) {
            datas_[i] = getSmartLendingEntireViewData(payable(smartLendings_[i]));
        }
    }

    /// @notice Get the entire data for all SmartLendings, EXCEPT underlying DexEntireData. use write method for that.
    /// @return datas_ An array of structs containing all the data for all SmartLendings
    function getAllSmartLendingEntireViewDatas() public view returns (SmartLendingEntireData[] memory datas_) {
        return getSmartLendingEntireViewDatas(getAllSmartLendingAddresses());
    }

    /// @notice Get the entire data for a SmartLending, incl. underlying DexEntireData and totalUnderlyingAssets for each token
    /// @param smartLending_ The address of the SmartLending
    /// @return data_ A struct containing all the data for the SmartLending
    /// @dev expected to be called via callStatic
    function getSmartLendingEntireData(
        address payable smartLending_
    ) public returns (SmartLendingEntireData memory data_) {
        data_ = getSmartLendingEntireViewData(smartLending_);

        data_.dexEntireData = DEX_RESOLVER.getDexEntireData(data_.dex);

        data_.totalUnderlyingAssetsToken0 =
            (data_.totalUnderlyingShares * data_.dexEntireData.dexState.token0PerSupplyShare) /
            1e18;
        data_.totalUnderlyingAssetsToken1 =
            (data_.totalUnderlyingShares * data_.dexEntireData.dexState.token1PerSupplyShare) /
            1e18;
    }

    /// @notice Get the entire data for multiple SmartLendings
    /// @param smartLendings_ An array of SmartLending addresses
    /// @return datas_ An array of structs containing all the data for each SmartLending
    /// @dev expected to be called via callStatic
    function getSmartLendingEntireDatas(
        address[] memory smartLendings_
    ) public returns (SmartLendingEntireData[] memory datas_) {
        uint256 length_ = smartLendings_.length;
        datas_ = new SmartLendingEntireData[](length_);

        for (uint256 i; i < length_; i++) {
            datas_[i] = getSmartLendingEntireData(payable(smartLendings_[i]));
        }
    }

    /// @notice Get the entire data for all SmartLendings
    /// @return datas_ An array of structs containing all the data for all SmartLendings
    /// @dev expected to be called via callStatic
    function getAllSmartLendingEntireDatas() public returns (SmartLendingEntireData[] memory datas_) {
        return getSmartLendingEntireDatas(getAllSmartLendingAddresses());
    }

    /// @notice gets a user position at a certain SmartLending. EXCLUDING underlyingAssetsToken0 and underlyingAssetsToken1.
    ///          use write method for that.
    function getUserPositionView(
        address payable smartLending_,
        address user_
    ) public view returns (UserPosition memory userPosition_) {
        userPosition_.user = user_;
        userPosition_.smartLendingAssets = FluidSmartLending(payable(smartLending_)).balanceOf(user_);

        {
            (uint256 exchangePrice_, ) = FluidSmartLending(smartLending_).getUpdateExchangePrice();
            userPosition_.underlyingShares = (userPosition_.smartLendingAssets * exchangePrice_) / 1e18;
        }

        {
            IERC20 token0_ = IERC20(FluidSmartLending(smartLending_).TOKEN0());
            IERC20 token1_ = IERC20(FluidSmartLending(smartLending_).TOKEN1());

            userPosition_.underlyingBalanceToken0 = token0_.balanceOf(user_);
            userPosition_.underlyingBalanceToken1 = token1_.balanceOf(user_);

            userPosition_.allowanceToken0 = token0_.allowance(user_, address(smartLending_));
            userPosition_.allowanceToken1 = token1_.allowance(user_, address(smartLending_));
        }
    }

    /// @notice gets a user position at a certain SmartLending incl. underlyingAssetsToken0 and underlyingAssetsToken1
    /// @dev expected to be called via callStatic
    function getUserPosition(
        address payable smartLending_,
        address user_
    ) public returns (UserPosition memory userPosition_) {
        userPosition_ = getUserPositionView(smartLending_, user_);

        {
            DexResolverStructs.DexState memory dexState_ = DEX_RESOLVER.getDexState(
                address(FluidSmartLending(smartLending_).DEX())
            );

            userPosition_.underlyingAssetsToken0 =
                (userPosition_.underlyingShares * dexState_.token0PerSupplyShare) /
                1e18;
            userPosition_.underlyingAssetsToken1 =
                (userPosition_.underlyingShares * dexState_.token1PerSupplyShare) /
                1e18;
        }
    }

    /// @notice gets all Smart lendings entire data and all user positions for each.
    ///         Excluding underlying DexEntireData and underlyingAssetsToken0 and underlyingAssetsToken1. use write method for that.
    function getUserPositionsView(address user_) external view returns (SmartLendingEntireDataUserPosition[] memory) {
        SmartLendingEntireData[] memory smartLendingsEntireData_ = getAllSmartLendingEntireViewDatas();
        SmartLendingEntireDataUserPosition[] memory userPositionArr_ = new SmartLendingEntireDataUserPosition[](
            smartLendingsEntireData_.length
        );
        for (uint256 i = 0; i < smartLendingsEntireData_.length; ) {
            userPositionArr_[i].smartLendingEntireData = smartLendingsEntireData_[i];
            userPositionArr_[i].userPosition = getUserPositionView(
                payable(smartLendingsEntireData_[i].smartLending),
                user_
            );
            unchecked {
                i++;
            }
        }
        return userPositionArr_;
    }

    /// @notice gets all Smart lendings entire data and all user positions for each.
    ///         incl. underlying Dex (=`getSmartLendingEntireViewData()` + DexEntireData) and underlyingAssetsToken0 and underlyingAssetsToken1.
    /// @dev expected to be called via callStatic
    function getUserPositions(address user_) external returns (SmartLendingEntireDataUserPosition[] memory) {
        SmartLendingEntireData[] memory smartLendingsEntireData_ = getAllSmartLendingEntireDatas();
        SmartLendingEntireDataUserPosition[] memory userPositionArr_ = new SmartLendingEntireDataUserPosition[](
            smartLendingsEntireData_.length
        );
        for (uint256 i = 0; i < smartLendingsEntireData_.length; ) {
            userPositionArr_[i].smartLendingEntireData = smartLendingsEntireData_[i];
            userPositionArr_[i].userPosition = getUserPosition(
                payable(smartLendingsEntireData_[i].smartLending),
                user_
            );
            unchecked {
                i++;
            }
        }
        return userPositionArr_;
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.8.21;

import { Structs as DexResolverStructs } from "../dex/structs.sol";

abstract contract Structs {
    struct SmartLendingEntireData {
        // address of the smart lending
        address smartLending;
        // The name of the SmartLending token.
        string name;
        // The symbol of the SmartLending token.
        string symbol;
        // The number of decimal places for the SmartLending token.
        uint8 decimals;
        // The total supply of the SmartLending token.
        uint256 totalSupply;
        // total dex shares according to smart lending exchange rate
        uint256 totalUnderlyingShares;
        // total token0 amount built with dex token0PerSupplyShare. (!!) this is not set when calling the view method
        uint256 totalUnderlyingAssetsToken0;
        // total token1 amount built with dex token1PerSupplyShare. (!!) this is not set when calling the view method
        uint256 totalUnderlyingAssetsToken1;
        // The address of the first token in the underlying dex pool.
        address token0;
        // The address of the second token in the underlying dex pool.
        address token1;
        // The address of the underlying dex pool.
        address dex;
        // The last timestamp when the exchange price was updated in storage.
        uint256 lastTimestamp;
        // The fee or reward rate for the SmartLending. If positive then rewards, if negative then fee. 1e6 = 100%, 1e4 = 1%, minimum 0.0001% fee or reward.
        int256 feeOrReward;
        // The current exchange price of the SmartLending updated to block.timestamp.
        uint256 exchangePrice;
        // The address of the rebalancer.
        address rebalancer;
        // exchange rate for x assets per 1 underlying pool share
        uint256 assetsPerShare;
        // exchange rate for x underlying pool shares per 1 SmartLending asset (=exchangePrice)
        uint256 sharesPerAsset;
        // The difference in balance for rebalancing. difference between the total smart lending shares on the DEX and the total smart lending shares calculated.
        // A positive value indicates fees to collect, while a negative value indicates rewards to be rebalanced.
        uint256 rebalanceDiff;
        // structs fetched directly from DexResolver:
        DexResolverStructs.DexEntireData dexEntireData; // (!!) this is not set when calling the view method
        DexResolverStructs.UserSupplyData dexUserSupplyData; // supply data of the SmartLending at the dex
    }

    struct UserPosition {
        address user;
        uint256 smartLendingAssets; // ERC20 smart lending assets that the user owns
        uint256 underlyingShares; // dex shares according to smart lending exchange rate
        uint256 underlyingAssetsToken0; // position token0 amount built with dex token0PerSupplyShare. (!!) this is not set when calling the view method
        uint256 underlyingAssetsToken1; // position token1 amount built with dex token1PerSupplyShare. (!!) this is not set when calling the view method
        uint256 underlyingBalanceToken0; // token0 user balance
        uint256 underlyingBalanceToken1; // token1 user balance
        uint256 allowanceToken0; // allowance of token0 for user to the smartLending
        uint256 allowanceToken1; // allowance of token1 for user to the smartLending
    }

    struct SmartLendingEntireDataUserPosition {
        SmartLendingEntireData smartLendingEntireData;
        UserPosition userPosition;
    }
}

