        LiquidateMemoryVars memory memoryVars_;

        memoryVars_.vaultVariables2 = vaultVariables2;

        if (((vaultVariables_ >> 2) & X20) == 0) {
            revert FluidVaultError(ErrorTypes.Vault__TopTickDoesNotExist);
        }

